UPDATE locales_npc_text SET entry = 7249, Text0_0_loc2 = 'Je viens juste de terminer un nouvel imploseur dimensionnel qui me rendra plus riche que dans mes rêves de gobelin les plus fous ! Tout ce dont j\'ai besoin maintenant, c\'est qu\'un Ingénieur gobelin habile construise un déchiqueteur dimensionnel qui s\'y connecte et l\'essaie !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7249;
UPDATE locales_npc_text SET entry = 7251, Text0_0_loc2 = 'Grâce à la technologie récupérée par de courageux gnomes qui se sont aventurés dans Gnomeregan, j\'ai réussi à construire ma plus grande invention : le Transporteur de matière ! Tout ce dont j\'ai besoin maintenant, c\'est qu\'un Ingénieur gnome habile construise une balise puissante accordée au transporteur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7251;
