UPDATE locales_npc_text SET entry = 7257, Text0_0_loc2 = 'Prenez la clé et ouvrez ces chaînes ! Hitah\'ya la Gardienne la détient… au fond de la caverne…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7257;
UPDATE locales_npc_text SET entry = 7274, Text0_0_loc2 = 'Emplois/Objets trouvés/On recherche :', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7274;
UPDATE locales_npc_text SET entry = 7294, Text0_0_loc2 = 'Zog zog, mon $g ami:amie; ! Je suis Kelm Hargunth, le principal fournisseur des voltigeurs Warsong. Lorsque vous améliorerez votre statut auprès des voltigeurs, je vous donnerai l\'accès à un superbe choix de matériel que vous pourrez utiliser dans le goulet des Warsong. Si vous avez besoin de réparations, je peux aussi fournir ce type de service.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7294;
UPDATE locales_npc_text SET entry = 7295, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7295;
UPDATE locales_npc_text SET entry = 7314, Text0_0_loc2 = 'Bienvenue ! Bienvenue à l\'Étonnante Ménagerie Sombrelunienne de Yebb Becquabrach, où vous verrez une Myriade de Légendes Vivantes et d\'Étrangetés Mythiques ! Savourez votre visite, les amis !$B$BMais attention à ce que vous touchez ! Nos sujets, tant animés qu\'inanimés, sont redoutables ! Ils ont tôt fait de happer ou de brûler les mains égarées…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7314;
UPDATE locales_npc_text SET entry = 7315, Text0_0_loc2 = 'Où puis-je vous envoyer aujourd’hui, $c ?', Text0_1_loc2 = null, Text1_0_loc2 = 'Bien sûr, le centre nerveux des Tarides reste sans doute la Croisée, mais je préfère la quiétude de Camp Taurajo. Où dois-je vous envoyer ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Les voies de la Terre-mère sont innombrables. Puisse-t-elle veiller sur vous, où que vous alliez.', Text2_1_loc2 = null, Text3_0_loc2 = 'Traitez le coursier du vent correctement, et il vous conduira à votre destination. Puissent vos ancêtres veiller sur vous, $N.', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7315;
UPDATE locales_npc_text SET entry = 7334, Text0_0_loc2 = 'Bien le bonjour, et bienvenue au plus grand spectacle d\'Azeroth !$B$BEntrez, entrez et découvrez nos attractions ! Les merveilles découvertes par la foire de Sombrelune dans ce monde vaste et mystérieux ne manqueront pas de vous stupéfier ! Nous n\'avons reculé devant rien pour émerveiller les enfants de tous âges !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7334;
UPDATE locales_npc_text SET entry = 7336, Text0_0_loc2 = 'L\'argent ne fait pas tout, vous savez ? Il nous arrive de recevoir des dons, y compris de très généreux, mais la véritable motivation créative de notre troupe de baladins est d\'apporter un peu de bonheur dans ce monde ravagé par la guerre. Ne vous préoccupez pas de nous, et laissez-nous nous préoccuper de la meilleure manière de vous distraire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7336;
UPDATE locales_npc_text SET entry = 7337, Text0_0_loc2 = 'L’échange des bons se fait ici ! Apportez vos bons, nous en faisons des cadeaux ! Venez donc !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7337;
UPDATE locales_npc_text SET entry = 7338, Text0_0_loc2 = 'Bougez de là, $gmon gars:mam\'zelle;.', Text0_1_loc2 = null, Text1_0_loc2 = 'Allez, on dégage, c\'est pas une attraction, ici.', Text1_1_loc2 = null, Text2_0_loc2 = 'Si vous essayez de vous montrer sympa, économisez votre souffle. Ou mieux, arrêtez complètement de respirer.', Text2_1_loc2 = null, Text3_0_loc2 = 'Au cas où vous n\'auriez pas remarqué, je ne suis pas là pour assurer les relations avec les clients.', Text3_1_loc2 = null, Text4_0_loc2 = 'Si vous faites des problèmes à Silas, je deviens votre problème. Bonne journée.', Text4_1_loc2 = null, Text5_0_loc2 = 'J\'ai besoin d\'une deuxième tête parce que j\'ai besoin de perdre la première.', Text5_1_loc2 = null, Text6_0_loc2 = 'J’ai un peu vécu à Hache-tripes. C’était drôle. Vraiment.', Text6_1_loc2 = null, Text7_0_loc2 = 'Contrairement à la plupart des ogres, je sais à quoi servent les toilettes. C\'est là qu\'on trouve du papier. Maintenant, dégage.', Text7_1_loc2 = null WHERE entry = 7338;
UPDATE locales_npc_text SET entry = 7339, Text0_0_loc2 = 'Je vous attendais depuis longtemps, $N.$B$BTous les êtres intelligents de ce monde sont conduits par leurs choix. Les choix engendrent d’autres choix, et à leur tour, ces choix façonnent votre être. Votre avenir découle de tout cela – de votre identité et de vos choix.$B$BÊtes-vous de taille à découvrir votre avenir ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7339;
UPDATE locales_npc_text SET entry = 7340, Text0_0_loc2 = 'Très bien…$B$BVotre seigneur vous a chargé de garder un champ de maïs qui est régulièrement visité par des voleurs. Une nuit, pendant votre patrouille, vous tombez sur un homme hagard, en haillons, qui est en train de chiper du maïs. Lorsque vous approchez, il se met à genoux et commence à vous supplier. Il explique qu’il vole le maïs pour nourrir sa famille parce que son seigneur – qui est aussi le vôtre – le ruine à force d’impôts. Et il est vrai que votre seigneur est réputé pour avoir la main lourde dans ce domaine.$B$BFaites votre choix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7340;
UPDATE locales_npc_text SET entry = 7341, Text0_0_loc2 = 'Le choix suivant vous attend.$B$BVous avez juré à votre seigneur de remplir l’office de bourreau. Un homme vous est envoyé, un traître à votre seigneur et à votre peuple. Mais vous le connaissez. C’est un ami proche, un homme vertueux qui avait mystérieusement disparu. Le crime pour lequel il a été condamné a entraîné la mort de nombreux civils innocents. Lorsqu’il vous voit, il vous supplie de l’épargner et affirme qu’il est victime d’une machination.$B$BFaites votre choix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7341;
UPDATE locales_npc_text SET entry = 7354, Text0_0_loc2 = 'Vous ne voyez pas que je travaille ?', Text0_1_loc2 = 'Vous ne voyez pas que je travaille ?', Text1_0_loc2 = 'La foire arrive !', Text1_1_loc2 = 'La foire arrive !', Text2_0_loc2 = 'Hé oui, l\'ami ! La foire de Sombrelune va s\'installer ici même, dès que j\'aurai déchargé les chariots, monté les tentes, construit la ménagerie et servi les verres. Revenez nous voir !', Text2_1_loc2 = 'Hé oui, l\'ami ! La foire de Sombrelune va s\'installer ici même, dès que j\'aurai déchargé les chariots, monté les tentes, construit la ménagerie et servi les verres. Revenez nous voir !', Text3_0_loc2 = 'La foire de Sombrelune ne va pas tarder à ouvrir. Revenez dans un moment pour voir si nous avons fini.', Text3_1_loc2 = 'La foire de Sombrelune ne va pas tarder à ouvrir. Revenez dans un moment pour voir si nous avons fini.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7354;
UPDATE locales_npc_text SET entry = 7355, Text0_0_loc2 = 'Bienvenue à la foire, $gpetit:petite; ! Nous avons collecté des merveilles aux quatre coins du monde pour votre édification. Oh, et si vous parvenez à rassembler une série de cartes mineures de Sombrelune, dites-le-moi, car le maître… enfin, je veux dire que cela m’intéresserait beaucoup.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7355;
UPDATE locales_npc_text SET entry = 7356, Text0_0_loc2 = 'Nous avons disséminé des cartes de Sombrelune de par le monde afin de voir si des individus qui en seraient dignes pouvaient reconstituer l\'une des huit suites. Si vous parvenez à rassembler les huit cartes de l\'une des suites, nous vous récompenserons avec l\'une des cartes majeures de Sombrelune, un objet puissant qui constituera également un lien entre la foire de Sombrelune et vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7356;
UPDATE locales_npc_text SET entry = 7357, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite de Fauves, vous recevrez en récompense une carte majeure de Sombrelune du Dragon bleu. Elle est très utile pour rétablir les énergies magiques.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7357;
UPDATE locales_npc_text SET entry = 7358, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite de Portails, vous recevrez en récompense une carte majeure de Sombrelune du Néant distordu. Cette carte ramènera parfois votre esprit dans votre corps si vous mourez et vous rétablira.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7358;
UPDATE locales_npc_text SET entry = 7359, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite d\'Élémentaires, vous recevrez en récompense une carte majeure de Sombrelune du Maelström. Cette carte infligera parfois des dégâts importants à l\'ennemi que vous combattez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7359;
UPDATE locales_npc_text SET entry = 7360, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite de Seigneurs de guerre, vous recevrez en récompense une carte majeure de Sombrelune d\'Héroïsme. Cette carte vous soignera parfois lors des combats en mêlée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7360;
UPDATE locales_npc_text SET entry = 7361, Text0_0_loc2 = 'Le choix suivant vous attend.$B$BVous êtes $genvoyé:envoyée; en mission diplomatique par votre seigneur dans un pays aux moeurs cruelles actuellement en guerre contre le vôtre. La guerre a fait de nombreuses victimes des deux côtés. Votre mission est d\'établir un traité de paix honorable pour les deux peuples.$B$BLors d\'un dîner diplomatique, vous entendez le dirigeant du pays adverse violemment insulter votre magnanime seigneur. Vous êtes pratiquement $gcertain:certaine; que le dirigeant ne souhaitait pas que vous l\'entendiez, mais il a tout de même souillé l\'honneur de votre seigneur.$B$BFaites votre choix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7361;
UPDATE locales_npc_text SET entry = 7362, Text0_0_loc2 = 'Le choix suivant vous attend.$B$BVotre frère souhaite rejoindre les rangs d\'un prestigieux ordre militaire dont vous faites déjà partie. Vous êtes $gcertain:certaine; de son dévouement et de sa loyauté, mais vous doutez de son talent et de sa capacité à accomplir les difficiles tâches qui lui seraient confiées. Alors que vous n\'êtes pas $gsûr:sûre; qu\'il se montrerait à la hauteur, mettant ainsi sa propre vie et celle des autres en danger, vous savez que votre parole suffirait à le faire accepter au sein de l\'ordre ou à lui en barrer l\'accès.$B$BFaites votre choix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7362;
UPDATE locales_npc_text SET entry = 7363, Text0_0_loc2 = 'Le choix suivant vous attend.$B$BVous avez vaincu $gseul:seule; une bête terrible qui causait des ravages dans les campagnes et pris son oreille en guise de trophée. Vous apprenez plus tard que votre seigneur avait offert une récompense pour la mort de la bête, et qu\'un chevalier bon mais pauvre, dont vous savez qu\'il cherche à nourrir une famille avec des revenus très modestes, affirme avoir lui-même tué la bête. Vous n\'avez pas vraiment besoin d\'argent, mais vous savez que le chevalier pauvre ment pour son profit personnel.$B$BFaites votre choix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7363;
UPDATE locales_npc_text SET entry = 7364, Text0_0_loc2 = 'Votre avenir est décidé, $c. Pendant les prochaines heures, votre esprit sera en harmonie avec votre nature véritable. Soyez $gprudent:prudente;, car votre avenir, et votre destin, ne sont jamais aussi clairs que vous ne le pensez.$B$BSi vous vouliez entrevoir un petit aperçu de votre avenir sur lequel méditer, surtout faites-le-moi savoir. Je serais heureux de vous fournir une prédiction écrite, si vous avez de la place dans votre inventaire. Elle est tirée des révélations que vous m\'avez faites en répondant à mes questions.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7364;
UPDATE locales_npc_text SET entry = 7365, Text0_0_loc2 = 'Voici votre prédiction, $N. Faites-en bon usage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7365;
UPDATE locales_npc_text SET entry = 7366, Text0_0_loc2 = 'La foire de Sombrelune arrive en ville ! Ne la manquez pas !', Text0_1_loc2 = null, Text1_0_loc2 = 'Ne manquez surtout pas toutes les merveilles et curiosités de la foire de Sombrelune quand elle arrivera en ville !', Text1_1_loc2 = null, Text2_0_loc2 = 'La foire de Sombrelune arrive ! On s\'y amuse à TOUS les âges ! Venez nombreux !', Text2_1_loc2 = null, Text3_0_loc2 = 'Approchez-vous et laissez-moi vous parler du spectacle le plus fabuleux de tout Azeroth : la foire de Sombrelune ! Huzzah !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7366;
UPDATE locales_npc_text SET entry = 7374, Text0_0_loc2 = 'La voilà ! La foire de Sombrelune est désormais ouverte ! Ne manquez pas de voir le spectacle le plus passionnant de tout Azeroth !', Text0_1_loc2 = null, Text1_0_loc2 = 'La foire de Sombrelune est arrivée en ville ! Venez faire l\'expérience du spectacle le plus palpitant que vous verrez jamais !', Text1_1_loc2 = null, Text2_0_loc2 = 'La foire de Sombrelune n\'offrira son spectacle que pendant un court moment, avant que le rideau ne ne se referme. N\'oubliez pas de la visiter aujourd\'hui !', Text2_1_loc2 = null, Text3_0_loc2 = 'Mesdames, messieurs, et enfants de tous âges ! La foire de Sombrelune est arrivée ! Hourra !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7374;
UPDATE locales_npc_text SET entry = 7375, Text0_0_loc2 = 'La voilà ! La foire de Sombrelune est désormais ouverte !  Ne manquez pas de voir le spectacle le plus passionnant de tout Azeroth !', Text0_1_loc2 = null, Text1_0_loc2 = 'La foire de Sombrelune est arrivée en ville ! Venez faire l\'expérience du spectacle le plus palpitant que vous verrez jamais !', Text1_1_loc2 = null, Text2_0_loc2 = 'La foire de Sombrelune n\'offrira son spectacle que pendant un court moment, avant que le rideau ne ne se referme. N\'oubliez pas de la visiter aujourd\'hui !', Text2_1_loc2 = null, Text3_0_loc2 = 'Mesdames, messieurs et enfants de tous âges !  La foire de Sombrelune est arrivée !  Hourra !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7375;
UPDATE locales_npc_text SET entry = 7376, Text0_0_loc2 = 'Quand la foire de Sombrelune ouvrira, elle sera installée à Mulgore. Nous planterons nos tentes au sud-ouest des Pitons du Tonnerre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7376;
UPDATE locales_npc_text SET entry = 7377, Text0_0_loc2 = 'La foire de Sombrelune est actuellement installée dans les plaines dorées de Mulgore, au sud-ouest des Pitons du Tonnerre. Ne la manquez pas !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7377;
UPDATE locales_npc_text SET entry = 7378, Text0_0_loc2 = 'Quand la foire de Sombrelune ouvrira, elle sera installée dans la forêt d\'Elwynn. Nous planterons nos tentes juste au sud de Comté-de-l\'or !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7378;
UPDATE locales_npc_text SET entry = 7379, Text0_0_loc2 = 'La foire de Sombrelune est actuellement installée dans la forêt d\'Elwynn, au sud de Comté-de-l\'or. Ne la manquez pas !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7379;
UPDATE locales_npc_text SET entry = 7382, Text0_0_loc2 = 'Pour sûr, y\'a beaucoup de monde qui jette ses détritus par terre, par ici…', Text0_1_loc2 = 'Pour sûr, y\'a beaucoup de monde qui jette ses détritus par terre, par ici…', Text1_0_loc2 = 'Vous vous amusez bien ?', Text1_1_loc2 = 'Vous vous amusez bien ?', Text2_0_loc2 = 'N\'oubliez pas d\'acheter des rafraîchissements et des souvenirs !', Text2_1_loc2 = 'N\'oubliez pas d\'acheter des rafraîchissements et des souvenirs !', Text3_0_loc2 = 'La foire de Sombrelune est le plus grand événement sur tout Azeroth !', Text3_1_loc2 = 'La foire de Sombrelune est le plus grand événement sur tout Azeroth !', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7382;
UPDATE locales_npc_text SET entry = 7383, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à la foire de Sombrelune, $c !$B$BJe m\'appelle Kerri Hicks ! Je vous serrerais volontiers la main, mais j\'ai peur de la casser, car je suis la femme la plus forte du monde !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7383;
UPDATE locales_npc_text SET entry = 7387, Text0_0_loc2 = 'Venez ici ! Venez déguster nos extraordinaires ailes de poulet pimentées !', Text0_1_loc2 = null, Text1_0_loc2 = 'Les casse-croûtes de Sombrelune, tout juste sortis du grill ! Venez les savourer tant qu\'ils sont chauds !', Text1_1_loc2 = null, Text2_0_loc2 = 'Vous avez de l\'argent et nous avons de la nourriture. Qu\'est-ce que vous attendez ?!', Text2_1_loc2 = null, Text3_0_loc2 = 'Les grenouilles croustillantes sont en vente exclusivement dans notre foire ! Pour un temps limité seulement !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7387;
UPDATE locales_npc_text SET entry = 7389, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai acheté moi-même tous les objets qui sont en vente ici. Vous avez ma garantie personnelle d\'authenticité.', Text1_0_loc2 = null, Text1_1_loc2 = 'Les cartes de la foire de Sombrelune ? Allez voir le professeur, il saura vous en parler. Mais venez me voir si vous vendez des antiquités et des artefacts difficiles à trouver.', Text2_0_loc2 = null, Text2_1_loc2 = 'Venez voir mes marchandises tant que vous en avez l\'occasion. Nous sommes là pour la semaine, puis nous partirons pour un pays lointain.', Text3_0_loc2 = null, Text3_1_loc2 = 'Avez-vous la moindre idée de ce que j\'ai dû endurer pour pouvoir vous proposer ces objets inestimables ? Allons, allons, venez les voir de vos yeux !', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7389;
UPDATE locales_npc_text SET entry = 7390, Text0_0_loc2 = 'Hé, que quelqu\'un m\'aide à rattraper cette chose !', Text0_1_loc2 = null, Text1_0_loc2 = 'Il s\'enfuit !', Text1_1_loc2 = null, Text2_0_loc2 = 'Reviens ici !', Text2_1_loc2 = null, Text3_0_loc2 = 'Grenouilles et balles de cuir à vendre !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7390;
UPDATE locales_npc_text SET entry = 7393, Text0_0_loc2 = 'Hélas… Je suis désolé, mais je ne peux rien vous prédire. Ma dernière prédiction à votre sujet est trop récente.$B$BIl faut laisser passer plusieurs heures entre chaque prédiction, afin de laisser aux forces de la Destinée le temps de se réaligner. Alors et alors seulement, je pourrai voir votre avenir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7393;
UPDATE locales_npc_text SET entry = 7394, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7394;
UPDATE locales_npc_text SET entry = 7395, Text0_0_loc2 = 'Bonjour. Je suis Chronos, Celui qui n\'oublie jamais. Ma mémoire est parfaite jusqu\'au moindre détail.$B$BFaites-moi confiance.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7395;
UPDATE locales_npc_text SET entry = 7397, Text0_0_loc2 = 'Hello et bienvenue ! Je suis désolé, ma baraque est fermée… beaucoup de réparations à faire…$B$BMais j’ai juré sur mon propre sang de vous faire sourire. Souriez, s’il vous plaît, ou je devrai me couper la main et ne plus manger de chair vivante avant qu’elle n’ait repoussé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7397;
UPDATE locales_npc_text SET entry = 7400, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne retrouve plus Jubjub, ma grenouille apprivoisée. Elle adore la bière sombrefer, elle ne boit que ça, mais c\'est si dur d\'en trouver ! Je suis tombée à court, et Jubjub a dû s\'enfuir pour en trouver !$B$BJ\'aimerais bien qu\'elle revienne…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7400;
UPDATE locales_npc_text SET entry = 7401, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7401;
UPDATE locales_npc_text SET entry = 7402, Text0_0_loc2 = 'Nous devons assurer la survie de ce camp à tout prix, $c ! Nous ne devons pas faire quartier à nos ennemis, car ils ne nous témoigneront de leur part aucune pitié.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7402;
UPDATE locales_npc_text SET entry = 7403, Text0_0_loc2 = 'Les temps sont dangereux, $r. Un mal ancien se lève, et il se moque que vous apparteniez à la Horde ou à l\'Alliance.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7403;
UPDATE locales_npc_text SET entry = 7404, Text0_0_loc2 = null, Text0_1_loc2 = 'Boissons à vendre !  Venez étancher votre soif et goûter les meilleures bières de la foire de Sombrelune !', Text1_0_loc2 = null, Text1_1_loc2 = 'Ne faites pas votre timide, offrez donc une tournée générale !', Text2_0_loc2 = null, Text2_1_loc2 = 'Les pourboires sont toujours les bienvenus, mon $gami:amie;.', Text3_0_loc2 = null, Text3_1_loc2 = 'Elle vient tout juste d\'arriver... Une nouvelle cuvée de la réserve spéciale de Sombrelune.  Venez la goûter avant qu\'elle ne disparaisse !', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7404;
