UPDATE locales_npc_text SET entry = 7415, Text0_0_loc2 = 'Salutation jeune $C. C\'est ici que votre chef de guerre a défié Orgrim Marteau-du-destin ! Et même si cela s\'est déroulé il y a bien des années, cette image est encore fraîche dans ma mémoire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7415;
