UPDATE locales_npc_text SET entry = 7454, Text0_0_loc2 = 'Si vous êtes $gvenu:venue; pour sauver le monde, alors vous êtes au bon endroit. Celui qui n\'a pas de visage, le dieu sanguinaire, Hakkar - est revenu dans notre monde !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7454;
