UPDATE locales_npc_text SET entry = 7528, Text0_0_loc2 = 'Le goulet des Warsong ? Eh bien, il faudra que vous parliez à la belle Lylandris, dans le Hall des armes.', Text0_1_loc2 = 'Le goulet des Warsong ? Eh bien, il faudra que vous parliez à la belle Lylandris, dans le Hall des armes.', Text1_0_loc2 = 'Il ne faut pas manquer la moindre occasion de combattre la Horde, et tout particulièrement les orcs, si vous voulez mon avis. Vous pourrez vous battre tout votre saoul dans le goulet des Warsong, ça c\'est sûr. Allez donc dans le Hall des armes pour parler à Lylandris. Elle vous indiquera la route à suivre.', Text1_1_loc2 = 'Il ne faut pas manquer la moindre occasion de combattre la Horde, et tout particulièrement les orcs, si vous voulez mon avis. Vous pourrez vous battre tout votre saoul dans le goulet des Warsong, ça c\'est sûr. Allez donc dans le Hall des armes pour parler à Lylandris. Elle vous indiquera la route à suivre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7528;
