UPDATE locales_npc_text SET entry = 7534, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous vous attendiez à quoi, au juste ?$B$BMaywiki ne traite qu’avec les druides et les chamans.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7534;
UPDATE locales_npc_text SET entry = 7535, Text0_0_loc2 = 'Bonjour, $C. C\'est la Fête des moissons, le temps des récoltes et des souvenirs. Nous festoyons pour célébrer toutes les bonnes choses de la vie, sans omettre de nous souvenir de nos ancêtres et des héros du passé et de leur rendre grâce pour tout ce qu\'ils nous ont légué.$B$BVenez profiter du festin que nous vous offrons ! Par ailleurs, j\'offre des feux d\'artifice pour vous aider à célébrer cette époque heureuse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7535;
