UPDATE locales_npc_text SET entry = 7554, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous ne devons pas laisser l\'Alliance s\'étendre en Arathi ! Même leur minuscule refuge de l\'Ornière pourrait devenir menaçant, s\'il grossissait.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7554;
UPDATE locales_npc_text SET entry = 7555, Text0_0_loc2 = 'La bataille pour le bassin d\'Arathi est une bataille pour les ressources. Du bois, du minerai, de la nourriture… Tout cela est indispensable pour faire la guerre.$B$BEn priver nos ennemis est aussi bon que de les massacrer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7555;
UPDATE locales_npc_text SET entry = 7556, Text0_0_loc2 = 'Tout aveugle que je sois, j\'ai conservé la vision.$B$BJe m\'adresse surtout aux chasseurs et aux voleurs, $r.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7556;
UPDATE locales_npc_text SET entry = 7574, Text0_0_loc2 = 'Salut, $Gaventurier:aventurière;. Je suis Rin’wosho, marchand de Zuldazar. Je viens des mers du Sud, chargé de marchandises exotiques de mon lointain pays.$B$BSi vous prouvez votre valeur à ma tribu, nous ferons du troc. Si vous portez une Marque d\'honneur de ma tribu, alors, nous troquerons davantage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7574;
UPDATE locales_npc_text SET entry = 7594, Text0_0_loc2 = 'Je suis Zanza, un loa de Zandalar. Je suis venu de Zandalar, revêtu de cette forme éthérée, pour aider mon peuple. Hakkar ne contrôle pas mon âme.$B$BJ\'aide ceux qui ont aidé le peuple zandalarien.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7594;
UPDATE locales_npc_text SET entry = 7595, Text0_0_loc2 = '$gUn ami:Une amie; des zandalariens ! Vous vous êtes $genfoncé:enfoncée; loin dans les profondeurs du repaire du Dieu sanglant, $C. Écoutez attentivement ce que j\'ai à vous dire.$B$BIl y a des nombreux millénaires, lorsque les trolls de l\'empire de Zul\'Aman combattaient les envahisseurs hauts-elfes païens, une grande magie fut dérobée à Zanza. Elle fut utilisée par les hauts-elfes pour enchanter leurs armes et leurs armures, afin de mieux asservir les trolls de la région.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7595;
UPDATE locales_npc_text SET entry = 7596, Text0_0_loc2 = 'Vraiment ? Vous savez à quel point la version des hauts-elfes de la magie troll était pathétique ?$B$BLes hauts-elfes n\'ont jamais réussi à comprendre pourquoi ils étaient incapables de rivaliser avec le pouvoir du vaudou troll, en dépit de leur affinité pour la magie. Cette seule idée paraissait inconcevable aux peaux-pâles. À force, ils se sont résignés, et ont utilisé des versions abâtardies des enchantements trolls. Je crois qu\'ils appelaient ça des "arcanum".', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7596;
UPDATE locales_npc_text SET entry = 7597, Text0_0_loc2 = 'Avec les bons composants, Zanza peut créer les anciens enchantements trolls. Cette magie vous aidera à vaincre Hakkar et ses séides.$B$BJe ne vous demande que deux objets.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7597;
UPDATE locales_npc_text SET entry = 7598, Text0_0_loc2 = 'Le premier est un bijou primordial qui doit être pris sur les puissants lieutenants hakkari : Jin\'do et le seigneur sanglant.$B$BLe second est une poupée vaudou percée. On peut en trouver dans les piles d\'ordures qui jonchent cet endroit ignoble. Cherchez dans les piles de déchets.$B$BRamenez-les moi tous les deux, et je vous façonnerai un enchantement puissant !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7598;
UPDATE locales_npc_text SET entry = 7599, Text0_0_loc2 = 'Je regrette, $c, mais vous n\'êtes pas encore assez $gaguerri:aguerrie; pour entrer dans le goulet des Chanteguerres.', Text0_1_loc2 = 'Je regrette, $c, mais vous n\'êtes pas encore assez $gaguerri:aguerrie; pour entrer dans le goulet des Chanteguerres.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7599;
UPDATE locales_npc_text SET entry = 7614, Text0_0_loc2 = 'Bienvenue au concours de pêche de la Baie-du-Butin, qui nous permet de désigner toutes les semaines notre grand Maître pêcheur !
Entre 14 h et 16 h, des bancs de courbines longent les côtes de Strangleronce. Si votre flotteur tombe dans l’un de ces bancs lorsque vous pêchez, vous prendrez l’un des poissons du banc plutôt que les prises normales de Strangleronce.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7614;
UPDATE locales_npc_text SET entry = 7615, Text0_0_loc2 = 'Salutations, $c. Je suis Exzhal, Serviteur de Rastakhan.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7615;
UPDATE locales_npc_text SET entry = 7616, Text0_0_loc2 = 'Je suis désolé, $c, mais il vous faut davantage d\'expérience pour survivre dans la vallée d\'Alterac.', Text0_1_loc2 = 'Je suis désolée, $c, mais il vous faut davantage d\'expérience pour survivre dans la vallée d\'Alterac.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7616;
UPDATE locales_npc_text SET entry = 7634, Text0_0_loc2 = 'Les Parangons de puissance sont des vestiges de l’ancien empire gurubashi. Nous, les Zandalar, les recherchons non seulement pour leur incroyable valeur en tant que pièces de collection, mais aussi parce que nous pensons qu’ils contiennent la signature latente de la magie utilisée par le vieil empire.$B$BVous trouverez probablement trois sortes de Parangons à Zul’Gurub : des pièces, des bijoux et des objets primordiaux. Rapportez-en en quantité suffisante, et nous vous récompenserons en vous offrant certains des meilleurs objets jamais fabriqués par nos artisans.', Text0_1_loc2 = 'Les Parangons de puissance sont des vestiges de l’ancien empire gurubashi. Nous, les Zandalar, les recherchons non seulement pour leur incroyable valeur en tant que pièces de collection, mais aussi parce que nous pensons qu’ils contiennent la signature latente de la magie utilisée par le vieil empire.$B$BVous trouverez probablement trois sortes de Parangons à Zul’Gurub : des pièces, des bijoux et des objets primordiaux. Rapportez-en en quantité suffisante, et nous vous récompenserons en vous offrant certains des meilleurs objets jamais fabriqués par nos artisans.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7634;
