UPDATE locales_npc_text SET entry = 7636, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7636;
UPDATE locales_npc_text SET entry = 7638, Text0_0_loc2 = 'Si vous voulez mon avis, ce qu\'il y a de mieux dans la pêche, c\'est la lenteur. Ça laisse le temps de réfléchir, de penser aux événements passés et de préparer l\'avenir.$B$BOh, et c\'est aussi une très bonne excuse pour boire. Beaucoup.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7638;
UPDATE locales_npc_text SET entry = 7639, Text0_0_loc2 = 'Merci encore de m\'avoir rapporté mon mètre ruban porte-bonheur, $N. Je vous assure qu\'il m\'aide à attraper de plus gros poissons !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7639;
UPDATE locales_npc_text SET entry = 7640, Text0_0_loc2 = 'Pour attraper Gahz\'ranka, il vous faudra un appât spécial au puant de vase. Je les fais moi-même !$B$BMais l\'appât ne suffit pas, vous aurez aussi besoin de puants de vase… Vous devez les placer dans l\'appât de manière à ce qu\'il pue horriblement ! Lorsque vous aurez les puants de vase, jetez l\'appât au large de la pointe de Pagle à Zul\'Gurub… Vous n\'avez plus qu\'à vous servir à boire et à attendre… mais pas longtemps, parce que Gahz\'ranka ne tardera pas, c\'est sûr !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7640;
