UPDATE locales_npc_text SET entry = 7645, Text0_0_loc2 = 'Les puants de vase sont une variété de poisson particulièrement odorante qui apprécie des eaux tropicales. Les puants de vase zuliens sont les plus puants de tous, et on ne les trouve que dans les eaux fumantes de Zul\'Gurub.$B$BJe soupçonne Gahz\'ranka de n\'être là que pour eux, même si les trolls vous diront sans doute que c\'est à cause d\'un de leurs dieux…$B$BÀ vous de voir quelle version vous préférez, mais croyez-moi, si vous garnissez l\'un de mes appâts avec des puants de vase zuliens, ce vieux monstre viendra à tous les coups !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7645;
