UPDATE locales_npc_text SET entry = 7650, Text0_0_loc2 = 'Vous trouverez dame Hoteshem dans la salle d\'audience du roi. Elle vous aidera à vous rendre au bassin d\'Arathi, où la Ligue d\'Arathor a besoin d\'aide contre les Profanateurs.', Text0_1_loc2 = 'Vous trouverez dame Hoteshem dans la salle d\'audience du roi. Elle vous aidera à vous rendre au bassin d\'Arathi, où la Ligue d\'Arathor a besoin d\'aide contre les Profanateurs.', Text1_0_loc2 = 'Ce que la ligue d\'Arathor tente de faire au bassin d\'Arathi est admirable. Quel dommage que ces horreurs de morts-vivants et leurs amis de la Horde se mettent en travers de leur route. Partez pour le donjon de Hurlevent. Dame Hoteshem vous attend dans la salle d\'audience du roi. Elle peut vous aider à vous rendre au bassin d\'Arathi.', Text1_1_loc2 = 'Ce que la ligue d\'Arathor tente de faire au bassin d\'Arathi est admirable. Quel dommage que ces horreurs de morts-vivants et leurs amis de la Horde se mettent en travers de leur route. Partez pour le donjon de Hurlevent. Dame Hoteshem vous attend dans la salle d\'audience du roi. Elle peut vous aider à vous rendre au bassin d\'Arathi.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7650;
