UPDATE locales_npc_text SET entry = 7652, Text0_0_loc2 = 'Throm\'ka $c ! Êtes-vous $gprêt:prête; à vous battre pour la Horde dans le goulet des Chanteguerres ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7652;
UPDATE locales_npc_text SET entry = 7653, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $N. Voulez-vous que je vous aide dans votre voyage vers le goulet des Chanteguerres ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7653;
UPDATE locales_npc_text SET entry = 7654, Text0_0_loc2 = '$N, il faut écraser les Sentinelles d\'Aile-argent ! Êtes-vous $gprêt:prête; pour le goulet des Chanteguerres ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7654;
UPDATE locales_npc_text SET entry = 7655, Text0_0_loc2 = 'Êtes-vous ici pour vous battre pour nous dans le goulet des Chanteguerres ? Lok\'tar Ogar $c !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7655;
UPDATE locales_npc_text SET entry = 7656, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons besoin de votre appui au goulet des Chanteguerres, $c. Nous aiderez-vous dans notre combat contre les voltigeurs Chanteguerre ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7656;
UPDATE locales_npc_text SET entry = 7657, Text0_0_loc2 = 'Salut $gmon gars:ma petite;. Vous êtes $gprêt :prête ; à faire un tour dans la vallée d’Alterac et à donner une bonne raclée à la Horde ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7657;
UPDATE locales_npc_text SET entry = 7658, Text0_0_loc2 = '$N, avez-vous la moindre idée de ce qui se passe dans la vallée d’Alterac ? Êtes-vous de taille à combattre le clan Loup-de-givre ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7658;
UPDATE locales_npc_text SET entry = 7659, Text0_0_loc2 = 'La garde Foudrepique doit être détruite ! Prêt pour la vallée d’Alterac, $c ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7659;
UPDATE locales_npc_text SET entry = 7660, Text0_0_loc2 = null, Text0_1_loc2 = 'Lok\'tar $c ! Nous avons besoin de vos services dans la vallée d\'Alterac. Dois-je vous ajouter à nos listes ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7660;
UPDATE locales_npc_text SET entry = 7661, Text0_0_loc2 = 'Nos forces dans la vallée d\'Alterac ont besoin de sagesse pour vaincre, $C. Vous semblez être la personne qu\'il faut pour les diriger.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7661;
UPDATE locales_npc_text SET entry = 7662, Text0_0_loc2 = 'J’espère que vous êtes là pour vous rendre dans la vallée d’Alterac, $N. Nous avons besoin de toute l’aide que nous pouvons recevoir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7662;
UPDATE locales_npc_text SET entry = 7663, Text0_0_loc2 = null, Text0_1_loc2 = 'Les humains et leur soi-disant Ligue d’Arathor essayent de nous chasser du bassin d’Arathi. Il n’en est pas question, $c ! Êtes-vous volontaire pour vous y rendre et leur montrer que ce sera bientôt à eux, et non aux Profanateurs, de faire leurs bagages ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7663;
UPDATE locales_npc_text SET entry = 7664, Text0_0_loc2 = 'Le bassin d’Arathi était notre foyer, et avec votre aide, il le redeviendra ! Nous aiderez-vous, $c ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7664;
UPDATE locales_npc_text SET entry = 7665, Text0_0_loc2 = 'J’espère que vous êtes là pour nous aider à arracher le bassin d’Arathi aux Profanateurs. N’est-ce pas, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7665;
UPDATE locales_npc_text SET entry = 7666, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous attendais, $N. Êtes-vous $gprêt:prête; à nous aider dans le bassin d\'Arathi ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7666;
UPDATE locales_npc_text SET entry = 7667, Text0_0_loc2 = 'Vous croyez-vous de taille à détruire la Ligue d’Arathor, $c ? Je l’espère, en tout cas. Que voulez-vous ? Êtes-vous là pour me faire perdre mon temps ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7667;
UPDATE locales_npc_text SET entry = 7668, Text0_0_loc2 = 'Regardez autour de vous, $c. Vous vous trouvez au sein d’une compagnie choisie, c’est certain, mais je pense que vous avez encore besoin de faire vos preuves. Hum, je devrais peut-être vous donner l’occasion de nous aider dans le bassin d’Arathi. Est-ce que cela vous plairait ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7668;
UPDATE locales_npc_text SET entry = 7669, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7669;
UPDATE locales_npc_text SET entry = 7670, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7670;
UPDATE locales_npc_text SET entry = 7673, Text0_0_loc2 = 'Renataki des Mille lames.$B$BLa douleur est son sang. La peur est son alliée. Puisse-t-il revenir un jour, et avec lui, la joie du carnage !$B$BRenataki réside à présent près de la frontière de la folie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7673;
UPDATE locales_npc_text SET entry = 7674, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7674;
UPDATE locales_npc_text SET entry = 7676, Text0_0_loc2 = 'Holà holà holà ! Je suis là pour annoncer le fabuleux concours de pêche de Strangleronce, mais pour participer, vous devez être capable de survivre là-bas et être assez bon pêcheur pour pêcher dans ces eaux.$B$BRevenez me voir lorsque vous serez plus $gqualifié:qualifiée;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7676;
UPDATE locales_npc_text SET entry = 7677, Text0_0_loc2 = 'Hé hé hé ! Si vous voulez tester vos dons de pêcheur, j’ai l’endroit qu’il vous faut ! Ce dimanche, nous avons un concours de pêche à Strangleronce !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7677;
