UPDATE locales_npc_text SET entry = 7679, Text0_0_loc2 = 'Nous avons beaucoup plus besoin d’aide dans le bassin d’Arathi que la plupart des gens ne l’imaginent, $N. Mais heureusement, les temps difficiles sont aussi ceux où l’honneur et la réputation se gagnent le plus facilement. Je vous supplie d’aller voir le maître de guerre du bassin d’Arathi qui habite cette ville.', Text0_1_loc2 = 'Nous avons beaucoup plus besoin d’aide dans le bassin d’Arathi que la plupart des gens ne l’imaginent, $N. Mais heureusement, les temps difficiles sont aussi ceux où l’honneur et la réputation se gagnent le plus facilement. Je vous supplie d’aller voir le maître de guerre du bassin d’Arathi qui habite cette ville.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7679;
