UPDATE locales_npc_text SET entry = 7694, Text0_0_loc2 = '$N, la garde Foudrepique de la vallée d’Alterac a plus que jamais besoin de votre aide dans son combat contre le clan Loup-de-givre. Les occasions de gagner de l’honneur et de la réputation y sont plus nombreuses que jamais ! Voulez-vous rejoindre la bataille ?', Text0_1_loc2 = '$N, la garde Foudrepique de la vallée d’Alterac a plus que jamais besoin de votre aide dans son combat contre le clan Loup-de-givre. Les occasions de gagner de l’honneur et de la réputation y sont plus nombreuses que jamais ! Voulez-vous rejoindre la bataille ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7694;
