UPDATE locales_npc_text SET entry = 7704, Text0_0_loc2 = 'Le moment venu, les saisons passent pour nous tous, $C. Vous n\'êtes encore qu\'au printemps de votre vie. Revenez à nous lorsque vous serez plus $gexpérimenté:expérimentée;.', Text0_1_loc2 = 'Le moment venu, les saisons passent pour nous tous, $C. Vous n\'êtes encore qu\'au printemps de votre vie. Revenez à nous lorsque vous serez plus $gexpérimenté:expérimentée;.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7704;
UPDATE locales_npc_text SET entry = 7705, Text0_0_loc2 = '$C, je n’ai pas honte de dire que nous avons besoin de toute l’aide que nous pourrons recevoir au goulet des Chanteguerres. Si vous choisissez de nous prêter main-forte, sachez qu’il y a plus d’honneur que jamais à y gagner. De plus, les Sentinelles d\'Aile-argent regardent d\'un œil plus favorable ceux qui se battent à leurs côtés. Voulez-vous rejoindre la bataille ?', Text0_1_loc2 = '$C, je n’ai pas honte de dire que nous avons besoin de toute l’aide que nous pourrons recevoir au goulet des Chanteguerres. Si vous choisissez de nous prêter main-forte, sachez qu’il y a plus d’honneur que jamais à y gagner. De plus, les Sentinelles d\'Aile-argent regardent d\'un œil plus favorable ceux qui se battent à leurs côtés. Voulez-vous rejoindre la bataille ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7705;
