UPDATE locales_npc_text SET entry = 7712, Text0_0_loc2 = 'Salutations, ô $gmaître:maîtresse; de la Canne et du Moulinet ! Si vous souhaitez vous mesurer aux meilleurs pêcheurs du monde, écoutez-moi bien ! Ce dimanche, Strangleronce verra un concours qui désignera le maître pêcheur !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7712;
UPDATE locales_npc_text SET entry = 7713, Text0_0_loc2 = 'Les Parangons de puissance sont de très grands mojos. Mojos de l’empire Gurubashi. Nous, Zandalar, devons récupérer ce mojo. La tribu le prendra pour elle. Le Dieu sanglant ne nous détruira pas.$B$BTrois sortes de Parangons attendent à Zul’Gurub. Cherchez les pièces. Cherchez les bijoux. Cherchez les objets primordiaux. Tels sont les Parangons.$B$BAllez à Zul’Gurub maintenant. Tuez. Prenez les Parangons de puissance. Ramenez-les à la tribu. Gagnez notre respect. Gagnez nos armes. Gagnez nos armures. Gagnez un vrai pouvoir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7713;
UPDATE locales_npc_text SET entry = 7714, Text0_0_loc2 = 'Nous avons déjà un vainqueur pour le concours, mais Jang est prête à acheter toutes les courbines que vous avez pêchées !
Vous aurez toujours dimanche prochain pour montrer vos talents de pêcheur !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7714;
UPDATE locales_npc_text SET entry = 7717, Text0_0_loc2 = 'L’autel de Zanza, derrière moi, est un lieu sacré pour nous autres Zandalar. Nous y prions pour renforcer notre résolution en préparation de la guerre contre le Dieu sanglant Hakkar.$B$BProuvez votre valeur aux yeux de la tribu en l’utilisant pour détruire les Bijoux hakkari que vous trouverez dans Zul’Gurub. Par ailleurs, je vous donnerai les Marques d’honneur qui servent de monnaie à notre tribu en échange des séries de pièces que vous trouverez à Zul’Gurub. Ramenez-moi les pièces que je recherche, et vous serez $grécompensé:récompensée; !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7717;
UPDATE locales_npc_text SET entry = 7718, Text0_0_loc2 = 'Les trolls zandalariens ont énormément voyagé et beaucoup souffert pour arriver ici. Ne rien faire serait nous condamner à mort…', Text0_1_loc2 = 'Les trolls zandalariens ont énormément voyagé et beaucoup souffert pour arriver ici. Ne rien faire serait nous condamner à mort…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7718;
UPDATE locales_npc_text SET entry = 7720, Text0_0_loc2 = 'Nous, trolls zandalariens, avons voyagé jusqu\'ici depuis des îles lointaines, car nous ne pouvons ignorer la menace qu\'affronte à présent notre monde.$B$BLe chef de notre expédition, Molthor, nous a conduit sur l\'île de Yojamba, le long des côtes nord de Strangleronce, et recrute maintenant des héros courageux pour s\'aventurer dans l\'ancienne cité trolle... Zul\'Gurub.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7720;
UPDATE locales_npc_text SET entry = 7721, Text0_0_loc2 = 'Nous, trolls zandalariens, avons voyagé jusqu\'ici depuis des îles lointaines, car nous ne pouvons ignorer la menace qu\'affronte à présent notre monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7721;
UPDATE locales_npc_text SET entry = 7722, Text0_0_loc2 = 'Quelqu’un m’a dit un jour que ce n\'est pas à un vieux singe que l\'on va apprendre de nouvelles grimaces. Heureusement pour vous, j’ai découvert que c’était faux.$b$bBon. Voulez-vous que votre familier oublie ce qu’il sait ?', Text0_1_loc2 = 'Quelqu’un m’a dit un jour que ce n\'est pas à un vieux singe que l\'on va apprendre de nouvelles grimaces. Heureusement pour vous, j’ai découvert que c’était faux.$b$bBon. Voulez-vous que votre familier oublie ce qu’il sait ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7722;
UPDATE locales_npc_text SET entry = 7723, Text0_0_loc2 = 'Je suis Rutherford Twing, le principal fournisseur des Profanateurs ici au bassin d\'Arathi. Lorsque vous améliorerez votre statut auprès de nous, je vous donnerai l\'accès à un superbe choix de matériel que vous pourriez trouver utile. Si vous avez besoin de réparations, je peux aussi fournir ce type de service.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7723;
UPDATE locales_npc_text SET entry = 7724, Text0_0_loc2 = 'Je suis Samuel Hawke, le principal fournisseur de la Ligue d\'Arathor ici au bassin d\'Arathi. Lorsque vous améliorerez votre statut auprès de nous, je vous donnerai l\'accès à un superbe choix de matériel que vous pourriez trouver utile. Si vous avez besoin de réparations, je peux aussi fournir ce type de service.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7724;
UPDATE locales_npc_text SET entry = 7725, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous ne pouvons pas perdre la bataille pour le goulet des Chanteguerres. Nous nous battons pour notre honneur et pour nos terres. Les orcs et leurs alliés égarés ne nous les prendront pas !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7725;
UPDATE locales_npc_text SET entry = 7726, Text0_0_loc2 = 'Protéger les coupes que la Horde opère dans la forêt d\'Orneval n\'est pas facile, mais nous n\'avons jamais reculé devant le combat ! Les elfes de la nuit et leurs alliés tomberont !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7726;
UPDATE locales_npc_text SET entry = 7727, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous souhaite la bienvenue, $gvoyageur:voyageuse;. Tant que vous resterez à l’intérieur du Fort cénarien, vous serez à l’abri du danger.$B$BMais si vous quittez le fort, prenez garde, les silithides grouillent à l’extérieur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7727;
UPDATE locales_npc_text SET entry = 7728, Text0_0_loc2 = 'Je fournis au Fort diverses reliques nécessaires au lancement des sorts, ainsi que des composants alchimiques.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7728;
UPDATE locales_npc_text SET entry = 7729, Text0_0_loc2 = 'Un mal terrible s’agite, $r. Le sentez-vous ?$B$BBah ! Bien sûr que non. Donnez-moi vos armes et votre armure et je les remettrai d’aplomb.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7729;
UPDATE locales_npc_text SET entry = 7730, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh Ciel, mes réserves seront bientôt épuisées ! Qu’est-ce qui retient ce fichu gobelin ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7730;
UPDATE locales_npc_text SET entry = 7731, Text0_0_loc2 = 'Regardez derrière vous, $r. La ruche frémit, et tout autour de nous, le sol s’agite et tremble.$B$BL’heure est venue pour moi d’accomplir un serment que j’ai fait voici mille ans. Les sables se déplacent à nouveau. Les Qiraji se préparent à la guerre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7731;
UPDATE locales_npc_text SET entry = 7732, Text0_0_loc2 = 'Pourquoi maintenant, $r ? Je ne comprends pas. Il y a tant à faire…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7732;
UPDATE locales_npc_text SET entry = 7733, Text0_0_loc2 = 'Le commandant n’est plus lui-même. Sa maîtresse a disparu sans laisser de traces au cours de fouilles archéologiques dans le désert du sud. Oui, parfaitement, sans laisser de traces ! Très étrange, en vérité.$B$BDites, vous cherchez du travail, non ? Est-ce que j’ai besoin d’être encore plus clair ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7733;
UPDATE locales_npc_text SET entry = 7734, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous entendez ? Le bourdonnement devient plus fort de jour en jour. Ce doit être ces cristaux qui rendent les insectes dingues.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7734;
UPDATE locales_npc_text SET entry = 7735, Text0_0_loc2 = 'Regardez les cieux, $c. Les Vols draconiques n’ont pas été vus dans cette région depuis mille ans ! Ces Qiraji préparent quelque chose, je le sens !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7735;
UPDATE locales_npc_text SET entry = 7736, Text0_0_loc2 = 'Barbe-de-bronze a encore disparu ! Tout le bon sens de la famille a été attribué à ses frères.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7736;
UPDATE locales_npc_text SET entry = 7737, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7737;
UPDATE locales_npc_text SET entry = 7738, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7738;
UPDATE locales_npc_text SET entry = 7739, Text0_0_loc2 = 'Bonne Sanssaint, $C !', Text0_1_loc2 = null, Text1_0_loc2 = 'Vous aimez les bonbons ? J\'aime les bonbons.', Text1_1_loc2 = null, Text2_0_loc2 = 'Allez les Réprouvés ! Vive nous !', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7739;
UPDATE locales_npc_text SET entry = 7740, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à la célébration de l’homme d’osier, $c !$B$BEn l’honneur de la Sanssaint, la plus révérée de nos fêtes, les Réprouvés brûlent un homme d’osier. C’est ce jour-là que la reine banshee nous a délivrés des griffes du roi-liche et du Fléau. Depuis, nous sommes libres. Ceux qui voulaient notre perte en paient le prix… la vengeance !$B$BLa crémation de l’homme d’osier commencera à huit heures du soir !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7740;
UPDATE locales_npc_text SET entry = 7741, Text0_0_loc2 = 'Vous, là-bas ! Vous paraissez en bonne santé... Nous avons besoin d\'un coup de main pour défendre Southshore !$B$BCes maudits Réprouvés sont sur les nerfs, à cause de la Sanssaint. Leur "grande fête" les conduira sans nul doute sur le pas de notre porte. Si vous avez le temps, votre aide nous serait vraiment précieuse !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7741;
UPDATE locales_npc_text SET entry = 7742, Text0_0_loc2 = 'Nous devons frapper les premiers. Nous ne pouvons nous permettre de répéter les erreurs du passé…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7742;
UPDATE locales_npc_text SET entry = 7744, Text0_0_loc2 = 'Allez-vous-en, infidèle ! Que le pouvoir des Abysses vous détruise !', Text0_1_loc2 = 'Allez-vous-en, infidèle ! Que le pouvoir des Abysses vous détruise !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7744;
UPDATE locales_npc_text SET entry = 7746, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $c. Bienvenue au Fort cénarien. D’ici, nous allons frapper les silithides là où ils sont les plus puissants. J’espère que vous venez nous prêter main-forte.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7746;
UPDATE locales_npc_text SET entry = 7747, Text0_0_loc2 = null, Text0_1_loc2 = 'C’est un plaisir de vous revoir, $N. Grâce à votre aide, la sécurité de nos lignes de ravitaillement a été considérablement renforcée. J’espère que vous comprenez à quel point la logistique est importante dans de grandes batailles comme celle que nous livrons.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7747;
UPDATE locales_npc_text SET entry = 7749, Text0_0_loc2 = 'Comment osez-vous communier avec un duc du Conseil, ver !', Text0_1_loc2 = 'Comment osez-vous communier avec un duc du Conseil, ver !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7749;
UPDATE locales_npc_text SET entry = 7751, Text0_0_loc2 = 'Vous chassez Poigne-de-mort, $N ? Je suis très impressionné. Elle m\'a fait perdre deux hommes, rien que cette semaine. Espérons que vous aurez plus de chance qu\'eux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7751;
UPDATE locales_npc_text SET entry = 7752, Text0_0_loc2 = 'Hé ! Merci d’avoir sauvé ma peau ! Je pense que je vais faire profil bas pendant un moment, le temps de reprendre des forces.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7752;
UPDATE locales_npc_text SET entry = 7753, Text0_0_loc2 = 'Qui êtes-vous ? Où suis-je ? Je me sens… si faible…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7753;
UPDATE locales_npc_text SET entry = 7754, Text0_0_loc2 = 'Quel moucheron se présente devant les seigneurs du Conseil ? Un moucheron, sans nul doute, qui veut se faire écraser !', Text0_1_loc2 = 'Quel moucheron se présente devant les seigneurs du Conseil ? Un moucheron, sans nul doute, qui veut se faire écraser !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7754;
UPDATE locales_npc_text SET entry = 7755, Text0_0_loc2 = 'Vous feriez mieux de vous asseoir, parce que va être long.$B$BLorsque nous sommes arrivés en Silithus, dame Mar\'alith a été la première elfe de la nuit du Fort cénarien à se joindre à notre expédition. Notre routine n\'avait rien d\'inhabituel : former un groupe pour s\'aventurer dans les ruches à la recherche d\'artefacts perdus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7755;
UPDATE locales_npc_text SET entry = 7756, Text0_0_loc2 = 'Bien sûr ! Ces ruches grouillent de silithides qui ne sont pas précisément ravis de nous voir.$B$BTout se passait bien jusqu\'à ce que nous tombions sur un cristal orné de glyphes dans la Ruche\'Regal. Il l\'attirait comme une flamme attire un papillon ! Elle a posé sa jolie joue contre le cristal et aussitôt, elle est entrée en transe ! Bien sûr, le reste de l\'équipe s\'est inquiété. Nous l\'avons ramassée et sortie de là fissa !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7756;
UPDATE locales_npc_text SET entry = 7757, Text0_0_loc2 = 'Nous l\'avons ramenée au commandant, dans l\'espoir que les druides du Cercle pourraient la remettre sur pied. Oh, pour sûr, ils sont arrivés à la réveiller. Nous nous sommes dit que l\'affaire était réglée et que nous pouvions nous remettre au travail.$B$BPendant quelques semaines, tout s\'est passé normalement. À ce moment-là, Brann était encore des nôtres, alors notre organisation était un peu mieux ordonnée. Et puis un jour, elle est arrivée au camp, sortant de nulle part.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7757;
UPDATE locales_npc_text SET entry = 7758, Text0_0_loc2 = 'Z\'êtes $gbouché :bouchée; ? De qui je parle, à votre avis ? Oui, la fille – dame Mar\'alith. Enfin bon, elle était de retour, et pas de bonne humeur. C\'est à ce moment-là qu\'elle a commencé à nous demander des trucs. Des tas de trucs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7758;
UPDATE locales_npc_text SET entry = 7759, Text0_0_loc2 = 'Déjà, elle a demandé que nous la ramenions immédiatement à Ruche\'Regal ! Brann n\'a pas voulu en entendre parler. Il lui a dit qu\'il n\'était pas question de remettre les pieds dans une de ces ruches sans une armée pour nous tenir compagnie. C\'était devenu beaucoup trop dangereux. Ces saletés d\'insectes sont trois fois plus nombreux que quand nous avons commencé.$B$BC\'est à ce moment-là que Natalia a craqué.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7759;
UPDATE locales_npc_text SET entry = 7760, Text0_0_loc2 = 'Comment ça, comment ça ? Elle a perdu la boule ! Elle est devenue dingue ! Bon, d\'habitude, je ne cogne pas les dames, mais là, quand j\'ai repris connaissance, j\'étais en colère. Brann, Rutgar, le singe et moi, nous nous sommes comportés comme de vrais orcs, sur ce coup ! Et même comme ça, nous avons eu du mal à la tenir à distance. Elle donnait l\'impression d\'être possédée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7760;
UPDATE locales_npc_text SET entry = 7761, Text0_0_loc2 = 'Qui sait ? Bref, c\'est la dernière fois que je l\'ai vue. Frankal pourra peut-être vous en dire plus.$B$BOh, sans vouloir changer de sujet, je suis sûr que votre boulot pour le commandant est important, mais nous sommes devant quelque chose d\'énorme, ici. ÉNORME, parfaitement ! Nous avons demandé à Forgefer de nous envoyer l\'armée, mais ils ont fait les sourds. Enfin, on a eu une réponse idiote à propos de troupes trop dispersées pour assurer la protection du royaume et blablabla. Une fois que vous aurez remis votre rapport au commandant, revenez nous voir avec des amis. Nous avons besoin d\'aide !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7761;
UPDATE locales_npc_text SET entry = 7762, Text0_0_loc2 = 'Oh la barbe, ça recommence… Bon, asseyez-vous $gmon gars:ma petite dame;.$B$BJe suis sûr que Rutgar vous en a déjà dit pas mal, alors que vais vous épargner les détails oiseux et en venir directement au fait.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7762;
UPDATE locales_npc_text SET entry = 7763, Text0_0_loc2 = 'C\'est parti ! Donc, dame Natalia était revenue pour un deuxième tour aux ruches, mais Brann a dit “pas question”. Avant que la bagarre ne commence, elle a parlé à quelqu\'un ou quelque chose. Et le plus dingue, c\'est que c\'était dans une langue qu\'aucun de nous n\'a comprise.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7763;
UPDATE locales_npc_text SET entry = 7764, Text0_0_loc2 = 'Un peu que c\'est bizarre ! Surtout qu\'entre Brann, Rutgar, le singe et moi, nous comprenons trente-huit langues, y compris un brin de titan – vanir ET aesir.$B$BQuoi qu\'elle ait bourdonné, ce n\'était pas destiné aux personnes *présentes*. Ensuite, elle a hoché la tête, et elle nous a foncé dessus. Elle nous a attaqués !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7764;
UPDATE locales_npc_text SET entry = 7765, Text0_0_loc2 = 'Écoutez, $gl\'ami:ma petite;, je vais vous dire, ce qui nous a attaqué n\'était pas une prêtresse ! Ses yeux brillaient d\'une lueur maléfique, et elle écumait. Vraiment. Elle s\'est approché, a poussé une espèce de cri délirant qui nous a tous fait fuir, et ensuite, elle a commencé à éplucher nos esprits un par un. J\'entendais des espèces de mots d\'ombre dans ma tête, et les autres tournaient de l\'œil ici et là. Dingue ! Sans ce fichu singe, on y serait tous passés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7765;
UPDATE locales_npc_text SET entry = 7766, Text0_0_loc2 = 'Pour dire quoi ? C\'est juste un singe, espèce de banane ! Quoi qu\'il en soit, le singe s\'est mis en colère quand il a vu le vieux Brann piquer du nez dans le sable. Glibb a cassé proprement la figure à l\'elfe, et elle n\'a rien pu faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7766;
UPDATE locales_npc_text SET entry = 7767, Text0_0_loc2 = 'Et alors ? Alors, elle a foncé droit vers la ruche. Et vous ne savez pas le plus dingue ? Il ne s\'est pas trouvé un seul insecte pour essayer de l\'arrêter, ou même pour se mettre sur son chemin. Elle est passée au milieu d\'eux comme s\'ils étaient potes depuis des siècles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7767;
UPDATE locales_npc_text SET entry = 7768, Text0_0_loc2 = 'Ouaip.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7768;
UPDATE locales_npc_text SET entry = 7770, Text0_0_loc2 = 'Le cristal est couvert de glyphes et de runes compliqués. Vous n\'y comprenez rien.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7770;
UPDATE locales_npc_text SET entry = 7771, Text0_0_loc2 = 'Un faible murmure sort de la pierre…$B$BSalutations, petit être. Avez-vous des nouvelles pour le templier ?', Text0_1_loc2 = 'Un faible murmure sort de la pierre…$B$BSalutations, petit être. Avez-vous des nouvelles pour le templier ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7771;
UPDATE locales_npc_text SET entry = 7772, Text0_0_loc2 = 'Une voix claire sort de la pierre...$B$BOù est votre médaillon de statut, $gpetit:petite; ? Vous ne pouvez pas parler à un duc sans prouver votre titre...', Text0_1_loc2 = 'Où est votre médaillon de statut, $gpetit:petite; ? Vous ne pouvez pas parler à un duc sans prouver votre titre…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7772;
UPDATE locales_npc_text SET entry = 7773, Text0_0_loc2 = 'Une voix claire sort de la pierre...$B$BVous êtes $gvenu:venue; à nous en arborant votre titre, donc nous vous écouterons. Mais n\'abusez pas de notre patience, $C. Le conseil n\'a pas de temps à perdre à entendre les commérages d\'un être tel que vous.', Text0_1_loc2 = 'Vous êtes $gvenu:venue; à nous en arborant votre titre, donc nous vous écouterons. Mais n\'abusez pas de notre patience, $C. Le conseil n\'a pas de temps à perdre à entendre les commérages d\'un être tel que vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7773;
UPDATE locales_npc_text SET entry = 7776, Text0_0_loc2 = 'Une voix tonitruante surgit de la pierre...$B$BQu\'est-ce donc? Je perçois la présence d\'un petit sectateur qui ose s\'aventurer ici ! Déguerpis, vermine, si tu ne veux pas affronter le courroux des seigneurs du Conseil !', Text0_1_loc2 = 'Une voix tonitruante surgit de la pierre...$B$BQu\'est-ce donc? Je perçois la présence d\'un petit sectateur qui ose s\'aventurer ici ! Déguerpis, vermine, si tu ne veux pas affronter le courroux des seigneurs du Conseil !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7776;
UPDATE locales_npc_text SET entry = 7778, Text0_0_loc2 = 'Où voulez-vous aller ?', Text0_1_loc2 = 'Où voulez-vous aller ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7778;
UPDATE locales_npc_text SET entry = 7779, Text0_0_loc2 = 'Les esprits de Silithus nous appellent, $n. La terre pleure, car elle a une histoire bouleversante à raconter…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7779;
UPDATE locales_npc_text SET entry = 7780, Text0_0_loc2 = 'Salutations, $c. Ne vous ai-je pas $grencontré:rencontrée; récemment lors d\'une manifestation exclusive ? Votre visage m\'est familier, mais il est vrai qu\'il y a beaucoup de monde dans cette ville.$B$BSi vous étiez à cette convention, on vous a sûrement donné un code secret. Dites-moi votre code et je vous donnerai un cadeau spécial en échange.', Text0_1_loc2 = 'Salutations, $c. Ne vous ai-je pas $grencontré:rencontrée; récemment lors d\'une manifestation exclusive ? Votre visage m\'est familier, mais il est vrai qu\'il y a beaucoup de monde dans cette ville.$B$BSi vous étiez à cette convention, on vous a sûrement donné un code secret. Dites-moi votre code et je vous donnerai un cadeau spécial en échange.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7780;
UPDATE locales_npc_text SET entry = 7781, Text0_0_loc2 = null, Text0_1_loc2 = '$N ! J’espère que vous êtes $gprêt:prête; à vous mettre au travail, parce qu’il y a énormément à faire sur l’île de Haut-soleil.$B$BDepuis la destruction du puits de soleil par Arthas et le Fléau, notre peuple dérive sur un océan d’incertitude. Nous oscillons au bord de l’anéantissement. Mais cela changera, $c. Vous allez à la fois progresser et nous aider à guérir.$B$BL’Outreterre nous attend !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7781;
UPDATE locales_npc_text SET entry = 7782, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Cercle cénarien est sage et connaît nombre des secrets de la terre. Les artisans feraient bien de tenir compte des paroles de ces druides.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7782;
UPDATE locales_npc_text SET entry = 7783, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7783;
UPDATE locales_npc_text SET entry = 7784, Text0_0_loc2 = 'De la Flèche solaire, nous avions un point de vue splendide sur le puits de soleil… avant. Nos vies ont été bouleversées, $N, mais nous continuons. À notre manière, nous sommes des survivants.$B$BEt si vous voulez traverser cette époque troublée, continuer à votre manière, vous DEVEZ apprendre comment survivre. C’est un devoir pour tous les elfes de sang. Vous devez maîtriser votre insatiable faim de magie avant que ce ne soit elle qui vous maîtrise.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7784;
UPDATE locales_npc_text SET entry = 7785, Text0_0_loc2 = 'Vous devez continuer à servir la cause des elfes de sang. Notre survie en dépend ! Nous devons œuvrer tous ensemble à nous sortir du bourbier dans lequel nous sommes enfoncés. Si vous êtes $gprêt:prête; à travailler, commençons tout de suite.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7785;
UPDATE locales_npc_text SET entry = 7786, Text0_0_loc2 = 'La connaissance est le pouvoir. Le VRAI pouvoir, $gmon jeune ami:ma jeune amie; ! Accumulez-en autant que vous le pourrez, et traitez ceux qui en possèdent déjà comme ils le méritent.$B$BAvant la destruction du puits de soleil, nous vivions dans l’illusion d’avoir atteint l’apogée de notre civilisation. Le Fléau nous a mis à genoux… mais dans un sens, il nous a ramené à la réalité.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7786;
UPDATE locales_npc_text SET entry = 7787, Text0_0_loc2 = 'Je suis à un moment critique de mes recherches ! Vous êtes libre de vous joindre à moi pour un peu de contemplation silencieuse. Toutefois, si vous n’y voyez pas d’inconvénient, j’insiste sur « silencieuse ».', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7787;
UPDATE locales_npc_text SET entry = 7788, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous allez à la capitale, j\'ai une tâche importante pour vous !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7788;
UPDATE locales_npc_text SET entry = 7789, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue ! Avancez, avancez, ne soyez pas timide. Voici la prunelle de mes yeux : l\'ultra-canon Explonheimer 5000 ! N\'est-il pas magnifique ? Vous avez l\'air d\'avoir un peu de brioche, mais je pense que vous pourrez quand même y entrer. Voler est tellement amusant... Vous voulez apprendre ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7789;
UPDATE locales_npc_text SET entry = 7790, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh, c\'est tout simple ! Vous l\'utilisez comme toutes les choses que vous pouvez activer. Ne vous inquiétez pas, je m\'occuperai de la mèche. Quand vous serez en l\'air, vous aurez l\'impression de "flotter", mais cela ne durera pas éternellement. Vous devez viser la cible qui se trouve sur le lac, tout là-bas. Pour commencer la descente, il suffit de détacher vos "ailes". Pour atterrir dans l\'eau, il faut que vous agissiez au bon moment, sinon... euh... Au fait, je ne vends pas d\'assurance-vie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7790;
UPDATE locales_npc_text SET entry = 7792, Text0_0_loc2 = null, Text0_1_loc2 = 'Bien le bonjour, $c. Bienvenue à la foire de Sombrelune. Il y a tant de choses à faire à la foire que les personnes qui viennent nous voir pour la première fois sont souvent un peu perdues. Je suis là pour vous aider. Vous avez des questions ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7792;
UPDATE locales_npc_text SET entry = 7793, Text0_0_loc2 = null, Text0_1_loc2 = 'Une excellente question ! Nous avons de nombreuses choses à vendre sur la foire. Si vous êtes intéressés par la nourriture et les boissons, allez jusqu\'à la baraque de Stamp et Sylannia. Ma boisson favorite est la réserve spéciale de Sombrelune.
Si vous préférez acheter quelque chose de plus exotique, Lhara aura sûrement des articles à vous proposer. Elle se spécialise dans les raretés venant du monde entier.
Et il y a Flik, qui est toujours en train de courir quelque part. Si vous arrivez à l\'attraper, il vend des balles pour s\'amuser à les lancer, et aussi des grenouilles comme animal de compagnie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7793;
UPDATE locales_npc_text SET entry = 7794, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis très contente que vous m\'ayez posé la question. Les bons de Sombrelune vous permettent en quelque sorte d\'aider la foire, et vous pouvez les échanger contre des lots spéciaux !$B$BPour commencer, vous devriez parler à Chronos, Kerri Hicks, Ringling et Yebb Nebblegear, pour voir lequel vous pourrez le mieux aider. Ils ont toujours une liste de choses à faire pour améliorer nos attractions.$B$BQuand vous aurez récolté suffisamment de bons, allez voir Gelvas Crasseporte et il vous donnera la liste des lots parmi lesquels vous pourrez choisir votre récompense.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7794;
UPDATE locales_npc_text SET entry = 7795, Text0_0_loc2 = null, Text0_1_loc2 = 'Personne ne sait exactement qui a fabriqué les cartes de Sombrelune, ni quelle est leur véritable utilité. Je peux néanmoins vous dire que l’on en trouve dans tout Azeroth, généralement entre les mains d’un être vil qui tente d\'en percer le mystère.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7795;
UPDATE locales_npc_text SET entry = 7796, Text0_0_loc2 = null, Text0_1_loc2 = 'Plusieurs choses me viennent à l\'esprit. Je vous recommande d\'aller rendre visite à Sayge pour qu\'il vous tire la bonne aventure. Je sais… c\'est un gnoll. Mais c\'est un gnoll très spécial, qui possède le don de prédire l\'avenir !$B$BEt la foire s\'agrandit sans cesse, avec toutes sortes de nouvelles attractions !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7796;
UPDATE locales_npc_text SET entry = 7797, Text0_0_loc2 = 'Nous n\'avons pas qu\'un seul ennemi en Silithus, $N. Pendant que l\'attention générale se concentre sur les silithides, mon frère aîné et moi-même surveillons étroitement les fous furieux du Marteau du crépuscule.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7797;
