UPDATE locales_npc_text SET entry = 7812, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous devriez plutôt demander ce qu\'on ne peut PAS faire !$B$BTous les aubergistes du monde distribuent des friandises à ceux qui font la tournée des bonbons et de nombreuses villes sont décorées pour l\'occasion ! Il y a aussi la fête de l\'Homme d\'osier, juste à l\'extérieur de Fossoyeuse, en soirée.$B$BEncore une chose - j\'ai entendu qu\'une petite fille malade de l\'orphelinat, ici à Orgrimmar, cherche quelqu\'un pour l\'aider à collecter des bonbons... Peut-être que vous pourriez faire quelque chose ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7812;
UPDATE locales_npc_text SET entry = 7813, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous devriez plutôt demander ce qu\'on ne peut PAS faire !$B$BTous les aubergistes du monde distribuent des friandises à ceux qui font la tournée des bonbons et de nombreuses villes sont décorées pour l\'occasion ! Il y a aussi le sergent Hartman, à Southshore, qui a besoin d\'aide pour lutter contre les Réprouvés et leur fête de l\'Homme d\'osier.$B$BEncore une chose - j\'ai entendu qu\'un petit garçon malade de l\'orphelinat de Hurlevent cherche quelqu\'un pour l\'aider à collecter des bonbons.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7813;
