UPDATE locales_npc_text SET entry = 7900, Text0_0_loc2 = 'Il y a un millier d\'années, lorsque la Guerre des sables changeants s\'est achevée, trois des nôtres se sont sacrifiés pour piéger les Qiraji dans ces salles pendant qu\'Anachronos et Forteramure scellaient Ahn\'Qiraj.$B$BChacun de nous fit le vœu de rechercher Merithra, Arygos et Caelestrasz si le Mur du scarabée devait un jour s\'ouvrir.$B$BIl s\'est ouvert... et nous sommes venus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7900;
