UPDATE locales_npc_text SET entry = 7902, Text0_0_loc2 = '<Narain tient une enveloppe fermée contre son front.>$B$BQu\'est-ce que... Mmmm... Velours !$B$BEst-ce que j\'ai raison ?$B$BSi vous êtes là pour réapprendre une compétence d\'artisanat, voyez le livre qui est posé là-bas...$B$B<Narain indique « La Divination pour les nuls ».>', Text0_1_loc2 = null, Text1_0_loc2 = 'Ne me dites rien ! Chut ! Vous êtes ici au sujet de Porte-cendres ?!? Oui... Oui... Non ! Au temps pour moi.$B$BQue puis-je faire pour vous aider, $r ?$B$BSi vous êtes là pour réapprendre une compétence d\'artisanat, voyez le livre qui est posé là-bas...$B$B<Narain indique « La Divination pour les nuls ».>', Text1_1_loc2 = null, Text2_0_loc2 = 'Narain Divinambolesque, à votre service ! Ingénieur à la retraite et maître devin ! Je peux vous prédire votre avenir avant même qu\'il ne se produise !$B$BSi vous êtes là pour réapprendre une compétence d\'artisanat, voyez le livre qui est posé là-bas...$B$B<Narain indique « La Divination pour les nuls ».>', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7902;
