UPDATE locales_npc_text SET entry = 8059, Text0_0_loc2 = 'Nous sommes fiers de servir Thrall. Nous irons aussi loin que nécessaire pour vaincre l\'ennemi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8059;
UPDATE locales_npc_text SET entry = 8060, Text0_0_loc2 = 'Vous arrivez juste à temps, $C. Sentez-vous le sol trembler ?$B$BQuelque chose arrive de la ruche. Mettez-vous à côté des grunts, $N. Je compte sur vous !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8060;
