UPDATE locales_npc_text SET entry = 8064, Text0_0_loc2 = 'Ne vous méprenez pas, je préférerais être n\'importe où plutôt que dans un tunnel plein de silithides. D\'un autre côté, si en restant ici à les observer, j\'aide à gagner la guerre, je continuerai aussi longtemps que nécessaire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8064;
UPDATE locales_npc_text SET entry = 8065, Text0_0_loc2 = 'Bientôt, nous débarrasserons Azeroth de l\'abominable présence des silithides. Leur potentiel de destruction est bien supérieur à ce que vous avez pu voir, $N. Des puissances plus redoutables et plus sombres se cachent derrière leurs activités.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8065;
