UPDATE locales_npc_text SET entry = 8076, Text0_0_loc2 = 'Autour du cou du renne se trouve une étiquette portant le logo des Gourmandises Fumebois, ainsi qu\'un nom : "Metzen".$B$BMetzen vous regarde avec des yeux tristes et mélancoliques. C\'est comme si le renne avait été paralysé par une force invisible.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8076;
UPDATE locales_npc_text SET entry = 8077, Text0_0_loc2 = 'Les yeux de Metzen s\'illuminent de joie lorsque vous répandez la poudre sur lui. Il secoue ses bois vers vous et laisse échapper ce qui ressemble à un soupir de soulagement.$B$BAutour de vous, l\'énergie magique commence à se répandre. On dirait qu\'il va se passer quelque chose !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8077;
UPDATE locales_npc_text SET entry = 8078, Text0_0_loc2 = 'Même si vous parveniez à nous libérer sans vaincre C’Thun, nous emploierions tous nos pouvoirs à renvoyer la bête dans l’enfer d’où elle est venue jadis – QUEL QU’EN SOIT LE PRIX.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8078;
UPDATE locales_npc_text SET entry = 8079, Text0_0_loc2 = null, Text0_1_loc2 = 'Les héros de la progéniture ? C’Thun doit être banni. Nous pouvons vous aider, $gmortel:mortelle; !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8079;
