UPDATE locales_npc_text SET entry = 8202, Text0_0_loc2 = 'Honorer ses ancêtres, c\'est honorer son peuple et sa culture, $C. Les Pièces des ancêtres sont un moyen tangible de reconnaître ceux qui ont choisi de suivre l\'antique voie du respect.$B$BPendant cette fête lunaire, vous allez avoir l\'occasion de rencontrer de nombreux Anciens. Ce faisant, vous acquerrez des Pièces des ancêtres. Remettez-les-moi, et je récompenserai votre promptitude en vous laissant choisir parmi des objets uniques.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8202;
UPDATE locales_npc_text SET entry = 8205, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous salue, $N. Nous venons de Reflet-de-Lune pour répandre au loin la nouvelle de la Fête lunaire.$B$BVous joindrez-vous à nos célébrations ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8205;
