UPDATE locales_npc_text SET entry = 8212, Text0_0_loc2 = '« Vous avez fait un boulot splendide pendant la collecte des ressources », qu’ils ont dit. « Voilà une promotion », qu’ils ont dit. En réalité, ça voulait dire « Carnes, mon brave Carnes, vous allez être cantonné en Silithus, devant un nid à cafards, en compagnie d’une bande de nains moites. »$B$BMerci pour cette promotion, capitaine !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8212;
UPDATE locales_npc_text SET entry = 8213, Text0_0_loc2 = null, Text0_1_loc2 = 'Tout comme mon frère, j’honore ceux qui honorent leurs ancêtres. Je suis heureuse de partager mes secrets d’artisanat avec les disciples de la Fête lunaire !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8213;
UPDATE locales_npc_text SET entry = 8214, Text0_0_loc2 = 'Comme beaucoup d’autres, je suis venu aider mes confrères druides lorsque la guerre a commencé. Mais lorsque les portes d’Ahn’Qiraj se sont ouvertes, j’ai réalisé qu’il restait beaucoup à faire. J’ai décidé de rester en Silithus et de faire de mon mieux pour aider notre cause, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8214;
UPDATE locales_npc_text SET entry = 8215, Text0_0_loc2 = 'Les silithides peuvent sembler meurtriers, mais comparés à leurs maîtres qiraji, ils sont inoffensifs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8215;
UPDATE locales_npc_text SET entry = 8216, Text0_0_loc2 = 'Cette guerre n’est pas terminée. Elle commence à peine ! À toute heure du jour et de la nuit, des hommes et des femmes courageux combattent notre ennemi à l’intérieur d’Ahn’Qiraj, $N. La cité des Qiraji tombera avant longtemps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8216;
UPDATE locales_npc_text SET entry = 8217, Text0_0_loc2 = null, Text0_1_loc2 = 'Grâce aux nombreuses aides que nous avons reçu de tous les coins de Kalimdor, notre combat contre les Qiraji progresse lentement mais régulièrement.$B$BAvoir des soldats ne suffit pas pour faire la guerre, $N. Ce conflit compte de nombreux héros anonymes, négociants ou artisans qui veillent à ce que nos forces reçoivent tout ce dont elles ont besoin.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8217;
UPDATE locales_npc_text SET entry = 8218, Text0_0_loc2 = 'Les gens de Reflet-de-Lune savent faire la fête. Qui l’aurait cru ?', Text0_1_loc2 = 'Les gens de Reflet-de-Lune savent faire la fête. Qui l’aurait cru ?', Text1_0_loc2 = 'Essayez les lanceurs de feux d\'artifice ! C\'est super !', Text1_1_loc2 = 'Essayez les lanceurs de feux d\'artifice ! C\'est super !', Text2_0_loc2 = 'Cette lumière lunaire conduit à Hurlevent. Utilisez votre invitation lorsque vous le désirerez.', Text2_1_loc2 = 'Cette lumière lunaire conduit à Hurlevent. Utilisez votre invitation lorsque vous le désirerez.', Text3_0_loc2 = 'N’essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text3_1_loc2 = 'N’essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8218;
UPDATE locales_npc_text SET entry = 8219, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8219;
UPDATE locales_npc_text SET entry = 8220, Text0_0_loc2 = 'La célébration lunaire est magnifique, cette année !', Text0_1_loc2 = 'La célébration lunaire est magnifique, cette année !', Text1_0_loc2 = 'Ils disent que ces feux d\'artifice utilisent la puissance de la lune !', Text1_1_loc2 = 'Ils disent que ces feux d\'artifice utilisent la puissance de la lune !', Text2_0_loc2 = 'Cette lumière lunaire supérieure vous enverra à Darnassus.', Text2_1_loc2 = 'Cette lumière lunaire supérieure vous enverra à Darnassus.', Text3_0_loc2 = 'N’essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text3_1_loc2 = 'N’essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8220;
UPDATE locales_npc_text SET entry = 8221, Text0_0_loc2 = 'Je sais bien que ce n\'est pas le cas, mais je jurerai que cette fête a été organisée par des orcs !', Text0_1_loc2 = 'Je sais bien que ce n\'est pas le cas, mais je jurerai que cette fête a été organisée par des orcs !', Text1_0_loc2 = 'Vous avez vu ? La dernière fusée a failli mettre le feu à la chemise du nain ! Hah !', Text1_1_loc2 = 'Vous avez vu ? La dernière fusée a failli mettre le feu à la chemise du nain ! Hah !', Text2_0_loc2 = 'Ce rayon de lune vous enverra à Orgrimmar. Utilisez votre invitation lorsque vous voudrez partir.', Text2_1_loc2 = 'Ce rayon de lune vous enverra à Orgrimmar. Utilisez votre invitation lorsque vous voudrez partir.', Text3_0_loc2 = 'N\'essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text3_1_loc2 = 'N\'essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8221;
UPDATE locales_npc_text SET entry = 8222, Text0_0_loc2 = 'J\'aime la Fête lunaire. C\'est un bon endroit pour se dérouiller les sabots !', Text0_1_loc2 = 'J\'aime la Fête lunaire. C\'est un bon endroit pour se dérouiller les sabots !', Text1_0_loc2 = 'Je ne sais pas comment marchent ces feux d\'artifice, mais ils sont très beaux !', Text1_1_loc2 = 'Je ne sais pas comment marchent ces feux d\'artifice, mais ils sont très beaux !', Text2_0_loc2 = 'Cette lumière lunaire vous déposera aux Pitons du Tonnerre. Utilisez-la lorsque vous voudrez quitter la fête.', Text2_1_loc2 = 'Cette lumière lunaire vous déposera aux Pitons du Tonnerre. Utilisez-la lorsque vous voudrez quitter la fête.', Text3_0_loc2 = 'N\'essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text3_1_loc2 = 'N\'essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8222;
UPDATE locales_npc_text SET entry = 8223, Text0_0_loc2 = 'Rien de tel qu\'une petite célébration lunaire pour vous réchauffer les os !', Text0_1_loc2 = 'Rien de tel qu\'une petite célébration lunaire pour vous réchauffer les os !', Text1_0_loc2 = 'Faites attention. Difficile d\'allumer des feux d\'artifice lorsque vous ne sentez pas vos doigts.', Text1_1_loc2 = 'Faites attention. Difficile d\'allumer des feux d\'artifice lorsque vous ne sentez pas vos doigts.', Text2_0_loc2 = 'Ce rayon de lune conduit à Fossoyeuse. Retirez-vous quand vous le désirerez.', Text2_1_loc2 = 'Ce rayon de lune conduit à Fossoyeuse. Retirez-vous quand vous le désirerez.', Text3_0_loc2 = 'N\'essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text3_1_loc2 = 'N\'essayez pas de vous téléporter dans une des villes ennemies. Cela ne marchera pas. Ici, nous sommes tous amis, mais nos vieilles rancœurs nous attendent à la maison.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8223;
UPDATE locales_npc_text SET entry = 8225, Text0_0_loc2 = 'Présage dort dans les profondeurs du lac Elune’ara, mais il peut être invoqué depuis la rive sud-est, près du refuge des saisons de Malfurion. Vous y trouverez des lanceurs de chapelets spéciaux…$B$BUtilisez vos comètes dans ces lanceurs, et les serviteurs de Présage viendront. Tirez-en d’autres, environ trois douzaines, ou lancez la Chandelle d’Elune sur ses serviteurs, et il s’éveillera.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8225;
UPDATE locales_npc_text SET entry = 8226, Text0_0_loc2 = 'Nous travaillons depuis longtemps pour rendre sa gloire passée à ce pays, $c. Nous devrons toujours rester vigilants face ceux qui ont envahi et profané Quel’Thalas. Nous n’aurons ni trêve ni repos tant qu’ils n’auront pas été chassés.$B$BNul ne se met en travers du chemin des sin’dorei !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8226;
UPDATE locales_npc_text SET entry = 8227, Text0_0_loc2 = null, Text0_1_loc2 = 'C’est un affreux gâchis, $c, et c’est entièrement de ma faute ! On m’avait bien dit de quitter les quais tant qu’il en était encore temps. Est-ce que j\'en ai tenu compte ? Non !$B$BQu’est-ce que je vais faire, maintenant ? Ces elfes de sang à l’air malade, les Déshérités, se sont enfuis avec mes marchandises magiques. Je les soupçonne de les avoir mangées. Je suis à peu près sûre que cela veut dire que je ne pourrai pas les récupérer. Ensuite, les murlocs Sinistrécaille ont dérobé le reste de mes produits au milieu de la nuit ! Enfin, pour accumuler toutes les calamités sur la tête d’une malheureuse marchande honnête, mon équipage a déserté !$B$BJe crois que je vais pleurer…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8227;
UPDATE locales_npc_text SET entry = 8228, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $c. En vérité, ces temps sont troubles, si c’est ce que doit devenir notre Quel’Thalas bien-aimée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8228;
UPDATE locales_npc_text SET entry = 8229, Text0_0_loc2 = 'Les Déshérités sont une nuisance permanente, $N. Lorsqu’ils ne se mettent pas eux-mêmes en danger en puisant dans des énergies arcaniques instables pour satisfaire leurs appétits, ils mettent sur pied des complots imbéciles contre notre respectable société.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8229;
UPDATE locales_npc_text SET entry = 8232, Text0_0_loc2 = 'J’espère que Loralthalis va bien, $c. Je devrais peut-être aller moi-même à la flèche de Ternesoir ? Peut-être aurais-je dû y aller le premier ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8232;
UPDATE locales_npc_text SET entry = 8233, Text0_0_loc2 = 'On ne peut vivre avec sa honte que pendant un temps, $c, avant de se sentir obligé d’agir pour corriger ses erreurs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8233;
UPDATE locales_npc_text SET entry = 8234, Text0_0_loc2 = 'Un sale gâchis, voilà ce que c’est ! Toutes ces créatures qui sortent de nulle part et qui courent partout, complètement hors de contrôle ! Et où sont passés le magistère et ses apprentis ? Ils m’ont laissé tout seul ! Un petit « bonjour Wyllithen, voudriez-vous vous joindre à nous ? » en passant ne les aurait pas tués, pourtant !$B$BQuel culot !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8234;
