UPDATE locales_npc_text SET entry = 8238, Text0_0_loc2 = 'Hé, $gmessire:damoiselle;. Vous semblez être $gun:une:c; $c au palais délicat, capable de goûter les bons vins. J\'ai une faveur à vous demander, et je pense pouvoir vous faire une offre que vous ne pourrez pas refuser… Oh, laissez tomber !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8238;
