UPDATE locales_npc_text SET entry = 8240, Text0_0_loc2 = 'Ah, vous devez être $gle gars:la fille; qui doit me faire visiter le bois des Chants éternels. Vous avez pris votre temps pour arriver, hein ?$B$BBon, très bien, allons-y. Ne gâchons pas d\'autres minutes, nous en avons gaspillé assez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8240;
