UPDATE locales_npc_text SET entry = 8292, Text0_0_loc2 = 'Quoi ? Comment avez-vous fait pour éviter mon geôl… mon sbire, là dehors ? Enfin, je suppose que c’est une bonne chose. Ces temps-ci, les ventes sont médiocres.$B$BQuels besoins infernaux puis-je combler pour vous, $c ?', Text0_1_loc2 = null, Text1_0_loc2 = 'C’est très astucieux, la façon dont vous êtes $gentré:entrée; ici, $N.$B$BQuoi ? Cela vous surprend que je connaisse votre nom ? Je suis un démon, c’est mon métier. Je connais aussi votre vrai nom, donc vous feriez mieux d’acheter quelque chose de très cher.', Text1_1_loc2 = null, Text2_0_loc2 = 'Vous êtes ici pour le bâtonnet ? Ou peut-être pour faire une provision de runes ?$B$BAttendez une minute ! Vous essayez seulement de me fourguer tout un tas de trucs inutiles, c’est ça, hein ? Que soit maudite mon obsession continuelle d’acheter, acheter, acheter !', Text2_1_loc2 = null, Text3_0_loc2 = 'Quoi ? Non, non, je ne répare pas les armures. Long-guet se trouve à une demi-lieue au nord, $gmon frère:ma sœur;.$B$BMaintenant, achetez-moi quelque chose, ou fichez le camp avant que j’appelle mon démoniste pour qu’il s’occupe de vous !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8292;
