UPDATE locales_npc_text SET entry = 8306, Text0_0_loc2 = 'Moi non, mais beaucoup de gens cherchent l’amour. Vous devriez mettre un peu de parfum ou d’eau de Cologne, acheter mes gages d’amour, et les faire circuler !', Text0_1_loc2 = 'Moi non, mais beaucoup de gens cherchent l’amour. Vous devriez mettre un peu de parfum ou d’eau de Cologne, acheter mes gages d’amour, et les faire circuler !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8306;
UPDATE locales_npc_text SET entry = 8307, Text0_0_loc2 = 'Les gages d’amour sont de petits messages d’affection que vous pouvez donner aux habitants des villes et des capitales qui ont le cœur à l’amour. En échange, elles vous offriront un cadeau d’adoration.$B$BSi vous êtes déjà $gadoré:adorée; par quelqu’un d’autre, elles vous remettront au moins un cadeau d’amitié.', Text0_1_loc2 = 'Les gages d’amour sont de petits messages d’affection que vous pouvez donner aux habitants des villes et des capitales qui ont le cœur à l’amour. En échange, elles vous offriront un cadeau d’adoration.$B$BSi vous êtes déjà $gadoré:adorée; par quelqu’un d’autre, elles vous remettront au moins un cadeau d’amitié.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8307;
UPDATE locales_npc_text SET entry = 8308, Text0_0_loc2 = 'Si vous mettez de l’eau de Cologne ou du parfum, vous verrez qui a le cœur à l’amour. Tous acceptent les gages d’amour… et vous offrent un don en échange.$B$BAttention, pour donner un gage d’amour à quelqu’un, vous devez avoir la bonne odeur. Les hommes aiment l’odeur du parfum, et les femmes celles de l’eau de Cologne.', Text0_1_loc2 = 'Si vous mettez de l’eau de Cologne ou du parfum, vous verrez qui a le cœur à l’amour. Tous acceptent les gages d’amour… et vous offrent un don en échange.$B$BAttention, pour donner un gage d’amour à quelqu’un, vous devez avoir la bonne odeur. Les hommes aiment l’odeur du parfum, et les femmes celles de l’eau de Cologne.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8308;
UPDATE locales_npc_text SET entry = 8312, Text0_0_loc2 = 'Vous voulez savoir qui est le héros le plus populaire du royaume ? Eh bien, voici les derniers chiffres !$B$BThrall : $2200w$BCairne : $2201w$BSylvanas : $2202w$BTotal de la Horde : $2207w$B$B$BBolvar : $2203w$BMagni : $2204w$BTyrande : $2205w$BTotal de l\'Alliance : $2206w$B$BSi vous voulez voter, apportez vos assortiments de cadeaux !', Text0_1_loc2 = 'Vous voulez savoir qui est le héros le plus populaire du royaume ? Eh bien, voici les derniers chiffres !$B$BThrall : $2200w$BCairne : $2201w$BSylvanas : $2202w$BTotal de la Horde : $2207w$B$B$BBolvar : $2203w$BMagni : $2204w$BTyrande : $2205w$BTotal de l\'Alliance : $2206w$B$BSi vous voulez voter, apportez vos assortiments de cadeaux !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8312;
