UPDATE locales_npc_text SET entry = 8319, Text0_0_loc2 = 'Quand vous donnez des gages d\'amour aux habitants des villes, vous recevez différents cadeaux en retour. Vous pouvez en garder ou en utiliser certains, mais vous pouvez aussi en faire des assortiments que vous donnerez à votre héros préféré !$B$BAllez voir Kalin Colportecœur pour donner vos cadeaux.$B$BVous pourrez trouver Kalin près de Cairne, Sylvanas et Thrall.', Text0_1_loc2 = 'Quand vous donnez des gages d\'amour aux habitants des villes, vous recevez différents cadeaux en retour. Vous pouvez en garder ou en utiliser certains, mais vous pouvez aussi en faire des assortiments que vous donnerez à votre héros préféré !$B$BAllez voir Kalin Colportecœur pour donner vos cadeaux.$B$BVous pourrez trouver Kalin près de Cairne, Sylvanas et Thrall.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8319;
UPDATE locales_npc_text SET entry = 8321, Text0_0_loc2 = 'Le contenu de cette page change sans arrêt. Vous avez l’impression que vos pensées s’enfuient de votre crâne avant d’être absorbées par le livre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8321;
UPDATE locales_npc_text SET entry = 8326, Text0_0_loc2 = 'Des images, des formes et des symboles dansent devant vos yeux. Le livre semble être informé de votre compétence dans le travail du cuir, et il vous offre un choix. Attention, une fois que vous aurez pris une décision, vous ne pourrez plus changer d’avis !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8326;
UPDATE locales_npc_text SET entry = 8327, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8327;
UPDATE locales_npc_text SET entry = 8332, Text0_0_loc2 = 'Thrall a fait preuve d’une grande générosité en m’accordant l’asile dans son domaine. Je ne veux pas abuser de son hospitalité. Si j’attirais des ennuis à notre chef, je serais déshonoré.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8332;
UPDATE locales_npc_text SET entry = 8333, Text0_0_loc2 = null, Text0_1_loc2 = 'Essayez de ne pas trop attirer l’attention. J’attends que le roi m’accorde une audience. Cela ne m’ennuie pas du tout de patienter, je me sens en sécurité avec tous ces gardes costauds autour de moi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8333;
UPDATE locales_npc_text SET entry = 8334, Text0_0_loc2 = 'Je me suis fait à l\'idée de perdre la vie. Perdre Ysida, en revanche, est insupportable, même après ma mort.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8334;
UPDATE locales_npc_text SET entry = 8335, Text0_0_loc2 = 'Bien le bonjour ! Seriez-vous $gintéressé:intéressée; par des condensateurs ectoplasmiques ? À moins que nous ne vous laissiez tenter par ce superbe assimilateur spectral omnidirectionnel ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8335;
UPDATE locales_npc_text SET entry = 8336, Text0_0_loc2 = 'Quoi encore ? Vous ne voyez pas que je suis débordé ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8336;
UPDATE locales_npc_text SET entry = 8344, Text0_0_loc2 = null, Text0_1_loc2 = 'Ça va aller maintenant, ne vous inquiétez pas pour moi. Je trouverai un moyen de sortir dès que j’aurai effectué quelques rituels de purification. Cet endroit est pollué par une ignoble magie, $N.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8344;
UPDATE locales_npc_text SET entry = 8347, Text0_0_loc2 = null, Text0_1_loc2 = 'Notre toute dernière attraction, et je me permets d\'insister sur le fait qu\'elle est entièrement gratuite : des chars à vapeur miniaturisés ! Ou plutôt des chariottes, comme nous les avons subtilement rebaptisés, que l\'on peut contrôler depuis les consoles de contrôle de chariotte éparpillées à travers la foire.$B$BTout ce que vous avez à faire, c\'est d\'activer une console de contrôle disponible. Chaque chariotte est équipée d\'un jeu d\'accessoires de base et d\'un pouvoir spécial aléatoire que vous pourrez déchaîner contre votre ennemi !$B$BVivez des batailles épiques avec vos amis, faites une petite promenade, ou semez une prodigieuse pagaille… à vous de décider !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8347;
UPDATE locales_npc_text SET entry = 8349, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8349;
UPDATE locales_npc_text SET entry = 8350, Text0_0_loc2 = null, Text0_1_loc2 = 'On raconte que Kel’Thuzad recherche Atiesh, le grand bâton légendaire du Gardien.$B$BOui, celui-là même que portait autrefois Medivh.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8350;
UPDATE locales_npc_text SET entry = 8351, Text0_0_loc2 = 'Surveillez-vous, $r. Nous autres de la Fraternité de la Lumière ne sommes pas aussi laxistes que nos frères laïques lorsqu’il faut dispenser la justice contre ceux qui veulent nuire au monde.$B$BMalheur à ceux qui déclenchent la colère de Korfax !$B$BMaintenant, si vous n’avez pas d’affaire à traiter avec moi ou avec les autres membres de la Fraternité, merci de nous laisser poursuivre notre réunion.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8351;
UPDATE locales_npc_text SET entry = 8352, Text0_0_loc2 = 'Le roi-liche tente d’imposer sa présence dans notre monde. Nous ne devons pas le laisser faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8352;
UPDATE locales_npc_text SET entry = 8353, Text0_0_loc2 = 'Qu’est-ce que la Fraternité de la Lumière ? Eh bien… à la base nous sommes tous membres de l’Aube d’argent, mais des membres de l’Aube d’argent qui ne sont pas entravés par la morale, la culpabilité ou les vaines émotions humaines. Une sorte de Croisade écarlate, mais sans la bêtise, l’absence de commandement et le fanatisme aveugle.$B$BNe laissez pas le commandant écarlate vous dire le contraire.$B$BEt ne dites pas à Tyrosus que je vous ai dit ça... En cas de bagarre, je l’estropierais et ce serait une honte terrible.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8353;
UPDATE locales_npc_text SET entry = 8354, Text0_0_loc2 = null, Text0_1_loc2 = 'L’Aube d’argent est peut-être constituée d’idéalistes impuissants, mais leurs chefs savent qu’ils ne sont pas de taille. J’ai été envoyée ici pour y être l’émissaire de la Croisade écarlate. Une ambassadrice, si vous préférez... Unis ou pas, je ne crois pas que nous aurons la moindre chance d’entamer les armées de Kel’Thuzad, mais nous arriverons peut-être à les contenir assez longtemps pour permettre un miracle.$B$BMoi-même ? Je mourrai au combat...$B$B<Marjhan hausse les épaules.>', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8354;
UPDATE locales_npc_text SET entry = 8355, Text0_0_loc2 = 'Ce titre peut prêter à confusion. En fait je suis quelqu’un de très bien quand on apprend à me connaître.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8355;
UPDATE locales_npc_text SET entry = 8356, Text0_0_loc2 = 'Ils ont failli envoyer mon frère Loksey à cette réunion. Il n’arrive déjà pas à s’occuper correctement de ses chiens, alors je n’ose pas imaginer son incompétence s’il devait représenter la Croisade écarlate.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8356;
UPDATE locales_npc_text SET entry = 8357, Text0_0_loc2 = 'Je t’attendais, jeune...$B$B<Rimblat tousse.>$B$BJe vous prie de m’excuser, une vieille manie. Je suis Rimblat, du Cercle terrestre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8357;
UPDATE locales_npc_text SET entry = 8358, Text0_0_loc2 = null, Text0_1_loc2 = 'Bonjour, $c. Excusez-moi, mais je suis en pleine conversation avec Rimblat. Il pense que même cet endroit peut encore être sauvé.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8358;
UPDATE locales_npc_text SET entry = 8359, Text0_0_loc2 = 'Qu’est-ce que vous voulez, misérable ragondin ? Hors de ma vue, ou vous allez découvrir pourquoi on m’appelle la Voix du courroux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8359;
UPDATE locales_npc_text SET entry = 8363, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8363;
UPDATE locales_npc_text SET entry = 8364, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8364;
UPDATE locales_npc_text SET entry = 8366, Text0_0_loc2 = 'Je suis ce que vous appelez un capitaliste, $r.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8366;
UPDATE locales_npc_text SET entry = 8374, Text0_0_loc2 = 'Oh, je vous salue… euh… comment vous appelez-vous, déjà ?$B$BAucune importance ! Qu’est-ce qui vous amène dans ma résidence campagnarde au milieu de ces ravissants paysages ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8374;
UPDATE locales_npc_text SET entry = 8375, Text0_0_loc2 = 'Salut, $C. Vous vous amusez ? Que pensez-vous de la fête ? J\'espère que nous ne manquerons de rien. La pire des choses qui puisse nous arriver, c\'est de manquer de hors-d\'œuvre ou pire, de choses à boire, vous êtes d\'accord ?$B$BAh, excusez-moi, il faut que je m\'occupe de mes autres invités. Amusez-vous bien, surtout !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8375;
UPDATE locales_npc_text SET entry = 8378, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8378;
UPDATE locales_npc_text SET entry = 8379, Text0_0_loc2 = null, Text0_1_loc2 = 'N\'est-ce pas qu\'on s\'amuse bien ? Je suis si contente que le seigneur Saltheril organise des fêtes tous les soirs, surtout avec toutes ces vilaines choses qui se passent dans le Sud. C\'est comme si rien n\'était arrivé, pas vrai ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8379;
UPDATE locales_npc_text SET entry = 8388, Text0_0_loc2 = null, Text0_1_loc2 = 'Comme Kel’Thuzad, j’ai appartenu au Kirin Tor. Et comme Kel’Thuzad, je suis à la recherche d’Atiesh, le grand bâton du Gardien. Les trames de nos destins sont entremêlées, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8388;
UPDATE locales_npc_text SET entry = 8389, Text0_0_loc2 = null, Text0_1_loc2 = 'Progéniture d’Aegwynn, celui qui a été le dernier Gardien de Tirisfal… Medivh.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8389;
UPDATE locales_npc_text SET entry = 8390, Text0_0_loc2 = null, Text0_1_loc2 = 'L’histoire et la légende se mêlent, et je n’ai ni le temps ni l’envie de les raconter encore. Sachez seulement que Medivh a été le dernier des Gardiens. Le grand bâton Atiesh était un point focal de ses pouvoirs… et de sa corruption.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8390;
UPDATE locales_npc_text SET entry = 8391, Text0_0_loc2 = null, Text0_1_loc2 = 'La corruption d’un Titan déchu. La corruption du Destructeur. Après tout, le bâton fut forgé dans la graine même de la haine. C’est ce qui m’amène ici…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8391;
UPDATE locales_npc_text SET entry = 8392, Text0_0_loc2 = null, Text0_1_loc2 = '« Après la chute de Karazhan, le Kirin Tor a envoyé un groupe de mages fouiller la tour et s’emparer de tous les objets importants… pour les besoins de ses "recherches". C’est alors que le bâton légendaire a été découvert et ramené à Dalaran. »', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8392;
UPDATE locales_npc_text SET entry = 8393, Text0_0_loc2 = null, Text0_1_loc2 = 'Il s’en est suivi une série d’événements tragiques. Tous les mages du Kirin Tor qui sont entrés en contact avec Atiesh sont morts.$B$BIl se trouve qu\'il s\'agit d\'un artefact doué de conscience. Une conscience qui lui avait été octroyée par Sargeras.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8393;
UPDATE locales_npc_text SET entry = 8394, Text0_0_loc2 = null, Text0_1_loc2 = '<Angela acquiesce.>$B$BAprès ces morts, les Six décidèrent que la meilleure solution était de sceller le bâton derrière des barrières magiques impénétrables et de nommer un gardien, quelqu’un qui veillerait sur le bâton…. J’ai été choisie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8394;
UPDATE locales_npc_text SET entry = 8395, Text0_0_loc2 = null, Text0_1_loc2 = 'Il n’y a pas grand-chose à ajouter. Dalaran a été presque complètement détruite par Archimonde. Le bâton a été perdu, probablement détruit par les sécurités des barrières.$B$BCertains pensent que le bâton a été la cause des représailles d’Archimonde.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8395;
UPDATE locales_npc_text SET entry = 8396, Text0_0_loc2 = null, Text0_1_loc2 = 'Après la chute de Dalaran, j’ai quitté le Kirin Tor. J’avais passé de longues années à veiller sur Atiesh et d’un seul coup, ma raison de rester au Kirin Tor m’a été arrachée. Je me suis promis de retrouver le bâton, quitte à y consacrer le reste de ma vie. C’est pour cela que j’ai rejoint l’Aube d’argent, et c’est pour cela que je suis ici.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8396;
UPDATE locales_npc_text SET entry = 8397, Text0_0_loc2 = null, Text0_1_loc2 = 'J’ai parcouru le monde à la recherche des différents fragments du bâton, et j’ai vite découvert que Kel’Thuzad voulait le reconstituer. Il en détient déjà plusieurs morceaux et les a confiés à ses lieutenants les plus proches.$B$BIl faut absolument que le grand bâton du Gardien reste hors des griffes de Kel’Thuzad. Je ne sais pas ce qui se produirait s’il arrivait à ses fins, mais ce serait certainement une catastrophe d’une ampleur inimaginable.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8397;
UPDATE locales_npc_text SET entry = 8398, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8398;
UPDATE locales_npc_text SET entry = 8399, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8399;
UPDATE locales_npc_text SET entry = 8401, Text0_0_loc2 = 'Je vous salue, $gvoyageur:voyageuse;. J\'ai pour instructions de traiter quiconque détient la clé de Karazhan comme un invité d\'honneur du Maître. N\'hésitez pas à me poser toutes les questions que vous souhaitez. En quoi puis-je vous être utile ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8401;
UPDATE locales_npc_text SET entry = 8402, Text0_0_loc2 = 'Nous sommes trop tolérants envers les Déshérités. Nous aurions tous dû les passer au fil de l’épée.$B$BLeur audace au mouillage des Voiles du soleil est impardonnable.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8402;
UPDATE locales_npc_text SET entry = 8403, Text0_0_loc2 = null, Text0_1_loc2 = 'Ce fut une longue journée, passée à massacrer les monstres décérébrés du Fléau qui sillonnent la Malebrèche. J’espère que vous êtes là pour donner un coup de main et pas pour bavarder.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8403;
UPDATE locales_npc_text SET entry = 8404, Text0_0_loc2 = 'Ah, des invités du Maître. J\'espère que vous faites attention où vous posez les pieds.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8404;
UPDATE locales_npc_text SET entry = 8406, Text0_0_loc2 = 'Maudites soient ces vermines ! Je suppose que vous ne pourriez pas m\'aider ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8406;
UPDATE locales_npc_text SET entry = 8407, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8407;
UPDATE locales_npc_text SET entry = 8408, Text0_0_loc2 = 'Ces sales bêtes sont absolument partout.$B$BSi vous préférez chasser à l\'extérieur, je vous recommande Pestebois ou Val-terreur. Elles sont particulièrement nombreuses dans le tunnel de Tisse-terreur. Vous avez aussi des chances d\'en trouver dans n\'importe quel camp du Fléau dans les Maleterres de l\'est.$B$BSi vous êtes de ceux qui préfèrent ramper dans des donjons, vous en trouverez des tas à Stratholme, plus quelques spécimens de la variété nécrofiel à la Scholomance.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8408;
UPDATE locales_npc_text SET entry = 8409, Text0_0_loc2 = 'Vous êtes débile ? Vous n\'avez pas remarqué ces créatures-squelettes TOUTES PLEINES D\'OS qui encombrent le paysage partout autour de vous ?$B$B<Rohan soupire.>$B$BBon, si vous aimez le grand air, vous trouverez des créatures squelettiques dans les deux Maleterres, de l\'est et de l\'ouest. Vous ne pouvez pas les rater… et elles ne vous rateront pas non plus, si vous en donnez l\'occasion.$B$BMaintenant, si vous préférez les donjons, Stratholme et à la Scholomance sont de bonne sources de fragments d\'os.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8409;
UPDATE locales_npc_text SET entry = 8410, Text0_0_loc2 = null, Text0_1_loc2 = 'Un autre jour parfait et déprimant à Tranquillien, pas vrai, $c ? Que puis-je faire pour vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8410;
UPDATE locales_npc_text SET entry = 8411, Text0_0_loc2 = 'Fendre les cieux… Il n’est pas de plus grande liberté, $c. Que puis-je faire pour vous aujourd’hui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8411;
UPDATE locales_npc_text SET entry = 8412, Text0_0_loc2 = 'Je vous salue, mon $gami:amie;. En quoi puis-je vous servir aujourd’hui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8412;
UPDATE locales_npc_text SET entry = 8413, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous allez à Tranquillien aujourd’hui, $c, ou il y a autre chose pour votre service ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8413;
UPDATE locales_npc_text SET entry = 8414, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8414;
UPDATE locales_npc_text SET entry = 8415, Text0_0_loc2 = 'Vous plaisantez ? Vous ne savez vraiment pas ?$B$BBon… Alors, si vous êtes du genre un peu pleutre qui ne se salit pas trop les mains, vous trouverez des morceaux de sombrefer sur les créatures des Steppes ardentes et de la gorge des Vents brûlants.$B$BSi vous avez soif de sang et d\'aventure, les donjons du pic Rochenoire et des profondeurs de Rochenoire en renferment des quantités.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8415;
UPDATE locales_npc_text SET entry = 8416, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous aimez chasser en extérieur, les tréants de Gangrebois et les flagellants d’Un’Goro sont une excellente source de palmes.$B$BSi vous avez le goût du risque, les ailes est et ouest de Hache-tripes vous en assureront une quantité presque illimitée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8416;
UPDATE locales_npc_text SET entry = 8417, Text0_0_loc2 = 'Voyez ce qu\'ils ont fait à notre pays bien-aimé, $c. Le Fléau doit être anéanti !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8417;
UPDATE locales_npc_text SET entry = 8418, Text0_0_loc2 = null, Text0_1_loc2 = 'Cela va demander un peu de travail, mais je pense que nous pouvons sauver la vie de cet elfe de sang.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8418;
UPDATE locales_npc_text SET entry = 8419, Text0_0_loc2 = 'Qui êtes vous ? J\'ai… si… froid.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8419;
UPDATE locales_npc_text SET entry = 8421, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous autres sin’dorei avons perdu une bonne part de nos anciennes terres. C’est une humiliation. Avant longtemps, nous devrons recouvrer nos forces et reprendre nos terres méridionales au Fléau !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8421;
UPDATE locales_npc_text SET entry = 8422, Text0_0_loc2 = 'J’ai pas envie de jouer à « devine ce qui pue » avec vous, $r. Dites-moi ce que vous voulez et disparaissez de ma vue !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8422;
UPDATE locales_npc_text SET entry = 8423, Text0_0_loc2 = 'Le grand exécuteur et la dame Auriférias me font attendre, mais je n’ai pas de temps à perdre ! Dehors, mes amis se font tuer, $c !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8423;
UPDATE locales_npc_text SET entry = 8424, Text0_0_loc2 = null, Text0_1_loc2 = 'J’ai ordonné aux forestiers Salissa et Lethvalin de se rendre respectivement à l’enclave, au sud, et à Tranquillien, à l’ouest. Cela fait plus d’une journée ! Les avez-vous vus ? Ils avaient dit qu’ils enverraient de l’aide. Est-ce vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8424;
UPDATE locales_npc_text SET entry = 8425, Text0_0_loc2 = 'Parfois, j\'ai l\'impression que je ne redeviendrai jamais un simple fonctionnaire au service de dame Auriférias. Je ne suis pas taillé pour la guerre.$B$BHeureusement, il existe des gens comme vous qui brûlent de voir Quel\'Thalas retrouver sa beauté passée. Écrasez le Fléau, $c ! Écrasez-le où que vous le rencontriez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8425;
UPDATE locales_npc_text SET entry = 8426, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8426;
UPDATE locales_npc_text SET entry = 8428, Text0_0_loc2 = 'J’ai été informé que vous avez pu aider le forestier Valanna et le lieutenant à Zeb’Sora.$B$BMerci, $c. Je suppose que je devrais rentrer à l\'enclave des Pérégrins à présent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8428;
UPDATE locales_npc_text SET entry = 8429, Text0_0_loc2 = 'Qu\'est-ce qui vous amène ici, $c ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8429;
UPDATE locales_npc_text SET entry = 8431, Text0_0_loc2 = 'Ah oui, bonjour $r. Comme vous pouvez le voir, mon commerce n’est pas encore ouvert. Excusez-moi, j’ai beaucoup à faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8431;
UPDATE locales_npc_text SET entry = 8432, Text0_0_loc2 = 'C’est vraiment un plaisir de vous revoir, $c. Non, sincèrement. Grâce à vous, je me fais des tas et des tas d’argent ici.$B$BEh bien, que puis-je vous vendre aujourd’hui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8432;
UPDATE locales_npc_text SET entry = 8433, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à Tranquillien, $c, et mes excuses pour l’état dans lequel vous le trouvez. Comme vous le voyez, nous avons eu quelques ennuis, mais les Réprouvés et dame Sylvanas Coursevent ont eu la bonté de nous apporter un secours aussi important qu’opportun.$B$BSi vous pouvez nous prêter main-forte, je vous prie de ne pas hésiter à le faire. Aucune aide ne sera de trop face à la multitude de factions qui se liguent contre nous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8433;
UPDATE locales_npc_text SET entry = 8434, Text0_0_loc2 = 'Le temps est venu pour la Horde et l’Alliance de tourner les yeux vers le Norfendre, vers l’invasion du roi-liche. Ces derniers temps, de nombreux territoires de Kalimdor et des Royaumes de l\'est ont été attaqués. Prendrez-vous les armes pour sauver votre monde de la destruction ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8434;
UPDATE locales_npc_text SET entry = 8435, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8435;
UPDATE locales_npc_text SET entry = 8438, Text0_0_loc2 = 'C\'est affreux, ce qui est arrivé à ces pauvres mules… Metz aura ma peau pour ça.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8438;
UPDATE locales_npc_text SET entry = 8439, Text0_0_loc2 = null, Text0_1_loc2 = 'Il est très important que nous découvrions ce que préparent les elfes de la nuit, $c ! Concentrez tous vos efforts dans ce but.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8439;
UPDATE locales_npc_text SET entry = 8440, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8440;
UPDATE locales_npc_text SET entry = 8441, Text0_0_loc2 = 'Vous êtes les renforts ? Les elfes de la nuit ont été mis en déroute !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8441;
UPDATE locales_npc_text SET entry = 8442, Text0_0_loc2 = 'Eh bien, $C, heureusement que vous avez désactivé le cristal de lune d\'An\'owyn au bon moment ! $B$BN\'hésitez surtout pas à intervenir de nouveau dans mes recherches !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8442;
UPDATE locales_npc_text SET entry = 8445, Text0_0_loc2 = 'Comment travailler dans ces conditions ? Et avec tous ces Réprouvés répugnants aux alentours ! Je pleure sur Tranquillien et Quel\'Thalas !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8445;
UPDATE locales_npc_text SET entry = 8446, Text0_0_loc2 = 'Oui… qu’est-ce que voulez, $r ? Vous ne voyez pas que je suis occupé ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8446;
UPDATE locales_npc_text SET entry = 8447, Text0_0_loc2 = '$R, au rapport ! Quelles nouvelles apportez-vous ? Allons, faites vite… La dame Auriférias et moi sommes au beau milieu d’une discussion consacrée à notre stratégie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8447;
UPDATE locales_npc_text SET entry = 8448, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à la chapelle de l\'Espoir de Lumière, $gétranger:étrangère;.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8448;
UPDATE locales_npc_text SET entry = 8452, Text0_0_loc2 = 'Nos caravanes font la navette deux fois par jour, mais que je sois damné si le Fléau ne fait pas de gros efforts pour empêcher Didier et ses mules de ravitailler le front. Si seulement nous avions quelques aventuriers héroïques pour protéger les chargements…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8452;
UPDATE locales_npc_text SET entry = 8454, Text0_0_loc2 = 'J\'ai beaucoup à faire et je manque de temps, $r. Alors sortez de mon chemin.', Text0_1_loc2 = 'J\'ai beaucoup à faire et je manque de temps, $r. Alors sortez de mon chemin.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8454;
UPDATE locales_npc_text SET entry = 8455, Text0_0_loc2 = 'J\'ai énormément à faire, et pas assez de temps pour le faire, $r ! Sortez de mon chemin s\'il vous plaît.$B$BSi vous avez été $gcontaminé:contaminée; par de la bave de ver ou de la pourriture de goule, je pourrai vous aider.', Text0_1_loc2 = 'J\'ai énormément à faire, et pas assez de temps pour le faire, $r ! Sortez de mon chemin s\'il vous plaît.$B$BSi vous avez été $gcontaminé:contaminée; par de la bave de ver ou de la pourriture de goule, je pourrai vous aider.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8455;
UPDATE locales_npc_text SET entry = 8456, Text0_0_loc2 = '<L’elfe de sang reprend rapidement conscience lorsque vous lui versez le breuvage dans la bouche.>$B$BJe vous dois la vie, $c. Sans votre aide, j’aurais succombé aux horribles tortures du Fléau.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8456;
UPDATE locales_npc_text SET entry = 8457, Text0_0_loc2 = '<L’elfe de sang gît sur le sol, plongé dans une torpeur artificielle.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8457;
UPDATE locales_npc_text SET entry = 8458, Text0_0_loc2 = 'Je vous salue, $r. Je suis Tristesoleil, l\'ambassadeur des sin’dorei, ou des elfes de sang si vous préférez notre nom usuel. J’espère que les nouveaux liens qui nous unissent à la Horde nous profiteront à tous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8458;
UPDATE locales_npc_text SET entry = 8459, Text0_0_loc2 = null, Text0_1_loc2 = '<La Réprouvée est blottie sur le sol, son visage crispé dans une horrible grimace. Pour l’instant, elle ne semble pas capable de se lever seule.>', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8459;
UPDATE locales_npc_text SET entry = 8460, Text0_0_loc2 = null, Text0_1_loc2 = 'Ahhhhhggg… cette douleur dans la tête me tue.$B$BIls vous ont $genvoyé:envoyée; pour me sauver, je suppose. Ils ont pris leur temps. Vous savez ce qu’ils font aux prisonniers, dans le coin ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8460;
UPDATE locales_npc_text SET entry = 8461, Text0_0_loc2 = '<L’elfe de sang est allongé sur le sol, comme un pantin aux fils coupés. Il respire encore, mais il semble avoir été victime de toxines à l\'effet sédatif.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8461;
UPDATE locales_npc_text SET entry = 8462, Text0_0_loc2 = 'Voilà une heureuse rencontre, $R. Je suis ici pour servir de conseiller au grand exécuteur… et, plus généralement, de ressource qu\'il utilise comme l\'entend. Je suis certain qu\'ensemble, nous forgerons une Horde plus puissante qu\'autrefois et que nous chasserons une fois pour toutes la menace du Fléau !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8462;
UPDATE locales_npc_text SET entry = 8463, Text0_0_loc2 = '<Varnis tousse violemment lorsque le breuvage de restauration agit.>$B$BMerci de m’avoir sauvé. Je dois retourner à Tranquillien sur-le-champ !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8463;
UPDATE locales_npc_text SET entry = 8464, Text0_0_loc2 = 'Les courants d\'air frais qui soufflent de temps en temps depuis le bois des Chants éternels me font vraiment mal au cœur, si vous voyez ce que je veux dire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8464;
UPDATE locales_npc_text SET entry = 8466, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous voulez parler de quelque chose d’important, $c, il serait plus convenable de demander audience à dame Auriférias. Mais je vous préviens, elle est très occupée, et vous risquez de faire antichambre pendant un certain temps.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8466;
UPDATE locales_npc_text SET entry = 8467, Text0_0_loc2 = null, Text0_1_loc2 = 'En temps normal, je ne vous laisserais pas me parler directement. Vous auriez dû demander une audience auprès de ma dame. Toutefois, comme je vous ai donné une tâche directement... que voulez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8467;
UPDATE locales_npc_text SET entry = 8468, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8468;
UPDATE locales_npc_text SET entry = 8469, Text0_0_loc2 = 'Ce panneau est couvert d\'annonces et d\'avis de recherche.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8469;
UPDATE locales_npc_text SET entry = 8470, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons beaucoup à faire, $N. Nos ennemis ont baissé leur garde. Il est temps de les attaquer. Nous allons leur montrer que les sin’dorei n’ont rien oublié et rien pardonné.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8470;
UPDATE locales_npc_text SET entry = 8471, Text0_0_loc2 = 'Les flots de la guerre déferlent à nouveau. Sorties des glaces du nord, les nécropoles du roi-liche assiègent nos terres. Il envoit ces nécropoles volantes à la conquête du monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8471;
UPDATE locales_npc_text SET entry = 8472, Text0_0_loc2 = null, Text0_1_loc2 = 'Voilà qui est fâcheux ! Comment pourrais-je ramasser des échantillons de minerai pour les magistères, si les mines sont envahies par de la vermine gnoll ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8472;
UPDATE locales_npc_text SET entry = 8474, Text0_0_loc2 = 'Où peut-elle être ? J’ai envoyé mon apprentie, Shatharia, aux mines de Terradiance, à l’ouest. Cela fait un bon moment qu’elle est partie. J’ai besoin de ces échantillons de minerai, et vite, ou notre expérience échouera !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8474;
UPDATE locales_npc_text SET entry = 8475, Text0_0_loc2 = 'Sans le minerai que vous nous avez fourni juste à temps, nous nous serions retrouvés dans une impasse.$B$BAttendez un peu que Shatharia pointe le bout de son nez ! Je ne manquerai pas de lui trouver des tâches… intéressantes, croyez-moi !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8475;
UPDATE locales_npc_text SET entry = 8476, Text0_0_loc2 = null, Text0_1_loc2 = 'Les Sin’dorei ont tant perdu au cours de ces dernières années. Nos terres, nos foyers et même nos identités…$B$BIl est temps de commencer à reprendre tout cela.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8476;
UPDATE locales_npc_text SET entry = 8477, Text0_0_loc2 = 'Avant de triompher des menaces extérieures comme le Fléau et les trolls Amani, nous devons venir à bout des obstacles internes. Les Déshérités nous ont nui pendant assez longtemps. Il est temps de les vaincre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8477;
UPDATE locales_npc_text SET entry = 8478, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous pensez que vous pourrez m\'aider à sortir de là ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8478;
UPDATE locales_npc_text SET entry = 8479, Text0_0_loc2 = 'Parmi les différents morceaux de parchemins contenant des listes et d’autres informations concernant les pérégrins, quelque chose attire votre attention. C’est le dessin d’un troll hideux, sans doute le monstre le plus effrayant que vous ayez jamais vu !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8479;
UPDATE locales_npc_text SET entry = 8480, Text0_0_loc2 = 'En effet, un nouvel assaut du Fléau se déroule dans les collines du Berceau-de-l’hiver. Votre intervention serait très bienvenue.$B$BIl reste $2284W nécropoles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8480;
UPDATE locales_npc_text SET entry = 8481, Text0_0_loc2 = 'Pour l’instant, cette région est épargnée par l’influence du Fléau. Mais je crains que ce soit qu’une question de temps avant son retour.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8481;
