UPDATE locales_npc_text SET entry = 8484, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8484;
UPDATE locales_npc_text SET entry = 8485, Text0_0_loc2 = null, Text0_1_loc2 = 'Il y a beaucoup à gagner en apprenant à connaître son ennemi. Retenez bien cette leçon, $N.$B$BPlus nous en saurons au sujet du Fléau, mieux nous serons préparés à le vaincre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8485;
UPDATE locales_npc_text SET entry = 8486, Text0_0_loc2 = 'Le Fléau établit de petites bases dans les régions qu’il attaque. Il reçoit de l’aide et des messages des nécropoles qui flottent au-dessus. Selon nous, le seul moyen de l’extirper est de tuer les troupes au sol qui entourent ces points.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8486;
UPDATE locales_npc_text SET entry = 8487, Text0_0_loc2 = null, Text0_1_loc2 = 'Restez à l’enclave des Pérégrins aussi longtemps qu’il vous plaira, $c. Nous accueillons à bras ouverts tous ceux qui sont disposés à participer à nos opérations dans les Terres fantômes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8487;
UPDATE locales_npc_text SET entry = 8488, Text0_0_loc2 = 'Des forces arcaniques incontrôlées peuvent faire des choses étranges à la terre, aux éléments et même aux esprits des morts.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8488;
UPDATE locales_npc_text SET entry = 8489, Text0_0_loc2 = 'Le grand exécuteur est très occupé, $c. Je pourrais peut-être vous aider ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8489;
UPDATE locales_npc_text SET entry = 8490, Text0_0_loc2 = 'Vous servez notre cause de manière exemplaire, $N. Entre les gnolls des mines de Terradiance et les trolls momifiés des catacombes des Amani, vous avez, à vous $gseul:seule;, porté de rudes coups à nos ennemis !$B$BNous devrions aller boire un verre ensemble un de ces jours, qu’en dites-vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8490;
UPDATE locales_npc_text SET entry = 8491, Text0_0_loc2 = 'C’est bon de vous revoir, $gl\'ami:beauté;. Nous avons bien travaillé ensemble. $gÀ bientôt.: J’espère qu’on va continuer comme ça.;', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8491;
