UPDATE locales_npc_text SET entry = 8500, Text0_0_loc2 = null, Text0_1_loc2 = 'Certainement, $N.$B$BLes aventuriers honorés par l\'Aube doivent soumettre un total combiné de 14 insignes pour avoir accès à des objets de qualité supérieure. Pour avoir accès à des objets encore plus puissants, ils doivent nous remettre 90 insignes.$B$BJe reconnais que c\'est un prix élevé, même pour ceux qui sont révérés au sein de l\'Aube.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8500;
UPDATE locales_npc_text SET entry = 8501, Text0_0_loc2 = 'La situation est grave, beaucoup plus grave que ce qu’imaginent la plupart des gens. Les Réprouvés sont ici nos alliés dans notre combat contre notre ennemi commun, le Fléau.$B$BCroyez-moi, sans eux, nous aurions perdu les Terres fantômes et le Fléau se serait déjà répandu dans tout le bois des Chants éternels !$B$BNous avons désespérément besoin de votre aide pour reconquérir nos terres qui furent si belles !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8501;
UPDATE locales_npc_text SET entry = 8502, Text0_0_loc2 = null, Text0_1_loc2 = 'Certainement, $N.$B$BLes aventuriers exaltés par l\'Aube doivent soumettre un total combiné de 12 insignes pour avoir accès à des objets de qualité supérieure. Pour avoir accès à des objets encore plus puissants, ils doivent nous remettre 54 insignes.$B$BJe reconnais que c\'est un prix élevé, même pour ceux qui sont exaltés au sein de l\'Aube.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8502;
UPDATE locales_npc_text SET entry = 8503, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8503;
UPDATE locales_npc_text SET entry = 8504, Text0_0_loc2 = 'Ces pierres runiques protègent les territoires des Chants éternels de la corruption du Fléau. La pierre runique de l’ouest a cessé de fonctionner il y a longtemps, et nous avons été contraints de carboniser le sol autour de sa position.$B$BAussi longtemps que je vivrais, je ne laisserai pas s’éteindre les pierres runiques restantes. Tel est mon serment.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8504;
UPDATE locales_npc_text SET entry = 8505, Text0_0_loc2 = null, Text0_1_loc2 = 'Bon, vous n’avez pas fait tout ce chemin juste pour papoter autour d’une tasse de thé. Voyons comment vous allez nous aider à nous sortir de cette situation.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8505;
