UPDATE locales_npc_text SET entry = 8507, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8507;
UPDATE locales_npc_text SET entry = 8513, Text0_0_loc2 = 'Le maître-artisan Omarion a tout simplement disparu une nuit. Comme un souffle dans le vent… Pouf ! Parti ! Bah, désormais, je suis le seul qui puisse fabriquer des choses pour les petits gars du front.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8513;
UPDATE locales_npc_text SET entry = 8515, Text0_0_loc2 = 'Les sin’dorei et les Réprouvés ont un objectif commun dans les Terres fantômes, $N. Nous triompherons du Fléau ensemble… Et ensuite, qui sait ? L’appui des Réprouvés s’avérera peut-être précieux dans notre quête de l’Outreterre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8515;
UPDATE locales_npc_text SET entry = 8516, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8516;
UPDATE locales_npc_text SET entry = 8517, Text0_0_loc2 = '<Tarsis gémit.>$B$BNous avons peut-être encore le temps… Écoutez-moi bien, $gmortel:mortelle;.$B$BJ\'ai cherché à travers le temps et le Néant. J\'ai passé des pactes avec des entités si viles et si abominables…$B$B<Tarsis tousse et crache du sang.>$B$BPensez-vous… pensez-vous qu\'Elune m\'ait abandonné, $R ? Mourir ici… de cette manière…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8517;
UPDATE locales_npc_text SET entry = 8518, Text0_0_loc2 = 'Ce que j\'ai fait… des actes horribles, tellement horribles…$B$B<Tarsis sanglote.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8518;
UPDATE locales_npc_text SET entry = 8519, Text0_0_loc2 = 'Je suis un bien-né, $R. Cela ne veut plus rien dire aujourd\'hui, mais il fut un temps où les bien-nés régnaient sur le monde. C\'était il y a dix mille ans…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8519;
UPDATE locales_npc_text SET entry = 8520, Text0_0_loc2 = 'Je veux le récupérer, $gmortel:mortelle;… Avec lui, les bien-nés pourraient renaître. Je pourrais tout faire revenir. Même la reine…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8520;
UPDATE locales_npc_text SET entry = 8521, Text0_0_loc2 = '<Tarsis s\'agrippe tant bien que mal à votre col. Il agonise, mais une puissance inimaginable brille encore dans ses yeux.>$B$BVraiment ? Alors écoutez-moi bien, car ce que je vais dire aura un sens, je vous le jure. Dans cette citadelle, vous trouverez l\'œuvre de ma vie : l\'emplacement exact de chaque fragment du bâton brisé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8521;
UPDATE locales_npc_text SET entry = 8522, Text0_0_loc2 = 'Atiesh, le grand bâton du Gardien. Le bâton transmis à travers les âges d\'un Gardien de Tirisfal à l\'autre. Le bâton corrompu par Sargeras. Le bâton du dernier Gardien. Le bâton de Medivh.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8522;
UPDATE locales_npc_text SET entry = 8523, Text0_0_loc2 = '<Les yeux de Tarsis s\'illuminent.>$B$BJe l\'ai tenu une fois… Je l\'ai tenu, en dépit des avertissements du Kirin Tor. Je l\'ai tenu, $gmortel:mortelle;… Un pouvoir sans limite. Le pouvoir de faire ce que je désirais, comme je le désirais. Il m\'a été repris aussitôt. Mais j\'y avais goûté… Et c\'était suffisant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8523;
UPDATE locales_npc_text SET entry = 8524, Text0_0_loc2 = '<Tarsis est grièvement blessé.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8524;
UPDATE locales_npc_text SET entry = 8526, Text0_0_loc2 = 'La bataille se passe bien. Les forces du Fléau semblent affaiblies. Je pense que ce n’est qu’une question de temps avant que nous les chassions de nos rivages. Pour vous aider dans ce combat, je mets ma magie à votre disposition.', Text0_1_loc2 = 'La bataille se passe bien. Les forces du Fléau semblent affaiblies. Je pense que ce n’est qu’une question de temps avant que nous les chassions de nos rivages. Pour vous aider dans ce combat, je mets ma magie à votre disposition.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8526;
UPDATE locales_npc_text SET entry = 8527, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8527;
UPDATE locales_npc_text SET entry = 8529, Text0_0_loc2 = 'Il a été perdu à la chute de la Citadelle pourpre… brisé en quarante-deux morceaux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8529;
UPDATE locales_npc_text SET entry = 8530, Text0_0_loc2 = 'Kel\'Thuzad déploie des efforts considérables pour reconstituer le bâton. Ses morceaux étaient dispersés aux quatre coins du monde, de Kalimdor aux mers du Sud. À un moment donné, j\'en détenais vingt... jusqu\'à ce qu\'ils me soient volés par les séides de Kel\'Thuzad. C\'est la raison de ma présence ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8530;
UPDATE locales_npc_text SET entry = 8531, Text0_0_loc2 = 'Vous ne m\'écoutez pas, on dirait… Non, si Kel\'Thuzad avait reconstitué le bâton, je ne pense pas que cette conversation serait possible. Kel\'Thuraz détient la tête d\'Atiesh. Ses serviteurs gardent quarante fragments. Quant au dernier morceau, son talon, il a été découvert par Brann Bronzebeard. La dernière fois que Brann a été vu, c\'était dans le désert de Silithus. Je le soupçonne de faire l\'impossible pour échapper aux infatigables traqueurs que Kel\'Thuzad a dépêché dans ce monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8531;
UPDATE locales_npc_text SET entry = 8534, Text0_0_loc2 = 'Nos Sanctum absorbent une partie de la puissance arcanique des lignes telluriques qui traversent nos terres de façon occulte.$B$BEn l’absence du puits de soleil, ils nous garantissent des sources d’énergie fiables et régulières.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8534;
UPDATE locales_npc_text SET entry = 8535, Text0_0_loc2 = 'Le fait que Brise-clémente tienne encore debout est en soi un monument à la ténacité de notre peuple. Jamais plus nous ne nous avouerons vaincus face au Fléau.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8535;
UPDATE locales_npc_text SET entry = 8536, Text0_0_loc2 = 'La planche, $gchien scrofuleux:chienne scrofuleuse; ! Revilgaz m\'a évité le gibet, mais mon châtiment est presque pire !', Text0_1_loc2 = 'La planche, $gchien scrofuleux:chienne scrofuleuse; ! Revilgaz m\'a évité le gibet, mais mon châtiment est presque pire !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8536;
UPDATE locales_npc_text SET entry = 8537, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous feriez bien d\'éviter de jouer les fauteurs de troubles, $N !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8537;
UPDATE locales_npc_text SET entry = 8538, Text0_0_loc2 = 'Que puis-je faire pour vous, $N ? Faites vite, je n’ai pas que ça à faire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8538;
UPDATE locales_npc_text SET entry = 8539, Text0_0_loc2 = 'Que puis-je faire pour vous ? En fait, non ! Que pouvez-vous faire pour moi ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8539;
UPDATE locales_npc_text SET entry = 8541, Text0_0_loc2 = 'Vous avez servi notre cause très loyalement, $N. Permettez-moi de vous remettre cette source infinie de notre quintessence élémentaire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8541;
UPDATE locales_npc_text SET entry = 8542, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8542;
UPDATE locales_npc_text SET entry = 8548, Text0_0_loc2 = 'Dar\'Khan croit que nous céderons sans combattre devant le Fléau. Faisons en sorte de corriger cette idée fausse, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8548;
UPDATE locales_npc_text SET entry = 8549, Text0_0_loc2 = 'Vous ne voyez pas que j\'essaye d\'avoir une conversation avec cette dame ?Oh, bon, elle a l\'air préoccupée, de toute façon.$B$BJe ne vends que les meilleurs crus, d\'origine locale et plus lointaine. Voulez-vous jeter un œil ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8549;
UPDATE locales_npc_text SET entry = 8550, Text0_0_loc2 = 'S\'agit-il d\'une liste en provenance du havre de Saltheril ? Naturellement, il faut à sa seigneurie l\'une de mes meilleures bouteilles. Et bien sûr, tout ami du seigneur Saltheril bénéficie d\'un prix avantageux pour ce fin millésime.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8550;
UPDATE locales_npc_text SET entry = 8551, Text0_0_loc2 = 'Nous avons gagné $2219W batailles contre le Fléau. Tenez-vous $gprêt:prête;, $N, car cette guerre est encore loin d’être terminée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8551;
UPDATE locales_npc_text SET entry = 8552, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous avez l\'argent, j\'ai la nourriture et les boissons qui satisferont votre appétit. Chaque pièce de cuivre contribue à maintenir la Retraite en parfait état.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8552;
UPDATE locales_npc_text SET entry = 8553, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est une liste du seigneur Saltheril que vous avez là ? Oui, je peux vous vendre une fournée de hors-d\'œuvre, tout juste ôtés du feu.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8553;
UPDATE locales_npc_text SET entry = 8555, Text0_0_loc2 = 'Nous avons gagné $2219W batailles contre le Fléau. Tenez bon, $N ! Nous ne devons pas faiblir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8555;
