UPDATE locales_npc_text SET entry = 8573, Text0_0_loc2 = 'Le roi-liche n\'a envoyé qu\'une force réduite contre chaque capitale d\'Azeroth. Le gros de ses forces attaque régulièrement les régions suivantes :$B$BAzshara, les Terres foudroyées, les Steppes ardentes, le désert de Tanaris, les Maleterres de l\'est et le Berceau-de-l\'hiver.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8573;
