UPDATE locales_npc_text SET entry = 8619, Text0_0_loc2 = 'C’est une joie de vous voir, $gfrère:sœur; ! Cet endroit me donne la chair de poule. Tant que vous êtes ici, n’hésitez pas à profiter de tout ce qu’offrent ces soi-disant Réprouvés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8619;
