UPDATE locales_npc_text SET entry = 8634, Text0_0_loc2 = 'Je m’occupe de votre confort. Réfrénez vos impulsions violentes, $R. Vous êtes chez vous dans mon auberge…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8634;
UPDATE locales_npc_text SET entry = 8638, Text0_0_loc2 = null, Text0_1_loc2 = 'Bonjour $C. Si vous ne l\'avez pas encore vue, vous devriez parler au pérégrin Solanna.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8638;
UPDATE locales_npc_text SET entry = 8642, Text0_0_loc2 = 'Si vous avez perdu une pièce de votre armure zandalarienne, je pourrai peut-être vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8642;
