UPDATE locales_npc_text SET entry = 8650, Text0_0_loc2 = 'Je vous suis très reconnaissant de votre aide, $c !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8650;
UPDATE locales_npc_text SET entry = 8653, Text0_0_loc2 = 'Ah, vous êtes $gcelui:celle; qui a trouvé la lettre. Merci pour ces... nouvelles, en quelque sorte, de ma famille. Prenez donc à manger et à boire.', Text0_1_loc2 = 'Ah, vous êtes $gcelui:celle; qui a trouvé la lettre. Merci pour ces... nouvelles, en quelque sorte, de ma famille. Prenez donc à manger et à boire.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8653;
UPDATE locales_npc_text SET entry = 8656, Text0_0_loc2 = 'Eh, faites la queue ! Ah la la… La nourriture d’Outreterre ne me réussit pas.', Text0_1_loc2 = 'Ah la la… Eh, faites la queue ! La nourriture d’Outreterre ne me réussit pas.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8656;
UPDATE locales_npc_text SET entry = 8658, Text0_0_loc2 = 'Je m\'appelle Sid. Ça fait du bien de voir de nouvelles têtes par ici. Que puis-je faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8658;
UPDATE locales_npc_text SET entry = 8659, Text0_0_loc2 = 'Même si nous sommes arrivés sur la terre promise, $N, il nous reste encore beaucoup de travail à faire. Notre peuple n\'atteindra pas Kael\'thas si nous nous lançons sans réfléchir dans la traversée de l\'Outreterre.$B$BNous devons trouver des routes sûres, accumuler des ressources et éviter ou détruire nos ennemis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8659;
UPDATE locales_npc_text SET entry = 8662, Text0_0_loc2 = 'Qu’est-ce que je ne donnerais pas pour avoir un cristal de soin, là, maintenant !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8662;
UPDATE locales_npc_text SET entry = 8663, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8663;
UPDATE locales_npc_text SET entry = 8664, Text0_0_loc2 = null, Text0_1_loc2 = 'Comment allons-nous nous sortir de là ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8664;
UPDATE locales_npc_text SET entry = 8665, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8665;
UPDATE locales_npc_text SET entry = 8666, Text0_0_loc2 = null, Text0_1_loc2 = 'Personne ne vous posera de questions sur vos actes, $C. Vous avez fait ce qu\'il fallait faire.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8666;
UPDATE locales_npc_text SET entry = 8667, Text0_0_loc2 = 'Quelle joie de vous voir en vie et en bonne santé, $N !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8667;
UPDATE locales_npc_text SET entry = 8669, Text0_0_loc2 = 'Merci beaucoup d\'avoir rechargé nos cristaux de soin. J\'ai entendu dire que vous rendiez service à un peu tout le monde. C\'est du bon travail, continuez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8669;
UPDATE locales_npc_text SET entry = 8672, Text0_0_loc2 = 'Mirren et moi comptons parmi les meilleurs chasseurs des deux côtés de la Porte des ténèbres. Restez dans le coin, et vous apprendrez peut-être un truc ou deux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8672;
UPDATE locales_npc_text SET entry = 8673, Text0_0_loc2 = 'J’espère qu’vous n’êtes pas ici pour notre bière. On ne partage pas ! Et on ne r’viendra pas là-d’ssus !$b$bCe n’est pas not\' faute si personne, à part nous, n’a eu la bonne idée d’apporter d’la bière dans l‘Outreterre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8673;
UPDATE locales_npc_text SET entry = 8674, Text0_0_loc2 = null, Text0_1_loc2 = 'Espérons que toute l’Outreterre n’est pas aussi aride que cette région. Cela nous change des vertes prairies et des forêts des Chants éternels.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8674;
UPDATE locales_npc_text SET entry = 8675, Text0_0_loc2 = 'Il existe dans l\'Outreterre des présences démoniaques bien plus puissantes que toutes celles que j\'ai pu voir dans l\'ancien monde.$B$BUn grand pouvoir sera à la disposition de ceux qui oseront s\'en approcher, pour apprendre à exploiter et maîtriser leur force.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8675;
UPDATE locales_npc_text SET entry = 8676, Text0_0_loc2 = 'Je suis né ici, $r. C’est mon monde… et c’est le seul que je connaisse. Quand j’étais petit, mon père et ma mère me régalaient d’histoires de votre monde et de ses splendeurs. Oh, comme j’ai rêvé de quitter cet endroit...$B$BHélas, le destin est un maître bien cruel. Mon père et ma mère ont disparu depuis longtemps, et sont enterrés dans ce cimetière même. Je reste ici pour défendre leur honneur et celui de ma famille : les Fils de Lothar.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8676;
UPDATE locales_npc_text SET entry = 8678, Text0_0_loc2 = 'Comment puis-je vous aider ?', Text0_1_loc2 = 'Comment puis-je vous aider ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8678;
UPDATE locales_npc_text SET entry = 8680, Text0_0_loc2 = 'L\'Outreterre regorge d\'énergie magique, elle imprègne même l\'air que nous respirons. Vous ne la sentez pas, $N ? Je ne peux pas imaginer un endroit mieux adapté à notre race. Bientôt, les sin\'dorei auront une nouvelle terre dont ils pourront faire leur patrie !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8680;
UPDATE locales_npc_text SET entry = 8681, Text0_0_loc2 = null, Text0_1_loc2 = 'Mon époux a disparu depuis des jours. Nous avons entrepris ce pèlerinage ensemble, et nous étions supposés le terminer ensemble.$B$BToute cette inquiétude m\'a fait utiliser les cristaux de mana un peu plus souvent que je ne l\'aurais dû.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8681;
UPDATE locales_npc_text SET entry = 8682, Text0_0_loc2 = null, Text0_1_loc2 = 'Si on les élève correctement, les faucons-dragons s’apprivoisent très bien. C’est fascinant, n’est-ce pas ? Ils sont si amicaux et agréables quand on les éduque bien, et si violents et dangereux à l’état sauvage… Je les aime tant…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8682;
UPDATE locales_npc_text SET entry = 8683, Text0_0_loc2 = 'Ne… vous en faites pas… pour moi…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8683;
UPDATE locales_npc_text SET entry = 8684, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne pense pas que notre situation soit si mauvaise que cela. Je suis certaine que tout sera bientôt réparé et que le pouvoir du puits de soleil sera restauré. Il est impossible que la situation soit aussi mauvaise que tout le monde se complaît à le dire…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8684;
UPDATE locales_npc_text SET entry = 8685, Text0_0_loc2 = 'Se lier d\'amitié avec les Hauts-Soleils a été rentable. Je peux mener des recherches sur des sujets qui n\'existent qu\'en Outreterre, et ils profitent… de ma personnalité séduisante et de mon charme viril.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8685;
UPDATE locales_npc_text SET entry = 8688, Text0_0_loc2 = 'Je n’étais qu’un enfant quand mon père a été envoyé sur cette terre aride. Tout ce que je sais de lui vient de ce que les autres m’ont raconté.$B$BVos rêves changent-ils, $N ? Pas les miens. Je fais un rêve : un horizon pourpre m’entoure alors que les forces de la Légion, innombrables, combattent dans le lointain. Je m’agenouille devant le corps d’un homme, sans doute celui de mon père, et je pleure. Il halète, le corps brisé, et me murmure quelque chose. Mais en dépit de tous mes efforts, je ne parviens pas à comprendre ce qu’il tente de me dire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8688;
UPDATE locales_npc_text SET entry = 8698, Text0_0_loc2 = 'Vous pensez que je suis cruel envers cette larve ?$B$BIls ont tué deux de mes hommes ce mois-ci ! On est trop gentil envers ces vermines de Déshérités.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8698;
UPDATE locales_npc_text SET entry = 8699, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à Brise-clémente. Lune-d’argent nous a pratiquement abandonnés, mais nous refusons de partir. Notre foyer est ici.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8699;
UPDATE locales_npc_text SET entry = 8700, Text0_0_loc2 = null, Text0_1_loc2 = 'Mon travail se résume à une tâche et une seule : m’assurer que toute se passe bien au sanctum occidental.$B$BVous devez excuser mon mauvais caractère, mais comme vous le voyez, la situation ici nous échappe un peu…', Text1_0_loc2 = null, Text1_1_loc2 = 'Cela pourrait-il être l\'œuvre de saboteurs elfes de la nuit ? J\'en doute. Le sanctum occidental était tellement surchargé qu\'une catastrophe n\'était qu\'une question de temps.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8700;
UPDATE locales_npc_text SET entry = 8701, Text0_0_loc2 = null, Text0_1_loc2 = 'Depuis que l’empereur a été tué, la protection magique qui empêche la téléportation à l’intérieur du temple s’est grandement affaiblie. Si vous le désirez, je peux vous transposer dans le repaire de l’empereur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8701;
UPDATE locales_npc_text SET entry = 8702, Text0_0_loc2 = null, Text0_1_loc2 = 'Maintenant qu\'Ouro est mort, la route de la dernière salle est dégagée. Je peux vous y envoyer, si vous le désirez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8702;
UPDATE locales_npc_text SET entry = 8703, Text0_0_loc2 = 'Vous appréciez la fête, $c ?$B$BLes gardiens des flammes s’occupent des feux du présent. Quant à moi, je suis une sorte d’historien. Je conserve la mémoire des fêtes passées. Bien sûr, je transmettrai le souvenir de celle de cette année. Pour l’instant, tout se passe à merveille, n’est-ce pas ?$B$BVous savez, $N, tous les feux qui brûlent pendant cette fête ont un certain pouvoir. Nous avons les nôtres, mais je suis certain que des flammes sacrées brûlent aussi au cœur des capitales ennemies…', Text0_1_loc2 = 'Vous appréciez la fête, $c ?$B$BLes gardiens des flammes s’occupent des feux du présent. Quant à moi, je suis une sorte d’historienne. Je conserve la mémoire des fêtes passées. Bien sûr, je transmettrai le souvenir de celle de cette année. Pour l’instant, tout se passe à merveille, n’est-ce pas ?$B$BVous savez, $N, tous les feux qui brûlent pendant cette fête ont un certain pouvoir. Nous avons les nôtres, mais je suis certaine que des flammes sacrées brûlent aussi au cœur des capitales ennemies…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8703;
UPDATE locales_npc_text SET entry = 8712, Text0_0_loc2 = 'Si vous cherchez du boulot, $c, attrapez une pioche et commencez à creuser. Dans le coin, y\'a des ruines d\'un bout à l\'autre de l\'horizon !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8712;
UPDATE locales_npc_text SET entry = 8713, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutation, $N. Mes amis les Fils de Lothar et moi sommes coincés sur ce monde misérable depuis près de vingt ans, maintenant. Nous avons tenu du mieux que nous le pouvions, mais cela m’étonne encore de constater que la menace que nous affrontons aujourd’hui n’est que l’ombre de nos échecs passés.$B$BVoudriez-vous nous aider à les expier ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8713;
UPDATE locales_npc_text SET entry = 8715, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'étendue de la corruption en Outreterre est vraiment stupéfiante, $c. Le Cercle cénarien cherche à comprendre comment la faune de ces territoires a pu survivre. L’étude de ces terres fracassées et torturées et de leurs habitants nous apprendra peut-être à guérir les régions malades de notre propre monde.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8715;
UPDATE locales_npc_text SET entry = 8716, Text0_0_loc2 = 'Voir ce qu\'il est advenu de cette terre m\'attriste. L\'Expédition cénarienne fera ce qui est en son pouvoir pour comprendre et réparer les dégâts subis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8716;
UPDATE locales_npc_text SET entry = 8717, Text0_0_loc2 = 'Vous avez l\'air perdu, $C. Ne me poussez pas à vous mettre au travail sur les murs avec les autres péons.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8717;
UPDATE locales_npc_text SET entry = 8718, Text0_0_loc2 = null, Text0_1_loc2 = 'Heureuse rencontre, $C. Votre arrivée à Thrallmar ne pouvait mieux tomber. Le chef de guerre compte sur nous, dans la mesure où il ne peut être ici en personne.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8718;
UPDATE locales_npc_text SET entry = 8719, Text0_0_loc2 = 'Thrallmar a besoin de vous, $C. Le chef de guerre a besoin de vous. Soyez $gprêt:prête; à faire votre devoir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8719;
UPDATE locales_npc_text SET entry = 8724, Text0_0_loc2 = 'On ne gagne que peu d’engagements par la force brute. C’est la ruse, les reflexes et la connaissance de ses armes qui déterminent qui finit par l’emporter.', Text0_1_loc2 = 'On ne gagne que peu d’engagements par la force brute. C’est la ruse, les reflexes et la connaissance de ses armes qui déterminent qui finit par l’emporter.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8724;
UPDATE locales_npc_text SET entry = 8733, Text0_0_loc2 = 'La fortune nous sourit, $N, car voici $gun autre héros:une autre héroïne;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8733;
UPDATE locales_npc_text SET entry = 8740, Text0_0_loc2 = 'J’ai entendu parler de vos exploits, $N. Tant que vous serez parmi les Réprouvés, je ne doute pas vous vous comporterez avec la dignité qui convient à $gun vrai héros:une véritable héroïne; des sin’dorei !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8740;
UPDATE locales_npc_text SET entry = 8750, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous souhaite la bienvenue, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8750;
UPDATE locales_npc_text SET entry = 8751, Text0_0_loc2 = 'Je n\'ai pas le temps de bavarder, $C. Je dois m\'occuper des pèlerins et m\'assurer qu\'ils sont prêts pour le long voyage qui les attend.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8751;
UPDATE locales_npc_text SET entry = 8752, Text0_0_loc2 = 'Bienvenue au Guet de l’épervier, $c. Je m’occupe des pèlerins et je les aide à continuer leur long voyage. Si vous n’avez pas encore entrepris le pèlerinage, je peux vous communiquer les informations dont vous aurez besoin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8752;
UPDATE locales_npc_text SET entry = 8754, Text0_0_loc2 = null, Text0_1_loc2 = 'Marchez toujours dans la Lumière, $N.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8754;
UPDATE locales_npc_text SET entry = 8755, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à la Retraite des pérégrins. Nous sommes des forestiers, et nous avons fait le serment de défendre notre peuple contre ses ennemis, d’où qu’ils viennent.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8755;
UPDATE locales_npc_text SET entry = 8756, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8756;
UPDATE locales_npc_text SET entry = 8759, Text0_0_loc2 = 'Oh… Je vous connais ?', Text0_1_loc2 = 'Oh… Je vous connais ?', Text1_0_loc2 = 'Désolé, je ne parle pas au petit personnel.', Text1_1_loc2 = 'Désolée, je ne parle pas au petit personnel.', Text2_0_loc2 = 'Et vous êtes ?', Text2_1_loc2 = 'Et vous êtes ?', Text3_0_loc2 = 'J\'ai l\'impression que vous me prenez pour quelqu\'un d\'autre, quelqu\'un qui aurait envie de vous parler.', Text3_1_loc2 = 'J\'ai l\'impression que vous me prenez pour quelqu\'un d\'autre, quelqu\'un qui aurait envie de vous parler.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8759;
UPDATE locales_npc_text SET entry = 8760, Text0_0_loc2 = 'Salut $N ! La fête vous plaît ?', Text0_1_loc2 = 'Salut $N ! La fête vous plaît ?', Text1_0_loc2 = 'Ohé tout le monde, c\'est $N !', Text1_1_loc2 = 'Ohé tout le monde, c\'est $N !', Text2_0_loc2 = 'Les nouvelles vont vite dans notre petit milieu. Merci d\'avoir regarni nos réserves !', Text2_1_loc2 = 'Les nouvelles vont vite dans notre petit milieu. Merci d\'avoir regarni nos réserves !', Text3_0_loc2 = 'Cette fête est fantastique, n\'est-ce pas ?', Text3_1_loc2 = 'Cette fête est fantastique, n\'est-ce pas ?', Text4_0_loc2 = 'Vous devriez essayer les hors-d\'œuvre preste-patte, ils sont à mourir !', Text4_1_loc2 = 'Vous devriez essayer les hors-d\'œuvre preste-patte, ils sont à mourir !', Text5_0_loc2 = 'Hum… en définitive, cette année, la cuvée spéciale de Solegrâce n\'a pas grand-chose de spécial. Qu\'en pensez-vous, $N ?', Text5_1_loc2 = 'Hum… en définitive, cette année, la cuvée spéciale de Solegrâce n\'a pas grand-chose de spécial. Qu\'en pensez-vous, $N ?', Text6_0_loc2 = 'Il paraît que nous allons visiter la frontière ce soir afin d\'observer une authentique bataille avec le Fléau. C\'est délicieusement imprudent !', Text6_1_loc2 = 'Il paraît que nous allons visiter la frontière ce soir afin d\'observer une authentique bataille avec le Fléau. C\'est délicieusement imprudent !', Text7_0_loc2 = 'Cette Elisara Heurtesoleil fait preuve d\'un toupet ! Elle s\'imagine qu\'elle peut attirer l\'attention du seigneur Saltheril. Vous avez vu ce qu\'elle porte ? Elle est en retard d\'au moins une mode. C\'est triste, vraiment triste.', Text7_1_loc2 = 'Cette Elisara Heurtesoleil fait preuve d\'un toupet ! Elle s\'imagine qu\'elle peut attirer l\'attention du seigneur Saltheril. Vous avez vu ce qu\'elle porte ? Elle est en retard d\'au moins une mode. C\'est triste, vraiment triste.' WHERE entry = 8760;
UPDATE locales_npc_text SET entry = 8761, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8761;
UPDATE locales_npc_text SET entry = 8767, Text0_0_loc2 = 'J’ai réussi à me mettre dans une posture plutôt délicate. La prophétesse Regulkut a essayé de me prévenir, mais ma négligence m’a finalement rattrapé.$B$BJe suis venu ici pour étudier les ruines de Zeth’Gor, mais je suis tombé sur la Légion en chemin. Et tout ce que j’y ai gagné, c’est une vilaine coupure à la jambe.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8767;
UPDATE locales_npc_text SET entry = 8768, Text0_0_loc2 = 'Je ne peux pas vous former, jeune $c, mais je vous offre ma bénédiction. Allez vaillamment dans la Lumière !$B$BY a-t-il autre chose ?', Text0_1_loc2 = 'Je ne peux pas vous former, jeune $c, mais je vous offre ma bénédiction. Allez vaillamment dans la Lumière !$B$BY a-t-il autre chose ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8768;
UPDATE locales_npc_text SET entry = 8769, Text0_0_loc2 = null, Text0_1_loc2 = 'Il est très important que nous découvrions ce que préparent les elfes de la nuit, $c ! Concentrez tous vos efforts dans ce but.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8769;
UPDATE locales_npc_text SET entry = 8770, Text0_0_loc2 = 'Il n’y a pas si longtemps que cela, cet endroit fut le site d’une terrible bataille. Les troupes de Rempart-du-Néant et de l’Aube d’argent retenaient les vagues de démons qui surgissaient implacablement de la Porte des ténèbres.$B$BCe furent des jours sombres, teintés de sang et de désespoir…$B$BMais les royaumes d’Azeroth nous ont rapidement rejoints et nous avons repoussé les monstres ! Les combats continuent de l’autre côté de la Porte…$B$BCe camp est calme, maintenant, mais je crains que ce calme ne donne une fausse idée de la démence qui règne de l’autre côté de ce portail infernal.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8770;
UPDATE locales_npc_text SET entry = 8771, Text0_0_loc2 = 'L’héroïsme de la lignée de Trollemort est légendaire. Je vénère cet homme.$B$BLe trouver en vie, ici, dans l’Outreterre…$B$B<Le regard de Romus se perd dans le lointain.>$B$BLorsque je l’ai vu pour la première fois, j’ai failli m’évanouir. Ce fut un très grand choc. Lorsque je me suis agenouillé devant lui, je luttais pour ne pas pleurer.$B$BJe vais tout faire pour que les Fils de Lothar s’en sortent. Tout. Je vous le garantis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8771;
