UPDATE locales_npc_text SET entry = 8802, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, je suis le redresseur de torts Palanaar, un paladin errant de la Lumière. Je suis ici pour aider les elfes de la nuit à faire face aux problèmes qu\'ils rencontrent en ces terres.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8802;
