UPDATE locales_npc_text SET entry = 8822, Text0_0_loc2 = null, Text0_1_loc2 = 'Je n’ai plus de travail pour vous, $N. Mais je veux que vous sachiez que le partage de ce lutjan rouge était un événement qui restera toujours dans ma mémoire...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8822;
UPDATE locales_npc_text SET entry = 8823, Text0_0_loc2 = 'Bien sûr que j’ai un travail pour vous ! Nous manquons constamment de chasseurs. J’espère que vous vous en sortirez mieux que ces draeneï que nous avons dû rapatrier à l’Exodar... pour leur enterrement.$B$B<Actéon éclate de rire.>$B$BJe plaisante ! Vous auriez dû voir votre tête. On les a renvoyés à l’Exodar pour traiter quelques blessures légères. Rien dont ils ne pourront se remettre...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8823;
