UPDATE locales_npc_text SET entry = 8830, Text0_0_loc2 = 'Et pourquoi devrais-je vous parler, $r ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8830;
UPDATE locales_npc_text SET entry = 8832, Text0_0_loc2 = 'Regardez autour de vous, chaman. Ne voyez-vous pas l’équilibre des éléments dans la terre, l’air, l’eau, et le feu qui les a tous marqués ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8832;
UPDATE locales_npc_text SET entry = 8834, Text0_0_loc2 = 'Ne vous inquiétez pas... même si mes griffons ne sont pas complètement vivants, ils vous porteront sans problème jusqu\'à votre destination. Dites-moi, quelle tour cherchez-vous ?', Text0_1_loc2 = 'Ne vous inquiétez pas... même si mes griffons ne sont pas complètement vivants, ils vous porteront sans problème jusqu\'à votre destination. Dites-moi, quelle tour cherchez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8834;
UPDATE locales_npc_text SET entry = 8835, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous n\'avez pas encore appris à vous servir de bandages, je me ferai un plaisir de vous enseigner les bases de leur création et leur application. La différence entre la vie et la mort se résume bien souvent à des plaies correctement pansées.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8835;
UPDATE locales_npc_text SET entry = 8836, Text0_0_loc2 = 'J\'aurai ma revanche !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8836;
UPDATE locales_npc_text SET entry = 8838, Text0_0_loc2 = 'Je suis très occupé pour le moment, jeune $c. Je vous prie de m\'excuser.', Text0_1_loc2 = 'Je suis très occupée pour le moment, jeune $c. Je vous prie de m\'excuser.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8838;
UPDATE locales_npc_text SET entry = 8840, Text0_0_loc2 = 'Salutations, $c. J’ai été dépêché de l’Exodar pour superviser la construction de Guet d’azur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8840;
UPDATE locales_npc_text SET entry = 8844, Text0_0_loc2 = 'Un alchimiste peut faire des miracles avec de simples herbes, en les transformant en potions stables dont les effets peuvent varier de la guérison des plaies à la respiration sous-marine, avec toutes sortes d’intermédiaires.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8844;
UPDATE locales_npc_text SET entry = 8846, Text0_0_loc2 = 'Salut. Qu’est-ce qui vous amène au camp du Noroît aujourd’hui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8846;
UPDATE locales_npc_text SET entry = 8847, Text0_0_loc2 = 'Je vous souhaite le bonjour, $c.$B$BJ’attends un message de l’un des conseillers du haut-thane à propos d’une audience. J’ai dû batailler ferme pour avoir l’occasion de présenter mes lettres de créance.$B$BOn dit que les Marteaux-hardis « se méfient des étrangers ». Croyez-moi, c’est un euphémisme ! Enfin, les choses s’arrangeront lorsque j’aurai eu l’occasion de rencontrer Barbe-de-plumes. Il paraît qu’il est partisan de liens plus étroits avec l’Alliance.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8847;
UPDATE locales_npc_text SET entry = 8848, Text0_0_loc2 = null, Text0_1_loc2 = 'Qu\'est-ce que vous voulez ?$B$BC\'est un peu grossier de foncer sur les étrangers sans raison, non ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8848;
UPDATE locales_npc_text SET entry = 8849, Text0_0_loc2 = 'En avant toutes, moussaillon ! On m’appelle l’Cuistot parce que la cuistance, c’est mon boulot. Nous aut’ les McFadesauce sommes cuisiniers et marchands de tout ce qui peut réjouir les sens délicats des gourmets des deux sexes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8849;
UPDATE locales_npc_text SET entry = 8850, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous avez besoin de quelque chose ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8850;
UPDATE locales_npc_text SET entry = 8851, Text0_0_loc2 = 'Que la Lumière soit avec vous, $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8851;
UPDATE locales_npc_text SET entry = 8852, Text0_0_loc2 = null, Text0_1_loc2 = 'On dirait que de l’aide vient enfin d’arriver ! Les Omenaï vous souhaitent la bienvenue au camp des vestiges, $N.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8852;
UPDATE locales_npc_text SET entry = 8853, Text0_0_loc2 = 'Ikan essaye, encore et encore. Je pense que ces Crapuches ont beaucoup en commun avec les Perdus, comme... eh bien, d\'être perdus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8853;
UPDATE locales_npc_text SET entry = 8854, Text0_0_loc2 = 'Il faudra du temps, mais ces Roués apprendront les voies de la Lumière !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8854;
UPDATE locales_npc_text SET entry = 8855, Text0_0_loc2 = 'Bienvenue, $N. Je me présente : Landro Tirauloin, responsable de la Flamme noire. Vous n\'avez peut-être jamais entendu parler de cette organisation à ce jour. Cela n\'est pas surprenant : nous nous efforçons qu\'il en soit ainsi. Nous préférons éviter les... engagements politiques.$B$BLa Flamme noire a pour spécialité de proposer les meilleures commodités et divertissements disponibles sur le marché, qu\'il s\'agisse d\'objets introuvables, de combats de gladiateurs ou de lucratifs jeux de hasard.$B$BRecherchez-vous quelque chose en particulier ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8855;
UPDATE locales_npc_text SET entry = 8856, Text0_0_loc2 = 'Curiosités et raretés : nous sommes spécialisés dans la vente des objets les plus singuliers d\'Azeroth aux aventuriers les plus éclairés.$B$BSi vous venez me voir, c\'est que vous devez déjà avoir une idée de ce que vous recherchez. Dites-moi, $N, quel est l\'objet qui vous intéresserait ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8856;
UPDATE locales_npc_text SET entry = 8858, Text0_0_loc2 = null, Text0_1_loc2 = 'Avez-vous besoin de quelque chose, $c ? Je peux peut-être vous aider.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8858;
UPDATE locales_npc_text SET entry = 8860, Text0_0_loc2 = 'Nous ne reprendrons pas les Terres fantômes par la seule force des armes, $N. C\'est là que les magistères entrent en jeu.$B$BNul ne peut égaler notre ruse et notre connaissance des arcanes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8860;
UPDATE locales_npc_text SET entry = 8861, Text0_0_loc2 = null, Text0_1_loc2 = '<La prêtresse Il\'dinare vous observe avec méfiance.>', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8861;
UPDATE locales_npc_text SET entry = 8862, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis une elfe de la nuit, $gétranger:étrangère;. Vous ne savez peut-être rien de mon peuple, mais nous en savons beaucoup sur vous et vos ancêtres. Vous avez beau ne pas vous appeler érédar, votre apparence est riche en similitudes.$B$BVous avez aidé mes alliés et je vous en suis reconnaissante, mais pour le moment vous progressez sur un bien précaire chemin. Vous parviendrez peut-être avec le temps à dissiper les doutes et les peurs qui sont encore en mon sein.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8862;
UPDATE locales_npc_text SET entry = 8863, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $r. Vos nobles actions ne seront pas oubliées. Puisse Elune veiller sur vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8863;
UPDATE locales_npc_text SET entry = 8864, Text0_0_loc2 = 'Restez sur vos gardes dans le coin, $gétranger:étrangère;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8864;
UPDATE locales_npc_text SET entry = 8865, Text0_0_loc2 = 'Vous nous avez beaucoup aidés jusqu\'ici, $N. Vous feriez bien de donner un coup de main aux autres dans le camp.$B$BEt ne vous souciez plus de ces gobelins, je m\'occupe de tout.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8865;
UPDATE locales_npc_text SET entry = 8866, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est bon de vous revoir, $N. Lorsque les choses se seront un peu calmées, je vous parlerai de l\'inestimable trésor que nous avons perdu...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8866;
UPDATE locales_npc_text SET entry = 8867, Text0_0_loc2 = 'Qu’est-ce que vous regardez ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8867;
UPDATE locales_npc_text SET entry = 8868, Text0_0_loc2 = 'Je suis Adamant Cœur-de-fer, de la grande cité de Forgefer. Comme vous pouvez sans doute le deviner en regardant mon uniforme, j’ai fait partie de la garde d’élite de Forgefer. J’ai été le bras droit de Magni, parfaitement ! Du moins jusqu’à ce que je prenne ma retraite. Maintenant, je pratique l’archéologie en amateur. C’est un passe-temps, et je m’étais dit que c’était un bon moyen de parcourir le monde !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8868;
UPDATE locales_npc_text SET entry = 8869, Text0_0_loc2 = '<Cowlen sanglote.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8869;
UPDATE locales_npc_text SET entry = 8870, Text0_0_loc2 = 'Qu\'ai-je fait aux dieux pour qu\'ils me punissent aussi férocement ? Tout ce que je chérissais a disparu, est mort ou a été détruit. Ma famille m\'a été prise par les brutes de la forêt. Ma maison a été rasée. Mon bateau, coulé.$B$BJe suis un homme brisé, naufragé sur une île ravagée.$B$BÊtes-vous là pour m\'achever ? Tuez-moi vite, je ne résisterai pas. Prenez ma vie, $gétranger:étrangère;, je suis trop lâche pour le faire moi-même. Ma main tremble… la lame glisserait.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8870;
UPDATE locales_npc_text SET entry = 8871, Text0_0_loc2 = 'Si ce ne sont pas les dieux, qui ? Comment ? D\'où vient ce drame ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8871;
UPDATE locales_npc_text SET entry = 8872, Text0_0_loc2 = 'La Légion ? Les démons ? Ils vous traquent ? Mon peuple a aussi été chassé par la légion. Ses maîtres ténébreux avaient décidé de notre damnation.$B$B<Cowlen essuie ses larmes.>$B$BC\'est pour cette raison que j\'ai installé ma famille ici, il y a si longtemps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8872;
UPDATE locales_npc_text SET entry = 8873, Text0_0_loc2 = 'Je vous salue, $c. Avant que les elfes de la nuit n’invitent les draeneï à les rejoindre, cette clairière était pratiquement oubliée.$B$BLes kaldorei sont intimement liés à cette terre, et nous autres draeneï sommes honorés d’avoir été invités à les aider à guérir le Chant des forêts.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8873;
UPDATE locales_npc_text SET entry = 8874, Text0_0_loc2 = null, Text0_1_loc2 = 'C’est bon de voir un nouveau visage. Notre avant-poste est petit et nos ennemis nombreux. Nous sommes environnés de créatures qui cherchent à nous détruire : les satyres à l’ouest, les orcs du camp de bûcherons Chanteguerre au sud, et les démons de la colline Gangrefeu.$B$BNous sommes reconnaissants aux draeneï d’avoir accepté de nous rejoindre et de nous aider à fortifier le Chant des forêts contre toutes ces menaces.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8874;
UPDATE locales_npc_text SET entry = 8875, Text0_0_loc2 = 'Cela fait du bien d’entendre de nouveaux des voix d’autres personnes dans le Chant des forêts. Je commençais à me dire que je serai le dernier des kaldorei à poser les yeux sur ces terres avant qu’elles ne sombrent dans la corruption.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8875;
UPDATE locales_npc_text SET entry = 8876, Text0_0_loc2 = 'Sentez-vous le mal, au sud ? Par la Main, il doit être purifié !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8876;
UPDATE locales_npc_text SET entry = 8878, Text0_0_loc2 = 'Vous avez porté un rude coup au mal démoniaque qui vient du sud, mais cette région reste troublée, je le sens.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8878;
UPDATE locales_npc_text SET entry = 8880, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8880;
UPDATE locales_npc_text SET entry = 8881, Text0_0_loc2 = 'Vous êtes $r, n’est-ce pas ? Cela me remonte le moral de vous voir ! Toute aide est plus que bienvenue.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8881;
UPDATE locales_npc_text SET entry = 8883, Text0_0_loc2 = null, Text0_1_loc2 = 'Il y a des démons à tuer, $c. Avez-vous assez de courage pour faire ce qui doit être fait ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8883;
UPDATE locales_npc_text SET entry = 8887, Text0_0_loc2 = null, Text0_1_loc2 = 'Après avoir perdu une bonne partie de ses démons, la Légion y réfléchira à deux fois avant d\'attaquer à nouveau notre camp de bûcherons. Malgré tout, je sens comme l\'éveil d\'un mal démoniaque.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8887;
UPDATE locales_npc_text SET entry = 8892, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8892;
UPDATE locales_npc_text SET entry = 8893, Text0_0_loc2 = null, Text0_1_loc2 = 'Hourra pour le plus grand des héros !', Text1_0_loc2 = null, Text1_1_loc2 = 'On racontera l’histoire de votre voyage pendant des années !', Text2_0_loc2 = null, Text2_1_loc2 = 'J’ai enfin une histoire à raconter à mes petits-enfants... Un traître au royaume vivait parmi nous !', Text3_0_loc2 = null, Text3_1_loc2 = 'À dire vrai, je n’ai jamais aimé ce gnome...', Text4_0_loc2 = null, Text4_1_loc2 = 'Nous avons de la chance d’avoir survécu à cette épreuve. Sans vous, toute la population de l’île aurait été tuée !', Text5_0_loc2 = null, Text5_1_loc2 = 'Je me demande combien vivent encore parmi nous. Je connaissais l\'Étincelle depuis des années, et jamais je n’aurais pu imaginer qu’il était un traître.', Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8893;
UPDATE locales_npc_text SET entry = 8934, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8934;
UPDATE locales_npc_text SET entry = 8939, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue au Chant des forêts, $c. Si je peux vous aider d\'une façon ou d\'une autre, n\'hésitez pas à me le faire savoir.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8939;
UPDATE locales_npc_text SET entry = 8952, Text0_0_loc2 = '<Le fantassin se tient très droit, l\'air résolu.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8952;
UPDATE locales_npc_text SET entry = 8953, Text0_0_loc2 = 'Alors même que nous tentons d’obtenir l’aide des peuples de ce monde contre la Légion ardente, nous devons aussi apprendre à vivre en paix parmi eux.$B$BGardez ceci à l’esprit pendant vos périples en Azeroth. Que la Lumière vous guide.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8953;
UPDATE locales_npc_text SET entry = 8954, Text0_0_loc2 = 'Qu\'est-ce qui vous amène si haut, $gpetit:petite:r; $r ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8954;
UPDATE locales_npc_text SET entry = 8956, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8956;
UPDATE locales_npc_text SET entry = 8958, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8958;
UPDATE locales_npc_text SET entry = 8959, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous attendais.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8959;
UPDATE locales_npc_text SET entry = 8960, Text0_0_loc2 = 'Laissez-moi tranquille. Je n\'ai rien pour les individus de votre espèce.', Text0_1_loc2 = 'Laissez-moi tranquille. Je n\'ai rien pour les individus de votre espèce.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8960;
UPDATE locales_npc_text SET entry = 8961, Text0_0_loc2 = 'Ben alors, $Tminus:fillette; ? Tu veux ta môman ? C\'est bête, hein ? Peut-être que tu devrais aller faire un tour au mess des officiers de Thrallmar pour aller la chercher ?', Text0_1_loc2 = null, Text1_0_loc2 = 'Tu sais où tu te trouves ? Au bastion de l\'Honneur, mon chou ! Tu l\'aimes, ou tu le quittes !', Text1_1_loc2 = null, Text2_0_loc2 = 'Ce n\'est pas une partie de campagne, vermine ! Tu es en Outreterre, maintenant ! Allez, secoue-toi, ou on va t\'offrir un billet de retour à travers le portail.', Text2_1_loc2 = null, Text3_0_loc2 = 'Je n’ai ni le temps, ni l’envie, de t’expliquer la vie, salopard ! Bon, tu as quelque chose d’important à me dire, ou tu es juste $gvenu:venue; m’inviter à boire un verre, ma mignonne ?', Text3_1_loc2 = null, Text4_0_loc2 = 'Quel est ton défaut principal ?', Text4_1_loc2 = null, Text5_0_loc2 = 'Je t’ai $gvu:vue; en train de gambader dans le coin. Et si tu nous faisais une faveur à tous, et que tu allais restituer ton équipement à l’armurerie ?', Text5_1_loc2 = null, Text6_0_loc2 = 'Au nom de Kurdran, mais qui donc t’a $glaissé:laissée; franchir la Porte des ténèbres ?', Text6_1_loc2 = null, Text7_0_loc2 = 'Eh bien, eh bien, eh bien… Qu’est-ce que nous avons là ? Un $T civil:civil; ? C’est-y pas mignon… T’auras aucun traitement de faveur de ma part !', Text7_1_loc2 = null WHERE entry = 8961;
UPDATE locales_npc_text SET entry = 8962, Text0_0_loc2 = 'Parlez moins fort, $c !', Text0_1_loc2 = null, Text1_0_loc2 = 'Si tu as quelque chose à dire, parle !', Text1_1_loc2 = null, Text2_0_loc2 = 'Vous êtes un $Tcivil:civil; ?', Text2_1_loc2 = null, Text3_0_loc2 = 'Je suis occupé, dépêchez-vous !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 8962;
