UPDATE locales_npc_text SET entry = 9023, Text0_0_loc2 = 'Bienvenue à Thrallmar, $N ! À mesure que vous nous aiderez dans notre cause, nous vous donnerons accès à davantage de matériel.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9023;
UPDATE locales_npc_text SET entry = 9024, Text0_0_loc2 = 'Voulez-vous que je m\'occupe de vos animaux familiers ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9024;
UPDATE locales_npc_text SET entry = 9025, Text0_0_loc2 = 'Sur Argus, Mac’Aree était la plus sainte de nos cités.$B$BMe croirez-vous si je vous dis que ses trottoirs étaient pavés de métaux précieux ? Que ses rivières étincelaient même dans les ténèbres ?$B$BComme cette époque me manque… Combien de temps s’est écoulé depuis ? Mille ans ? Dix mille ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9025;
UPDATE locales_npc_text SET entry = 9028, Text0_0_loc2 = 'Cherchez dans les eaux qui traversent cette île, mais méfiez-vous, le frappeur satyre puant vit dans le fleuve.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9028;
UPDATE locales_npc_text SET entry = 9029, Text0_0_loc2 = 'Bienvenue au Guet du sang, $c. Au cours de mes voyages, j’ai récupéré des outils, des artéfacts et des connaissances auprès des différents peuples que j’ai rencontrés.$B$BSi vous avez besoin de mon savoir, n’hésitez pas !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9029;
UPDATE locales_npc_text SET entry = 9030, Text0_0_loc2 = 'Comment son nom l’indique, près des ruines. Celles de Loreth’Aran, dans ce cas.$B$BEn fait, son nom « n’indique » rien du tout, j’essayais juste de lui trouver un joli nom.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9030;
UPDATE locales_npc_text SET entry = 9031, Text0_0_loc2 = 'Les collybies gangrenées doivent leur nom à la magie qui les a corrompues. Vous pourrez en trouver à Nazzivian, le camp des satyres, à l\'ouest.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9031;
UPDATE locales_npc_text SET entry = 9032, Text0_0_loc2 = 'Les champignons de sang sont plus courants que les autres variétés de champignons. On peut en trouver dans les bois, un peu partout sur l’île de Brume-sang. Si vous tenez à votre vie, je vous conseille de rester au sud de la route qui coupe Brume-sang en deux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9032;
UPDATE locales_npc_text SET entry = 9033, Text0_0_loc2 = 'Si vous n’apprenez qu’une seule chose au cours de votre séjour sur Brume-sang, $c, j’espère que ce sera la vigilance. Cette île dévore d’abord les imprudents, puis elle use même les guerriers les plus attentifs.$B$BOù que vous vous tourniez, un ennemi se tient prêt à vous arracher la vie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9033;
UPDATE locales_npc_text SET entry = 9034, Text0_0_loc2 = null, Text0_1_loc2 = 'Tout ce qui est arrivé depuis l’accident est flou. J’ai l’impression qu’une vie entière s’est écoulée depuis l’Exodar.$B$BPour l’instant, ma vie se limite à cette île, avec ses animaux mutants et ses innombrables agents des elfes de sang. Bientôt, j’aurai l’impression de ne jamais rien avoir connu d’autre…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9034;
UPDATE locales_npc_text SET entry = 9036, Text0_0_loc2 = 'Les autres regardent ce paysage et ne voient que mort, corruption et adversité. Bien sûr, rien de tout cela ne manque au tableau, mais ils oublient un peu vite toutes les opportunités offertes par ce nouvel environnement.$B$BJe n’ai pas l’intention de les ignorer, ni de rester sans rien faire, $c. Avec votre aide, nous pourrons montrer aux résidents du Guet du sang comment utiliser l’environnement et ses créatures.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9036;
UPDATE locales_npc_text SET entry = 9037, Text0_0_loc2 = 'Salut, $c.$B$BLe vieux Kessel est un malin. Si j’étais vous, je ferais attention avant de passer un marché avec lui.$B$BIl m’a proposé de « m’occuper des elekk ». En bon naïf, je pensais qu’il voulait que je les nourrisse, que je les dresse, ce genre de choses. En fait, il m’a filé une pelle…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9037;
UPDATE locales_npc_text SET entry = 9038, Text0_0_loc2 = 'Merci de l’avertissement, $N. Nous allons nous préparer !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9038;
UPDATE locales_npc_text SET entry = 9039, Text0_0_loc2 = 'Nous vous remercions pour cet avertissement, ô $gpromis :promise;. La tribu Calmepin sera prête.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9039;
UPDATE locales_npc_text SET entry = 9040, Text0_0_loc2 = 'Merci de l’avertissement, $N. Nos défenses seront prêtes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9040;
UPDATE locales_npc_text SET entry = 9041, Text0_0_loc2 = 'Je suis Kessel, seigneur des elekk, disciple de la Main d’Argus. Êtes-vous la nouvelle recrue ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9041;
UPDATE locales_npc_text SET entry = 9042, Text0_0_loc2 = 'Je suis Achelus, le maître de poste du Guet du sang.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9042;
UPDATE locales_npc_text SET entry = 9043, Text0_0_loc2 = 'Je livre le courrier qui arrive et qui part de Brume-azur, $c. Si vous voulez transmettre un message, il passera très probablement d’abord par moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9043;
UPDATE locales_npc_text SET entry = 9044, Text0_0_loc2 = 'Avez-vous la moindre idée de ce que cela signifie, d’être une Main d’Argus ? Nous sommes des agents de la Lumière. Nous servons sans poser de questions. Nous mourons sans poser de questions.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9044;
UPDATE locales_npc_text SET entry = 9045, Text0_0_loc2 = 'Ils vous ont $genvoyé:envoyée; pour me retrouver ? Eh bien, je ne rentrerai pas sans mes hommes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9045;
UPDATE locales_npc_text SET entry = 9050, Text0_0_loc2 = 'Oui, je suis l\'un des "Roués". J’espère que cela ne vous dérange pas ? Roués ou draeneï, nous devons tous supporter ceux de notre société qui souhaitent nous tourner en ridicule. Un plus grand discernement et la communion avec les éléments de ce monde, voilà ce qui compte vraiment.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9050;
UPDATE locales_npc_text SET entry = 9051, Text0_0_loc2 = 'Je vous en prie, asseyez-vous et mettez-vous à l\'aise.', Text0_1_loc2 = 'Je vous en prie, asseyez-vous et mettez-vous à l\'aise.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9051;
UPDATE locales_npc_text SET entry = 9052, Text0_0_loc2 = 'Bien le bonjour, $c. Avez-vous besoin d\'un fidèle hippogriffe ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9052;
UPDATE locales_npc_text SET entry = 9053, Text0_0_loc2 = 'Si vous désirez vous rendre à l\'Exodar, j\'ai l\'hippogriffe qu\'il vous faut !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9053;
UPDATE locales_npc_text SET entry = 9054, Text0_0_loc2 = 'Chuuuut ! Ne faites pas de bruit, j\'observe des murlocs.$B$BVivre dans une énorme carapace de tortue est un sacrifice que je suis prêt à faire dans l\'intérêt de la science. Après tout, si les murlocs me voyaient, ils réagiraient à ma présence et contamineraient mes données !$B$BEn tant que premier gnome à entrer dans la Ligue des Explorateurs, je vais m\'aventurer courageusement là où nul nain n\'est jamais allé !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9054;
UPDATE locales_npc_text SET entry = 9056, Text0_0_loc2 = 'Avez-vous perdu la raison ? Regardez autour de vous, créature mortelle. Ne voyez-vous pas que vous arpentez une terre ravagée ? Quittez cet endroit si vous ne voulez pas souffrir comme moi et les miens...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9056;
UPDATE locales_npc_text SET entry = 9057, Text0_0_loc2 = 'Êtes-vous aussi aveugle que $gfou:folle; ?$B$BJe suis un fantôme, un rappel cruel d’une civilisation qui s’est éteinte depuis longtemps, lié à la terre que je n’ai pas su protéger.$B$BIl y a bien longtemps, dix mille ans dans le passé, j’étais de chair et de sang comme vous. J’étais le prince de cette contrée et un chevaucheur de dragon béni par Ysera du grand Rêve.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9057;
UPDATE locales_npc_text SET entry = 9058, Text0_0_loc2 = 'N’avez-vous jamais entendu parler d’Ysera ? La gardienne de la Nature ? L’aspect du Rêve ? Elle est la maîtresse de tous les dragons verts ! Oui, et Ysera en personne avait honoré mon royaume en y installant sa progéniture. Nous faisions cause commune avec ces nobles créatures, et ils nous permettaient de les chevaucher lorsque nous partions affronter nos ennemis. Tout était pour le mieux pendant des siècles... jusqu’à...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9058;
UPDATE locales_npc_text SET entry = 9059, Text0_0_loc2 = 'La progéniture d’Aile de mort... Les bienfaits d’Ysera avaient attiré le courroux d’Aile de mort, le maître des dragons noirs. Ils nous ont attaqués pendant notre sommeil ! Beaucoup trouvèrent la mort dès le premier assaut, mais les verts se dressèrent ensuite pour nous protéger.$B$B<Le prince Toreth désigne l’île du doigt.>$B$BCes ossements sont tout ce qui reste de ces grands dragons d’antan. Pas un ne fut épargné.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9059;
UPDATE locales_npc_text SET entry = 9060, Text0_0_loc2 = 'J’ai été le dernier à mourir. Comme je sentais que mon esprit quittait ma dépouille mortelle, j’ai fait un serment de sang.$B$B<Le prince Toreth hoche la tête.>$B$BUn pacte a été scellé entre cette terre et moi : mon sang pour ce monde. Je suis devenu le seul réceptacle de l’histoire de mon peuple.$B$BJe ne peux connaître le repos tant que je ne serai pas certain que l’histoire des chevaucheurs de dragons de Loreth’Aran ne succombera pas au passage du temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9060;
UPDATE locales_npc_text SET entry = 9061, Text0_0_loc2 = 'Les étudiants sont un peu comme des moutons. Ils sont utiles et même aimables tant que vous les guidez de près en faisant attention à eux.$B$BMais si vous les laissez seuls une seule minute, ils s’arrangent aussitôt pour tomber d’un précipice ou se noyer dans une eau peu profonde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9061;
UPDATE locales_npc_text SET entry = 9062, Text0_0_loc2 = 'Ce panneau contient différentes annonces et promesses de récompenses pour l’accomplissement de tâches apparemment très dangereuses. Rien ne retient en particulier votre attention ; vous devriez peut-être revenir y jeter un œil plus tard.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9062;
UPDATE locales_npc_text SET entry = 9063, Text0_0_loc2 = 'Salutations, $gcitoyen:citoyenne;. Je vous prie de circuler si vous n’avez rien d’autre à me demander.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9063;
