UPDATE locales_npc_text SET entry = 9065, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9065;
UPDATE locales_npc_text SET entry = 9066, Text0_0_loc2 = 'Salut à vous, $gchampion:championne;. Les récits de vos exploits seront contés à travers les âges. Un jour, votre statue prendra peut-être place parmi celles des héros de Hurlevent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9066;
UPDATE locales_npc_text SET entry = 9067, Text0_0_loc2 = 'Ne vous approchez pas de moi, $r ! Je n\'ai aucun moyen de savoir quand mon mal va me reprendre.$B$BLaissez-moi seul avant que ça ne recommence.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9067;
UPDATE locales_npc_text SET entry = 9070, Text0_0_loc2 = 'L\'examen du corps vous permet de conclure qu\'il s\'agit du chevalier de sang Aubétoile, le chef de l\'assaut manqué sur Mortholme.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9070;
UPDATE locales_npc_text SET entry = 9071, Text0_0_loc2 = 'Je peux vous aider à établir une guilde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9071;
UPDATE locales_npc_text SET entry = 9072, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9072;
UPDATE locales_npc_text SET entry = 9073, Text0_0_loc2 = null, Text0_1_loc2 = 'Que la grâce d\'Elune soit sur vous. En quoi puis-je vous servir ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9073;
UPDATE locales_npc_text SET entry = 9074, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous salue, $c. Il est bon que vous ayez décidé de venir nous voir.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9074;
UPDATE locales_npc_text SET entry = 9076, Text0_0_loc2 = 'Vous avez servi un des trois avec honneur, $N. Notre heure est venue. Kuros vous appellera quand il sera prêt.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9076;
UPDATE locales_npc_text SET entry = 9077, Text0_0_loc2 = 'Salutations, $c. Je suis Kuros, le second triumvir de la Main.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9077;
UPDATE locales_npc_text SET entry = 9079, Text0_0_loc2 = 'Je les observais pendant qu’ils le battaient sans pitié. Leur brute de lieutenant se comportait comme si tant de cruauté était complètement normal. Comme les coups pleuvaient, je sentais que je craquais.$B$BLe redresseur de torts Saruan encaissait tout ça, et je me suis mis à pleurer. Je n’avais pas peur… j’étais triste. Voir un triumvir draeneï être traité de la sorte. Si seulement j’avais pu me libérer de mes liens. Si seulement…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9079;
UPDATE locales_npc_text SET entry = 9080, Text0_0_loc2 = 'Après ce qui m’a semblé une éternité, le redresseur de torts a perdu conscience. La douleur était trop forte, même pour lui. Le plus cruel, que les autres appelaient Matis, a essayé de réveiller Saruan en éclaboussant son corps brisé d’eau croupie par la contagion. Mais Saruan n’a pas repris conscience.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9080;
UPDATE locales_npc_text SET entry = 9081, Text0_0_loc2 = 'Je voulais hurler mais seul un faible hoquet est sorti de ma bouche. J’ai aussitôt reçu une volée de coups pour cet acte de rébellion. Je me suis retrouvé étendu au sol, et j’ai vu que Matis faisait les cent pas devant le corps de Saruan. Il avait l’air visiblement inquiet. Il a dit que Sironas, ou Sirona, ou une entité de ce type lui arracherait la chair des os si Saruan était mort à cause d’eux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9081;
UPDATE locales_npc_text SET entry = 9082, Text0_0_loc2 = 'Que cette entité Sironas avait des projets pour Saruan…$B$B<L’écriture s’arrête brusquement sur une longue ligne en travers de la page. Ce draeneï est probablement mort pendant qu’il était en train d’écrire.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9082;
UPDATE locales_npc_text SET entry = 9084, Text0_0_loc2 = 'Bonjour, $C. Si vous souhaitez apprendre l\'art de la forge, vous êtes au bon endroit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9084;
UPDATE locales_npc_text SET entry = 9085, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh, ne faites pas attention à Copain. Il ne vous fera pas de mal, il aime juste se frotter un peu.$B$BC\'est juste un gros chaton adorable.$B$BPas vrai, Copain ? Oui c\'est vrai, mon petit mignon tout plein. Le petit bébé à sa maman, voilà ce que tu es !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9085;
UPDATE locales_npc_text SET entry = 9086, Text0_0_loc2 = 'Nous, les druides cénariens, sommes les protecteurs de l’équilibre de la nature. Le terme clé étant l’équilibre. Quand il faut agir pour le préserver, nous devons agir de façon décisive et sans attendre trop longtemps. Ne laissez personne vous dire le contraire, surtout ceux d’entre nous qui voudraient se cantonner dans des rôles d’observateurs !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9086;
UPDATE locales_npc_text SET entry = 9090, Text0_0_loc2 = 'Qui êtes-vous ? Laissez-moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9090;
UPDATE locales_npc_text SET entry = 9097, Text0_0_loc2 = 'Commencez par chercher vers le nord, $N. Nos éclaireurs ont repéré le piston dimensionnel près de la côte.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9097;
UPDATE locales_npc_text SET entry = 9098, Text0_0_loc2 = 'Mon histoire ? C’est bien aimable à vous de me le demander. Très bien.$B$BIl y a bien longtemps sur la planète Draenor, celle dont les restes forment maintenant l’Outreterre, tous les draeneï vivaient en paix. Mais un jour la Légion ardente est arrivée, et ils ont corrompu les orcs avec leurs magies gangrénées.$B$BNous avons été contraints de nous battre pour survivre, et les énergies corrompues auxquelles nous nous sommes exposés ont eu des effets épouvantables sur certains d’entre nous, transformant à terme certains en Roués comme moi, ou pire en Perdus dont les facultés intellectuelles ont apparemment disparu.$B$BDois-je continuer ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9098;
UPDATE locales_npc_text SET entry = 9099, Text0_0_loc2 = 'L\'exposition des Roués et Perdus aux énergies gangrenées a aussi eu pour effet secondaire de nous couper de la Lumière. À l’époque je ne savais pas vraiment pourquoi, mais en tant que redresseur de torts j’étais déterminé à le découvrir. J’ai alors décidé de faire un pèlerinage dans ce but.$B$BAprès l’horrible conclusion de la guerre, je suis donc parti parcourir les déserts de la planète brisée, en implorant le retour de la Lumière. Mais les années passaient et jamais la Lumière n’a répondu à mes prières.$B$BVoulez-vous que je développe plus avant ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9099;
UPDATE locales_npc_text SET entry = 9100, Text0_0_loc2 = 'J’avais eu la chance de trouver une oasis et je décidais d’y rester tant que je n’aurais pas reçu une réponse. Je restais donc assis là, ne bougeant que lorsque c’était absolument indispensable, et me nourrissant des rares créatures qui approchaient de mon point d’eau. C’est dire comme j’étais orgueilleux.$B$BComme je vous disais, des dizaines d’années avaient passé sans que je reçoive de réponse. Mais je me rappelle un coucher de soleil particulièrement beau. Ce soir-là, un doux zéphyr caressait le sol et il me semblait entendre une voix, alors que tout était désert.$B$BComme vous devez le penser, j’ai cru que ma raison avait fini par m’abandonner.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9100;
UPDATE locales_npc_text SET entry = 9101, Text0_0_loc2 = 'Ce vent avait quelque chose d’étrange : il ne s’arrêtait pas. Il a soufflé à travers le pays pendant des semaines, plus ou moins fort, mais sans jamais cesser. Et pendant tout ce temps la voix continuait à parler, sans que je puisse la comprendre, ni même savoir d’où elle provenait.$B$BMa longue période de solitude m’avait appris comment rester calme et méditer en mon for intérieur. Ce que je fis pour éviter de devenir fou. C’est alors que j’ai commencé à comprendre la voix. Elle venait du vent lui-même, et elle avait tant de choses à dire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9101;
UPDATE locales_npc_text SET entry = 9102, Text0_0_loc2 = 'Elle m’a parlé de secrets et de pouvoir, et également de vérités. La lumière n’avait pas abandonné les Roués ! Nous avions baigné si longtemps dans les énergies gangrenées que les orcs avaient manipulées contre les draeneï qu’en plus de corrompre nos corps, elles avaient anéanti notre faculté à être entendu, ou peut-être à entendre, la Lumière.$B$BMais nos esprits n’étaient pas brisés, et l’air m’offrait une autre voie sur laquelle avancer pour délivrer les miens.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9102;
UPDATE locales_npc_text SET entry = 9103, Text0_0_loc2 = 'La voix venait m’enseigner les voies du chamanisme, qui est en quelque sorte une autre facette de la Lumière. Et au vent se joignaient l’eau de mon oasis, le feu de mon campement, et la terre sur laquelle j’étais assis. Tous avaient leur mot à dire.$B$BAu cours des années qui ont suivi, ils m’ont enseigné comment faire appel aux éléments du monde et faire corps avec eux. Ils m’ont également appris que tous les mondes de toutes les étoiles du ciel, y compris celui-ci, sont composés de ces éléments.$B$BEt le jour est venu où j’ai senti que j’en savais assez et que je pouvais partir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9103;
UPDATE locales_npc_text SET entry = 9104, Text0_0_loc2 = 'Je suis retourné auprès des miens et j’ai été accueilli par des visages souriants et des bras tendus, surtout parmi les Roués. Les préjugés de nombreux draeneï ne s’étaient pas relâchés pendant mon absence. Cependant tout se passait comme si chacun savait que je revenais, et j’ai bientôt découvert pourquoi.$B$BVelen, le prophète et dirigeant de tous les draeneï, avait vu mon retour dans une de ses visions, et il était lui aussi présent pour m’accueillir. Il a demandé à me parler en privé, et comme je lui racontais mon histoire, il opinait en souriant, car cela aussi il l’avait prédit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9104;
UPDATE locales_npc_text SET entry = 9105, Text0_0_loc2 = 'Velen m’a pressé d’assumer un rôle de dirigeant au sein des Roués. Plus important encore, il m’a demandé d’enseigner à tous le chamanisme, aux Roués comme aux draeneï. J’ai accepté.$B$BNous savions alors que les préjugés de certains rendraient la tâche difficile, dans le meilleur des cas. Il y avait ceux qui méprisaient les Roués car ils constituaient un effrayant rappel du passé, et aussi ceux qui pensaient que la voie du chaman était un blasphème contre la Lumière.$B$BEn tout cas, voilà mon histoire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9105;
UPDATE locales_npc_text SET entry = 9106, Text0_0_loc2 = 'Si nos ennemis pouvaient comprendre que nous souhaitons seulement vivre en harmonie avec eux, peut-être que nous ne serions pas obligés de les attaquer pour assurer notre survie ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9106;
UPDATE locales_npc_text SET entry = 9107, Text0_0_loc2 = 'Leur jour viendra, $N.$B$BEt quand ça arrivera, je serai là.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9107;
UPDATE locales_npc_text SET entry = 9108, Text0_0_loc2 = 'Avec de nouveaux ennemis à nos portes, nous ne pouvons pas nous permettre de laisser nos anciens ennemis profiter de la situation !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9108;
UPDATE locales_npc_text SET entry = 9109, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est pas incroyable comme endroit ? Bon, c\'est un peu étouffant, si vous voulez mon avis.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9109;
UPDATE locales_npc_text SET entry = 9110, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9110;
UPDATE locales_npc_text SET entry = 9111, Text0_0_loc2 = 'Ces immondes géants fongiques ! Qu’est-ce que nous leurs avons donc fait ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9111;
UPDATE locales_npc_text SET entry = 9112, Text0_0_loc2 = null, Text0_1_loc2 = 'Si seulement je ne m\'étais pas tordue la cheville en venant ici, je serais là-dedans avec mes amis. Hummm, en y repensant, c\'est peut-être mieux d\'être ici.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9112;
UPDATE locales_npc_text SET entry = 9113, Text0_0_loc2 = 'Fhwoor aime les petits sporelins. Eux sauver Fhwoor. Pas les manger.$B$BFhwoor détester nagas. Fhwoor écraser !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9113;
UPDATE locales_npc_text SET entry = 9117, Text0_0_loc2 = 'Je suis Aesom, troisième triumvir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9117;
UPDATE locales_npc_text SET entry = 9118, Text0_0_loc2 = 'Ici repose la dépouille de l\'échardonneur Vertemain, l’un des druides qui accompagnaient la guetteuse Jhang. Apparemment, il a été abattu de façon nette et rapide alors qu’il tentait de s’échapper des enclos aux esclaves.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9118;
UPDATE locales_npc_text SET entry = 9119, Text0_0_loc2 = 'J\'ai presque réussi !$B$BJe suis venu ici pour prouver l\'existence de Bourbierreux, le plus mythique des géant fongiques. Mon vœu s\'est en quelque sorte réalisé.$B$BVous devez me laisser partir ! En échange, je vous offrirai une bénédiction qui vous aidera à vous occuper de lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9119;
UPDATE locales_npc_text SET entry = 9121, Text0_0_loc2 = 'Heureusement que vous étiez là ! Griffe et moi étions en train d’explorer cet endroit quand nous avons été embusqués par le seigneur des marais Musel\'ek. Il peut contrôler les animaux, même les druides !$B$BMalheureusement, Griffe était en forme d’ours quand c’est arrivé, et il est maintenant le familier du seigneur des marais. Je n’ose pas me transformer en félin, il pourrait m’avoir aussi !$B$BJe crains que vous ne deviez affronter Griffe pour pouvoir le sauver. Continuez tout droit vers l\'ouest, vous ne pouvez pas le manquer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9121;
UPDATE locales_npc_text SET entry = 9122, Text0_0_loc2 = 'Étant apparemment le seul "Roué" survivant, c’est pour moi un devoir et un honneur de reprendre les enseignements chamaniques du long-voyant Nobundo.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9122;
UPDATE locales_npc_text SET entry = 9123, Text0_0_loc2 = null, Text0_1_loc2 = 'Maudits soient les elfes de sang et leur téméraire magie !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9123;
UPDATE locales_npc_text SET entry = 9124, Text0_0_loc2 = 'Ne me faites pas perdre mon temps. Que voulez-vous ?', Text0_1_loc2 = 'Ne me faites pas perdre mon temps. Que voulez-vous ?', Text1_0_loc2 = 'Vous ne voyez pas que je suis occupé ?', Text1_1_loc2 = 'Vous ne voyez pas que je suis occupée ?', Text2_0_loc2 = 'Oui ? Qu\'est-ce que vous voulez ?', Text2_1_loc2 = 'Oui ? Qu\'est-ce que vous voulez ?', Text3_0_loc2 = 'Et vous êtes ?', Text3_1_loc2 = 'Et vous êtes ?', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9124;
UPDATE locales_npc_text SET entry = 9125, Text0_0_loc2 = 'Qu’il est embarrassant d’être pris comme familier par un chasseur !$B$BJe vous dois la vie, mais par pitié ne vous souciez plus de mon bien-être. Maintenant que vous m’avez libéré du joug de Musel\'ek, je me débrouillerai pour sortir d’ici. Je vais même probablement passer prendre Rayge en chemin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9125;
UPDATE locales_npc_text SET entry = 9127, Text0_0_loc2 = null, Text0_1_loc2 = 'Puisse la Lumière se plier à votre volonté en toutes choses.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9127;
UPDATE locales_npc_text SET entry = 9128, Text0_0_loc2 = null, Text0_1_loc2 = 'Toutes les choses de la nature sont connectées en une grande toile, une écologie. Si nous l’observons en prenant garde à ne pas la perturber, nous pouvons avoir la chance de reconstituer les relations complexes entre tous les organismes qui nous entourent.$B$BSi nous arrivons à comprendre tout cela, nous pourrons agir de façon efficace pour assurer le bien-être à venir de toutes les créatures. Si par contre nous interférons sans savoir, nous déclencherons certainement des catastrophes.$B$BJe suis ici pour observer les interactions complexes entre les sporelins et les seigneurs-tourbe.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9128;
UPDATE locales_npc_text SET entry = 9130, Text0_0_loc2 = '<Le redresseur de tort Aesom vous salue.>$B$BAvez-vous parlé à Admetius ? Je crois qu\'il demandait où vous étiez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9130;
UPDATE locales_npc_text SET entry = 9131, Text0_0_loc2 = 'Les druides me protègent des nagas et des Perdus malfaisants. En échange, je leur apprends tout ce qu’il faut savoir sur la faune et la flore du marécage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9131;
UPDATE locales_npc_text SET entry = 9134, Text0_0_loc2 = 'Vous avez de la chance que je sois enfermé dans cette cage, $r.', Text0_1_loc2 = 'Vous avez de la chance que je sois enfermée dans cette cage, $r.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9134;
UPDATE locales_npc_text SET entry = 9136, Text0_0_loc2 = '<L\'agent Épervier du soleil capturé vous regarde d\'un air méfiant.>$B$BJe ne vous ai encore jamais $grencontré:rencontrée;. Qui êtes-vous ?', Text0_1_loc2 = '<L\'agent Épervier du soleil capturé vous regarde d\'un air méfiant.>$B$BJe ne vous ai encore jamais $grencontré:rencontrée;. Qui êtes-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9136;
UPDATE locales_npc_text SET entry = 9137, Text0_0_loc2 = '<L\'agent Épervier du soleil capturé sourit d\'un air suffisant.>$B$BNe vous inquiétez pas, bleusaille, nous avons encore de nombreux contrôleurs, et nous disposons d\'un grand nombre d\'emplacements pour les portails. Je suis certain que Sironas aura bientôt ouvert une nouvelle porte. Ces chiens à la peau bleue ne feront pas long feu sur ce monde. Sironas et l\'abomination termineront de nettoyer les miettes que les troupes des Éperviers du soleil leur auront laissées.', Text0_1_loc2 = '<L\'agent Épervier du soleil capturé sourit d\'un air suffisant.>$B$BNe vous inquiétez pas, bleusaille, nous avons encore de nombreux contrôleurs, et nous disposons d\'un grand nombre d\'emplacements pour les portails. Je suis certaine que Sironas aura bientôt ouvert une nouvelle porte. Ces chiens à la peau bleue ne feront pas long feu sur ce monde. Sironas et l\'abomination termineront de nettoyer les miettes que les troupes des Éperviers du soleil leur auront laissées.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9137;
UPDATE locales_npc_text SET entry = 9138, Text0_0_loc2 = 'C\'est une idée de Sironas… L\'abomination est en fait l\'un des leurs. C\'est un haut gradé de l\'ordre militaire draeneï : le Triumvirat ou une bêtise de ce genre.$B$BQuelle que soit cette chose, elle est massive, puissante et folle de rage. Toutes les attaques contre la bobine vectorielle échoueront certainement tant que cette abomination se trouvera dans les parages.', Text0_1_loc2 = 'C\'est une idée de Sironas… L\'abomination est en fait l\'un des leurs. C\'est un haut gradé de l\'ordre militaire draeneï : le Triumvirat ou une bêtise de ce genre.$B$BQuelle que soit cette chose, elle est massive, puissante et folle de rage. Toutes les attaques contre la bobine vectorielle échoueront certainement tant que cette abomination se trouvera dans les parages.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9138;
UPDATE locales_npc_text SET entry = 9139, Text0_0_loc2 = 'Sironas assure que certains érédars possèdent la capacité de transformer leurs congénères en man\'ari. On suppose que les draeneï étaient autrefois des érédars, et Sironas est une puissante érédar.$B$B<L\'agent rit de bon cœur.>$B$BC\'est à peine croyable. Ces créatures pathétiques étaient autrefois des démons tout-puissants. Quels fous peuvent se priver ainsi d\'un tel pouvoir ?', Text0_1_loc2 = 'Sironas assure que certains érédars possèdent la capacité de transformer leurs congénères en man\'ari. On suppose que les draeneï étaient autrefois des érédars, et Sironas est une puissante érédar.$B$B<L\'agent rit de bon cœur.>$B$BC\'est à peine croyable. Ces créatures pathétiques étaient autrefois des démons tout-puissants. Quels fous peuvent se priver ainsi d\'un tel pouvoir ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9139;
UPDATE locales_npc_text SET entry = 9140, Text0_0_loc2 = 'C\'est évident. Sinon, comment aurait-elle pu infiltrer leurs rangs et se faire passer pour une draeneï ? Elle a vécu très longtemps parmi eux.', Text0_1_loc2 = 'C\'est évident. Sinon, comment aurait-elle pu infiltrer leurs rangs et se faire passer pour une draeneï ? Elle a vécu très longtemps parmi eux.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9140;
UPDATE locales_npc_text SET entry = 9141, Text0_0_loc2 = 'Nombreux ? Elle n\'a que deux points faibles. Ils se situent à la base de la structure, l\'un sur le côté ouest et l\'autre sur le côté est. Mais… attendez une minute. Comment avez-vous entendu parler de la bobine vectorielle ? Vous ne venez pas de dire que vous avez été $gcapturé:capturée; avant même de vous être $gmatérialisé:matérialisée; ?', Text0_1_loc2 = 'Nombreux ? Elle n\'a que deux points faibles. Ils se situent à la base de la structure, l\'un sur le côté ouest et l\'autre sur le côté est. Mais… attendez une minute. Comment avez-vous entendu parler de la bobine vectorielle ? Vous ne venez pas de dire que vous avez été $gcapturé:capturée; avant même de vous être $gmatérialisé:matérialisée; ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9141;
