UPDATE locales_npc_text SET entry = 9163, Text0_0_loc2 = 'Tout ceci est pour vous, $N. C\'est votre œuvre. Vous avez sauvé tous ces gens et des milliers d\'autres.$B$BVous avez su vous élever au-dessus des conflits et des tragédies de ce monde, et ce faisant vous nous avez élevés avec vous.$B$BVous avez tant fait… plus que ce que la plupart des gens auraient fait au cours de dix vies, et pourtant la vôtre ne fait que commencer. Si seulement nous avions un millier de héros tels que vous.$B$BMais écoutez-moi bien, $N. Votre destin vous attend dans l\'Outreterre. Je l\'ai perçu dans mes visions, une prophétie encore ignorée.$B$BRetrouvez notre peuple, $N. Retrouvez-les et amenez-les à la maison…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9163;
