UPDATE locales_npc_text SET entry = 9167, Text0_0_loc2 = 'Ysiel s’attendait à rencontrer une certaine opposition de l’ennemi lors de son incursion en Outreterre. C’est pourquoi nous autres, gardiens, nous sommes joints à cette mission.$B$BMais nous ne nous attendions pas du tout à ce que l’opposition des nagas soit aussi forte et puissante.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9167;
UPDATE locales_npc_text SET entry = 9168, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne sais pas du tout qui vous êtes, mais vous êtes $gle bienvenu:la bienvenue;. Archimonde et son armée du Fléau peuvent attaquer à n’importe quel moment, $c. Combattrez-vous ce mal à nos côtés ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9168;
