UPDATE locales_npc_text SET entry = 9172, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est moi qui dirige les défenses du Guet du sang. En quoi puis-je vous aider ?', Text1_0_loc2 = null, Text1_1_loc2 = 'Bonjour, vous êtes ici pour vous renseigner sur le conflit à Brume-sang ?', Text2_0_loc2 = null, Text2_1_loc2 = 'Je peux vous aider ?', Text3_0_loc2 = null, Text3_1_loc2 = 'Nous sommes des serviteurs inconditionnels de la Lumière. Mais vous êtes peut-être $gcurieux:curieuse; d\'en savoir plus sur nos objectifs ?', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9172;
