UPDATE locales_npc_text SET entry = 9174, Text0_0_loc2 = 'Bienvenue au quartier général des chevaliers de sang, défenseurs de Quel\'Thalas, fidèles serviteurs du prince Kael\'thas et véritables maîtres de la Lumière.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9174;
