UPDATE locales_npc_text SET entry = 9180, Text0_0_loc2 = 'Les esprits s’agitent, $r. Prenez garde à votre environnement quand vous serez en Nagrand, sinon vous serez consumé par leur furie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9180;
UPDATE locales_npc_text SET entry = 9181, Text0_0_loc2 = 'Le Cercle terrestre cherche à préserver la force et la majesté des éléments.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9181;
UPDATE locales_npc_text SET entry = 9182, Text0_0_loc2 = null, Text0_1_loc2 = 'Comment mon peuple peut-il combattre alors que les éléments sont en effervescence ? Ne peuvent-ils pas se rendre compte que sans le soutien des éléments, le monde ne peut simplement pas exister ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9182;
UPDATE locales_npc_text SET entry = 9183, Text0_0_loc2 = 'Je suis Untrag, du Cercle terrestre. Essayez de ne pas déranger les éléments tant que vous resterez au Trône des éléments.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9183;
