UPDATE locales_npc_text SET entry = 9190, Text0_0_loc2 = 'C\'est un plaisir de voir une personne saine d\'esprit par ici. Que puis-je faire pour vous aider ?', Text0_1_loc2 = 'C\'est un plaisir de voir une personne saine d\'esprit par ici. Que puis-je faire pour vous aider ?', Text1_0_loc2 = 'Ah, $gun:une; camarade forestier. De quoi avez-vous besoin ?', Text1_1_loc2 = 'Ah, $gun:une; camarade forestier. De quoi avez-vous besoin ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9190;
