UPDATE locales_npc_text SET entry = 9199, Text0_0_loc2 = null, Text0_1_loc2 = 'J’ai toujours aimé trier et organiser les choses. Je suppose que je tiens cela de ma mère.', Text1_0_loc2 = null, Text1_1_loc2 = 'Étudier les nouvelles espèces de plantes est très important pour notre expédition. Et pas uniquement celles qui ont un intérêt médical ou qui sont comestibles.$B$BLes végétaux constituent la base sur laquelle toutes les autres formes de vie se construisent. Les comprendre nous permet de mieux comprendre la nature dans son ensemble.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9199;
UPDATE locales_npc_text SET entry = 9201, Text0_0_loc2 = 'Je ne sais que penser pour l\'instant des gens de votre race, $r. Certains d\'entre vous peuvent se montrer violents et tuer à vue les sporelins.$B$BVous semblez $gamical:amicale;... mais peut-être un peu étrange quand même.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9201;
UPDATE locales_npc_text SET entry = 9202, Text0_0_loc2 = 'Bienvenue à Sporeggar, $r. Nous ne voyons habituellement pas beaucoup de vos semblables par ici, mais vous nous avez prouvé votre valeur. Faites comme chez vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9202;
UPDATE locales_npc_text SET entry = 9203, Text0_0_loc2 = 'Les seigneurs-tourbe dévorent les sacs de spores ! Il faut les arrêter !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9203;
UPDATE locales_npc_text SET entry = 9204, Text0_0_loc2 = 'Grâce à vous, nos jeunes seront en sécurité, $N. Rendez-vous à Sporeggar et rencontrez mon peuple. Ils ont tous entendu parler du bien que vous avez fait.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9204;
UPDATE locales_npc_text SET entry = 9206, Text0_0_loc2 = null, Text0_1_loc2 = 'Les défenseurs du Guet du sang protègent cet avant-poste de toute menace.', Text1_0_loc2 = null, Text1_1_loc2 = 'Les défenseurs sont ici pour protéger Guet du sang de la menace que représentent les Eperviers du soleil.', Text2_0_loc2 = null, Text2_1_loc2 = 'On nous a récemment parlé d\'une créature immensément puissante qui se fait invoquer près de la Bobine vectorielle. N\'ayez crainte, si quoi que ce soit menaçait la ville, nous donnerions nos vies pour la protéger.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9206;
UPDATE locales_npc_text SET entry = 9208, Text0_0_loc2 = '<La terre gronde lorsque Gordawg cesse de bouger.>$B$BParlez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9208;
UPDATE locales_npc_text SET entry = 9218, Text0_0_loc2 = 'Mon père est parti dans un endroit plus exotique à la recherche de plus gros gibier. Mais ne vous inquiétez pas, la chasse continue ici !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9218;
UPDATE locales_npc_text SET entry = 9219, Text0_0_loc2 = 'Gardez les yeux grands ouverts, $c. Dans le coin, les bestioles ont tendance à vous traquer, plutôt que le contraire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9219;
UPDATE locales_npc_text SET entry = 9220, Text0_0_loc2 = 'Écoute, $gmon gars:ma petite;. J’ai laissé l’expédition entre les mains tout à fait compétentes de mon fils, Junior. Lorsque vous avez traqué le gibier le plus féroce de tout Azeroth, vous finissez par en vouloir davantage.$B$BVous avez vu la taille des bêtes du coin ?!$B$BJe suis ici pour chasser la proie la plus dangereuse que vous ayez pu voir. J\'ai l’intention de tuer Longues-défenses et de prendre son cœur comme trophée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9220;
UPDATE locales_npc_text SET entry = 9221, Text0_0_loc2 = '$N ! Je me souviens fort bien de vos exploits de chasseur. Vous n\'aviez pas trop bien commencé à Strangleronce, mais à force de persévérance, vous avez fini par devenir $gun vrai pro:une vraie pro;, et même par coincer le vieux Bangalash.$B$BMais je vous assure, $gmon gars:ma petite; que le gibier d\'Outreterre, c\'est autre chose. J\'ai des cicatrices pour le prouver !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9221;
UPDATE locales_npc_text SET entry = 9226, Text0_0_loc2 = 'Je vous souhaite humblement la bienvenue à notre temple, esprit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9226;
UPDATE locales_npc_text SET entry = 9227, Text0_0_loc2 = 'Traiter avec les draeneï, le peuple qui s’est détourné de nous au plus fort de nos souffrances ? Il n’en est pas question.$B$BUn véritable esprit ne s’exprimerait pas ainsi ! Montrez-vous, imposteur ! Qui vous envoie ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9227;
UPDATE locales_npc_text SET entry = 9229, Text0_0_loc2 = 'Ainsi, nous serions censés faire confiance à ceux qui nous insultent en cherchant à nous tromper ? Les draeneï nous méprisent-ils au point d’avoir recours à ce genre de tours pour attirer notre attention ?$B$BNous savons qu’ils cherchent à nous « sauver », mais mon peuple ne veut pas de ce « rachat ». Nous sommes capables de nous débrouiller. Nous chercherons notre destinée comme un peuple fier et indépendant. Transmettez ce message à vos alliés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9229;
UPDATE locales_npc_text SET entry = 9230, Text0_0_loc2 = 'Nous sommes pressés de toutes parts par la Légion ardente, mais nos défenses tiennent. Tant que nous maintenons Archimonde à l’écart de l’Arbre-monde, chaque instant nous rapproche de la victoire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9230;
UPDATE locales_npc_text SET entry = 9231, Text0_0_loc2 = 'Bien. Assurez-vous qu’ils ont compris.$B$BEt maintenant, j’aimerais que vous laissiez mon temple en paix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9231;
UPDATE locales_npc_text SET entry = 9235, Text0_0_loc2 = 'Bienvenue au Havre d’Orebor, $gmon ami:mon amie;. Vous n’avez rien à craindre. Nous sommes Roués, mais ne nous sommes pas vos ennemis.$B$BJe suis Ikuti des Kurenaï, un groupe de Roués qui souhaite renouer avec nos frères draeneï et avec leurs nouveaux alliés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9235;
UPDATE locales_npc_text SET entry = 9236, Text0_0_loc2 = 'Maktu ne vous reconnaît pas, $r, mais il est heureux de vous rencontrer. Si vous avez besoin d’acheter quelque chose ou de réparer votre armure, allez donc le voir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9236;
UPDATE locales_npc_text SET entry = 9237, Text0_0_loc2 = 'Les souvenirs de ce temple voilent mon esprit comme une brume dense, mais je ne parviens pas à les assembler pour en faire une image cohérente Cet endroit a eu beaucoup d’importance pour moi autrefois. Tant que je n’aurai pas retrouvé ce souvenir, j’aurai l’impression qu’une part de moi-même me manque.$B$BLes draeneï m’aideront peut-être à retrouver ce que j’ai perdu.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9237;
UPDATE locales_npc_text SET entry = 9238, Text0_0_loc2 = 'Certaines personnes m\'appellent "Tim", mais pour mes amis, je suis "le spécialiste".', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9238;
UPDATE locales_npc_text SET entry = 9239, Text0_0_loc2 = 'Si vous avez besoin de poser la question, c\'est que vous ne me connaissez pas assez bien pour m\'appeler comme ça.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9239;
UPDATE locales_npc_text SET entry = 9240, Text0_0_loc2 = 'Salutations, $C. Vous souhaitez découvrir les limites de vos talents de chasseur ? N\'allez pas plus loin.$B$BHemet Nesingwary a réuni la plus remarquable expédition que l\'on ait jamais vu sur les vestiges de ce monde. Joignez-vous à nous pour ce safari si vous l\'osez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9240;
UPDATE locales_npc_text SET entry = 9241, Text0_0_loc2 = 'Ne vous laissez pas tromper par les ruines qui nous entourent, $C. Dalaran est aussi puissante qu\'autrefois.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9241;
UPDATE locales_npc_text SET entry = 9242, Text0_0_loc2 = 'Karazhan n’est pas qu’un simple bâtiment, $N. Si vous suivez les lignes telluriques qui sillonnent Azeroth, vous finirez par vous rendre compte qu’elles convergent toutes ici.$B$B<Alturus vous montre un endroit invisible, sous la tour.>$B$BFascinant, n’est-ce pas ?', Text0_1_loc2 = null, Text1_0_loc2 = 'L’archimage Alvareux a écrit un traité présentant une théorie fascinante. Il imaginait qu’une copie exacte de Karazhan existait dans un autre monde, mais que toutes deux partageaient le même espace.$B$BQuel dommage qu’il ait perdu la tête peu après !', Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9242;
UPDATE locales_npc_text SET entry = 9243, Text0_0_loc2 = 'La sagesse des naaru défie l’imagination. J’ai plus appris d’A’dal au cours des cinq derniers mois que dans les bibliothèques de Dalaran pendant toutes ces années.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9243;
UPDATE locales_npc_text SET entry = 9244, Text0_0_loc2 = 'C\'est ici que l\'on poste les avis de recherche pour les criminels notoires et les animaux dangereux. Pour l\'instant, tout ce qui est proposé vous paraîtrait beaucoup trop difficile, mais revenez examiner ce panneau lorsque vous aurez progressé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9244;
UPDATE locales_npc_text SET entry = 9245, Text0_0_loc2 = '“Un épais trait rouge a été tracé en travers des mots AVIS DE RECHERCHE : Griffe-de-mort. "Cette prime a été touchée par l\'héroïque $N, qui a triomphé de la terrible bête," peut-on lire en dessous, toujours à l\'encre rouge. “', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9245;
UPDATE locales_npc_text SET entry = 9246, Text0_0_loc2 = 'Ce panneau est couvert d’annonces et d’avis de recherche.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9246;
UPDATE locales_npc_text SET entry = 9248, Text0_0_loc2 = 'Bienvenue à Zabra\'jin, $C. Je suis le chasseur des ombres Denjai, responsable de cet avant-poste et commandant des forces des Sombrelances dans le marécage de Zangar.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9248;
UPDATE locales_npc_text SET entry = 9249, Text0_0_loc2 = 'Ne pensez même pas à filer avec mes bébés murlocs ! J\'ai des yeux d\'aigle et des oreilles de… de… d\'un truc qui entend vachement bien !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9249;
UPDATE locales_npc_text SET entry = 9250, Text0_0_loc2 = null, Text0_1_loc2 = 'Tor\'gash s\'imagine qu\'il peut lire l\'avenir dans les entrailles des oiseaux ou dans la disposition des osselets de ses ancêtres. En réalité, il suffit d\'interroger les esprits. Mais ne le lui dites pas, $N. Il est très têtu.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9250;
UPDATE locales_npc_text SET entry = 9256, Text0_0_loc2 = 'Vous avez fait vos preuves comme chasseur de gros gibier, $N. Mais il reste une créature pour hanter vos rêves… Longues-défenses !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9256;
UPDATE locales_npc_text SET entry = 9257, Text0_0_loc2 = 'Vous les avez tous eus : Bach\'lor, Banthar, Arrache-tripes et même Longues-défenses ! Vous êtes vraiment le plus grand chasseur qu\'il m\'ait été donné de rencontrer !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9257;
UPDATE locales_npc_text SET entry = 9258, Text0_0_loc2 = 'Je me battais au corps à corps contre un cerf talbuk dans les broussailles épineuses, avec seulement mon couteau bien aiguisé...<tousse>... et la vive intelligence du chasseur pour me sauver.$B$BEt soudain j\'ai été attaqué par deux féroces chasseurs venteroc qui ont plongé sur moi depuis les cieux ! Cette attaque des plus lâches...<tousse>... ne les a pas conduit bien loin cependant.$B$BJ\'ai leurs becs en guise de trophée, mais cette rude épreuve m\'a aussi valu une égratignure infectée. C\'est plutôt vilain, et ça me fait très mal, mais je suis sûr que ce sera guéri dans un jour ou deux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9258;
UPDATE locales_npc_text SET entry = 9259, Text0_0_loc2 = 'La première fois que j\'ai vu des champignons plus grands que des arbres, je ne me suis pas senti très à l\'aise. Au bout d\'un moment, on arrête de leur prêter attention.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9259;
UPDATE locales_npc_text SET entry = 9260, Text0_0_loc2 = null, Text0_1_loc2 = 'N’oubliez pas de prendre un peu de temps pour pêcher lorsque vous serez dans le marécage de Zangar. Vous ne savez pas quand vous aurez l’occasion de revoir l’eau.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9260;
UPDATE locales_npc_text SET entry = 9261, Text0_0_loc2 = 'Telredor était autrefois une retraite isolée destinées aux prêtres et pèlerins draeneï. Les prêtres qui y vivaient recevaient rarement des visiteurs.$B$BLors des premières attaques des orcs contre des villes draeneï, des réfugiés ont commencé à arriver en grand nombre. L\'anachorète Ahuurn et les autres ont fait du sanctuaire qu\'était Telredor un refuge. De nombreux draeneï, y compris moi, doivent leur survie à ses efforts.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9261;
UPDATE locales_npc_text SET entry = 9262, Text0_0_loc2 = null, Text0_1_loc2 = 'La vue du sommet de Telredor est splendide, n’est-ce pas ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9262;
UPDATE locales_npc_text SET entry = 9263, Text0_0_loc2 = 'Bienvenue à Telredor, $c. Si vous avez besoin de quoi que ce soit, n’hésitez pas à demander.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9263;
UPDATE locales_npc_text SET entry = 9264, Text0_0_loc2 = 'Je n’ai jamais vu autant de variétés de champignons en un seul endroit. Avec le temps, je suis sûr que je leur trouverai une utilité à tous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9264;
UPDATE locales_npc_text SET entry = 9265, Text0_0_loc2 = 'Telredor a connu plus de changements au cours de ces vingt dernières années que durant tous les siècles précédents. Oh, je ne me plains pas, $N, car le monde a encore plus changé durant cette même période.$B$BLa Lumière m’a apporté des amis et des compagnons que je n’aurais jamais rencontrés si j’étais resté un prêtre cloîtré. Apprenez ce que vous pourrez du passé, durant votre séjour ici. Je vous aiderai à comprendre où votre voyage vous mènera.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9265;
UPDATE locales_npc_text SET entry = 9267, Text0_0_loc2 = 'Le chasseur des ombres Denjai m\'a envoyé ici avec un petit contingent d\'ouvriers et de guerriers pour établir cet avant-poste. Nous avons à peine terminé la construction et il nous demande déjà des rapports !$B$BSi vous avez besoin de marchandises, je peux vous en proposer un petit nombre, mais nous attendons toujours la première cargaison de ravitaillement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9267;
UPDATE locales_npc_text SET entry = 9268, Text0_0_loc2 = 'Si jamais vous rencontrez le sorcier-docteur Tor\'gash, dites-lui que ses décoctions et ses charmes ne fonctionnent pas ! J\'ai fait exactement ce qu\'il m\'a dit de faire et Magasha n\'a toujours d\'yeux que pour Zurai.$B$BNe croyez pas un mot de ce que vous dit cet escroc !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9268;
UPDATE locales_npc_text SET entry = 9269, Text0_0_loc2 = null, Text0_1_loc2 = 'Reavij est aussi subtil qu\'un kodo ! Ce qui est amusant, c\'est qu\'il ne se rend pas compte qu\'on le voit venir à des kilomètres. Je vois bien comment il me fixe quand il pense que personne ne le regarde.$B$BIl me donne la chair de poule. Je regrette vraiment que le chasseur des ombres Denjai l\'ait envoyé avec nous. L\'établissement du poste du Rat des marais aurait été une occasion parfaite pour lui échapper.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9269;
UPDATE locales_npc_text SET entry = 9270, Text0_0_loc2 = 'Bonjour, $c. Je suis l\'un de ceux qu\'on appelle les Roués. Tout comme les Perdus, quoique de façon moins terrible, nous avons été victimes d\'énergies gangrenées qui ont transformé notre forme de draeneï initiale.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9270;
UPDATE locales_npc_text SET entry = 9271, Text0_0_loc2 = 'Garadar est plongée dans le chaos, étranger. Depuis que la grande-mère est tombée malade, Garrosh a sombré dans une profonde dépression. Ce n\'est certainement pas le fils de son père.$B$B<Kurkush soupire.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9271;
UPDATE locales_npc_text SET entry = 9273, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9273;
UPDATE locales_npc_text SET entry = 9274, Text0_0_loc2 = 'Des avis de recherche pour les chefs des nagas Sombrecrête et Écaille-sanglante sont affichés ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9274;
UPDATE locales_npc_text SET entry = 9275, Text0_0_loc2 = 'Ils sont morts. Jusqu\'au dernier…$B$BAlors que les Bourbesang étaient en train de nous exterminer, quelque chose s\'est emparé de moi, quelque chose que je ne peux pas comprendre. Dites-moi, avez-vous déjà ressenti une rage si grande qu\'elle brûle dans tout votre être ? Une chaleur qui dessèche la vie ? Une chaleur qui enflamme le sang dans vos veines ? C\'est ce que j\'ai ressenti.$B$B<Une lueur rouge brille un instant dans les yeux de Saurcroc.>$B$BJ\'étais le dernier survivant. Trente Bourbesang m\'encerclaient… Et je ne me souviens de rien après cette vision. J\'ai perdu connaissance et je me suis réveillé quelque temps plus tard.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9275;
UPDATE locales_npc_text SET entry = 9276, Text0_0_loc2 = 'Des temps difficiles se préparent, $gétranger:étrangère;…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9276;
UPDATE locales_npc_text SET entry = 9277, Text0_0_loc2 = 'Les esprits s\'agitent. Nous devons découvrir la cause de cette perturbation.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9277;
UPDATE locales_npc_text SET entry = 9283, Text0_0_loc2 = 'Hemet est le plus grand chasseur que je connaisse. Nous nous sommes croisés dans les collines de Berceau-de-l\'Hiver, sur Azeroth, il y a de nombreuses lunes. Lui et moi poursuivions la même proie : le grand tigre surnommé Rak\'shiri.$B$BPlutôt que de nous battre pour ce gibier, nous avons préféré conclure un pari ; celui qui récupérerait la peau du sabre-de-givre pourrait demander quelque chose à l’autre. À ma grande honte, c’est ainsi que j’ai gagné mon surnom : Fitz.$B$BFranchement, est-ce que j’ai l’air d’un Fitz ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9283;
UPDATE locales_npc_text SET entry = 9284, Text0_0_loc2 = '$N, le grand chasseur, est de retour. Racontez-moi votre dernière traque.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9284;
UPDATE locales_npc_text SET entry = 9286, Text0_0_loc2 = 'Oui, oui, ça va mieux, mais je me plais bien ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9286;
UPDATE locales_npc_text SET entry = 9287, Text0_0_loc2 = 'Le Consortium prépare cette opération depuis des mois ! Nous devions extraire Oshu’gun, le plus grand diamant de l’univers.$B$BMais à peine quelques minutes avant que nous ne commencions le travail, le prince-nexus Haramaad nous a prévenus que vous devions attendre qu’un problème politique soit résolu à Raz-de-Néant. Dans l’intervalle, j’ai transformé notre opération en bourse d\'échange pour gemmes de contrebande, mais tout cela écorne peu à peu nos profits.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9287;
UPDATE locales_npc_text SET entry = 9288, Text0_0_loc2 = 'Gezhe est ici pour faire son travail. Je suis ici pour m\'assurer que personne ne l\'ennuie pendant qu\'il le fait.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9288;
UPDATE locales_npc_text SET entry = 9289, Text0_0_loc2 = 'Savez-vous qui nous sommes, $gétranger:étrangère; ? Nous sommes des Mag\'har - des orcs bruns, comme j\'ai entendu les vôtres nous appeler. Les Mag\'har sont des survivants : survivants d\'une dynastie déchue ; survivants de la fièvre rouge ; survivants d\'un monde brisé...$B$BJe suis Jorin, fils de Kilrogg ; dernier de la lignée d\'Œil-mort, chef de guerre du clan de l\'Orbite-sanglante.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9289;
UPDATE locales_npc_text SET entry = 9290, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous ne nous serions pas écrasés si Hemet avait appris à éviter de tirer dans les ailerons quand il vise du gibier au sol !$B$BMais bon, pas d’inquiétude, je vais le réparer en un rien de temps.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9290;
