UPDATE locales_npc_text SET entry = 9296, Text0_0_loc2 = 'Salutations, $N !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9296;
UPDATE locales_npc_text SET entry = 9297, Text0_0_loc2 = null, Text0_1_loc2 = 'La grande-mère nous a tous élevés. Tous les Mag\'hari que vous pouvez croiser ont été influencés d\'une façon ou d\'une autre par la grande-mère. Elle nous manquera beaucoup quand elle s\'en ira.', Text1_0_loc2 = null, Text1_1_loc2 = 'La grande-mère m\'a dit que j\'ai été baptisé d\'après un grand héros orc.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9297;
UPDATE locales_npc_text SET entry = 9312, Text0_0_loc2 = 'Vous avez l\'air raisonnable, pour $gun:une:r; $r. Nous pouvons peut-être nous aider mutuellement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9312;
UPDATE locales_npc_text SET entry = 9315, Text0_0_loc2 = 'Soyez forts. Kael\'thas nous a trahis, et nos repères ne cessent de changer, mais ne perdez pas courage. Nous survivrons, comme toujours. Arthas n\'a pas pu venir à bout de notre détermination même lorsqu\'il a balayé notre terre, et Kael\'thas ne le pourra pas non plus. Que cela soit une leçon qui nous guidera vers l\'avenir prospère qui nous attend.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9315;
UPDATE locales_npc_text SET entry = 9316, Text0_0_loc2 = 'Dans quelle partie de notre splendide cité souhaitez-vous vous rendre ?', Text0_1_loc2 = 'Dans quelle partie de notre splendide cité souhaitez-vous vous rendre ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9316;
