UPDATE locales_npc_text SET entry = 9325, Text0_0_loc2 = 'L’auberge de Lune-d’argent se trouve à la Bourse royale. Elle est dirigée par une femme de grand talent nommée Velandra. Il paraît que l’on y voit aussi parfois le fameux tastevin.', Text0_1_loc2 = 'L’auberge de Lune-d’argent se trouve à la Bourse royale. Elle est dirigée par une femme de grand talent nommée Velandra. Il paraît que l’on y voit aussi parfois le fameux tastevin.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9325;
