UPDATE locales_npc_text SET entry = 9332, Text0_0_loc2 = 'Oh. Vous cherchez l’un de ces… types. Si vous voulez vraiment perdre votre temps en discutant avec ces arriérés, vous trouverez les forestiers sur la place des Pérégrins. Zandine s’occupera de vous.', Text0_1_loc2 = 'Oh. Vous cherchez l’un de ces… types. Si vous voulez vraiment perdre votre temps en discutant avec ces arriérés, vous trouverez les forestiers sur la place des Pérégrins. Zandine s’occupera de vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9332;
